/**
  * Select the node and all of its parent and children (up and down the hierarchy)
  *
  * 'this' is a reference to the GwtDiagramNode (which is a GlideWindow) that the action was called from
  * Use 'this.diagram' to reference the GwtDiagram
 **/ 

highlightHierarchy(this);

function highlightHierarchy(node) {
   node.diagram.clearSelected();
   selectNode(node, "parent");
   selectHierarchy(node, "child");
}

function selectNode(node, direction) {
   // Already been here?
   if (node.isActive())
      return;

   node.setSelected(true);
   selectHierarchy(node, direction);
}

function selectHierarchy(node, direction) {
   var edges = node.diagram.getEdges();
   for (var id in edges) {
      var edge = node.diagram.getEdge(id);
      if ((direction == "parent") && (edge.getTarget().getID() == node.getID()))
         selectNode(edge.getSource(), direction);

      if ((direction == "child") && (edge.getSource().getID() == node.getID()))
         selectNode(edge.getTarget(), direction);
   }
}

/**
  * Script executed on the Client for this action
  *
  * 'this' is a reference to the GwtDiagramNode (which is a GlideWindow) that the action was called from
  * Use 'this.diagram' to reference the GwtDiagram
 **/ 

focusAndRedraw(this);

function focusAndRedraw(node) {
   node.diagram.focusAndRedraw(node);	
}

var g_map_window = window.parent;
var g_opener_window = g_map_window.opener;
var g_opener = (g_opener_window && g_opener_window.GJSV? g_opener_window.getMainWindow() : null);

if(g_opener && g_opener.g_form) {
	var f = g_opener.g_form;
	var task_id = f.getValue('sys_uniqueValue'); 

	if (task_id) {
		for (var key in this.diagram.nodes) {
			var n = this.diagram.nodes[key];
			if (n.isActive() && (n.getData('is_collapsed') != 'true')) {
				var cmdb_id = n.getID();
				addAffectedCI(task_id, cmdb_id);
			}
		}
	}

	if(typeof(g_opener.refreshList) == "function") {
		g_opener.refreshList('task_ci');

	} else if(g_opener.GlideList2) {
		var lists = g_opener.GlideList2.getListsForTable('task_ci');
		for (var i = 0; i < lists.length; i++)
			lists[i].refresh();
	}

	alert("Affected CIs added");		
}

// create a relation between a task and a CI
function addAffectedCI(task_id, cmdb_id) {
        var affectedCI = new GlideRecord('task_ci');
        affectedCI.task = task_id;
        affectedCI.ci_item = cmdb_id;
        affectedCI.insert();
}

/**
  * Expand the CIs with issues
  *
  * 'this' is a reference to the GwtDiagramNode (which is a GlideWindow) that the action was called from
  * Use 'this.diagram' to reference the GwtDiagram
 **/ 

expandNodesWithIssues(this);

function expandNodesWithIssues(node) {
   var ids = this.bsmMap.getParam('sticky_nodes');
		
   var issueIds = node.getData('annotation_incident_id');
   ids = appendIssueIds(ids, issueIds);
   issueIds = node.getData('annotation_problem_id');
   ids = appendIssueIds(ids, issueIds);
   issueIds = node.getData('annotation_change_request_id');
   ids = appendIssueIds(ids, issueIds);
   issueIds = node.getData('annotation_cmdb_ci_outage_id');
   ids = appendIssueIds(ids, issueIds);

   this.bsmMap.setParam('sticky_nodes', ids);
   this.bsmMap.load();
}

function appendIssueIds(ids, issueIds) {
   if (!issueIds)
      return ids;
 
   if (ids)
      ids += ',';

   return ids + issueIds;
}

/**
  * Expand the CIs that are collapsed into this node
  *
  * 'this' is a reference to the GwtDiagramNode (which is a GlideWindow) that the action was called from
  * Use 'this.diagram' to reference the GwtDiagram
 **/ 

expandNode(this);

function expandNode(node) {
   // redraw the bsm map with the new/additional list of expanded ids
   var diagram = node.diagram;
   var ids = diagram.getParam('sticky_nodes');
   if (ids.length > 0)
      ids += ',';

   var max = diagram.getData('glide.bsm.max_to_expand');
   var newNodes = node.getData("collapsed_ids").split(',');

   if (max == '' || newNodes.length < max - 1) {
      //expand all
      ids += node.getData("collapsed_ids"); }
   else {
      for (var i = 0; i < max; i++) {
         ids += newNodes[i]; 
         if (i < max - 1) 
            ids += ',';         
      } 
   }
   diagram.setParam('sticky_nodes', ids);
   diagram.load();
   return true;
}

/**
  * Set the current CI as the CI for the incident
  *
  * 'this' is a reference to the GwtDiagramNode (which is a GlideWindow) that the action was called from
  * Use 'this.diagram' to reference the GwtDiagram
 **/ 

var g_map_window = window.parent;
var g_opener_window = g_map_window.opener;
var g_opener = (g_opener_window && g_opener_window.GJSV? g_opener_window.getMainWindow() : null);

setAsCI(this);

function setAsCI(node) { 
   var ciId = node.getID();
   var ciName = node.getData('label');
   var field = node.diagram.getParam('ref');
   var g_form = g_opener.g_form;
   g_form.setValue(field, ciId, ciName);
   g_map_window.close();
}

/**
  * Show the tasks for this CI
  *
  * 'this' is a reference to the GwtDiagramNode (which is a GlideWindow) that the action was called from
  * Use 'this.diagram' to reference the GwtDiagram
 **/ 

openTaskLink(this.getID());

function openTaskLink(id) {
   var url = "task_list.do?sysparm_query=cmdb_ci=" + id;
   try {
      var width = 700;
      var height = 500;
      var features = "resizable=yes,scrollbars=yes";
      popupOpenFocus(url + "&sysparm_nameofstack=bsm", "bsm_view", width, height, features, true, false);
   } catch (e) {
      if (confirm("Parent window was closed. Shall I open this link in the current window?")) {
         var iam = self;
         if (iam.parent && iam.parent != iam)
            iam = iam.parent;
				
         iam.location = "nav_to.do?uri=" + url;
      }
   }
}

showAffectedCIs(this);

function showAffectedCIs(node) {
   var w = new GlidePane("show_list", "task_ci_list");
   w.setTitle("Affected CI Tasks");
   w.addParm('table', 'task_ci_list');
   w.addParm('sysparm_view', 'bsm');
   var query = 'ci_item=' + node.getID() + '^task.active=true';
   w.addParm('sysparm_query', query);
   w.render();
}

/**
  * Collapse the CIs that were recently expanded. 
  *
  * 'this' is a reference to the GwtDiagramNode (which is a GlideWindow) that the action was called from
  * Use 'this.diagram' to reference the GwtDiagram
 **/ 

var stickyList = this.diagram.getParam('sticky_nodes').split(','); 

collapseNodes(this);

function collapseNodes(clickedNode) {
   var diagram = clickedNode.diagram;
   var nodes = diagram.nodes;  
   
   for (key in nodes) {
      var node = nodes[key];
      if (node.getData("is_expanded")=="true") {
         removeStickyNode(node.getID());     
      }     
   } 

   diagram.setParam('sticky_nodes', stickyList.join(','));
   diagram.load();
   return true;
}

function removeStickyNode(nodeID) {
   for (var i = 0; i < stickyList.length; i++) {
      var node = stickyList[i]; 
      if (node == nodeID) 
         stickyList.splice(i, 1); 
   } 
}

/**
  * Script executed on the Client for this action
  *
  * 'this' is a reference to the GwtDiagramNode (which is a GlideWindow) that the action was called from
  * Use 'this.diagram' to reference the GwtDiagram
 **/ 


var wfName = this.getData('name', '?');

var w = new GlideDialogWindow('subflow'); 

w.setPreference('renderer', 'RenderForm');
w.setPreference('type', 'direct');
w.setPreference('table', 'subflow');
w.setPreference('sysparm_subflow', 'true');
w.setPreference('sysparm_sys_id', this.getID());
w.render();

/**
  * Show the list of tasks for this CI
 **/ 

showTaskListDialog(this);

function showTaskListDialog(node) {
   var w = new GlidePane("show_list", "task_list");
   w.setTitle("Related Issues");
   w.addParm('table', 'task_list');
   w.addParm('sysparm_view', 'bsm');
   var query = 'cmdb_ci=' + node.getID() + '^active=true';
   w.addParm('sysparm_query', query);
   w.render();
}

/**
  * Show a cluster diagram for a cluster node.
 **/ 

focusAndRedraw(this);

function focusAndRedraw(node) {
   node.diagram.focusAndRedraw(node);	
}

/**
  * Show the list of outages for this CI
 **/ 

showOutageList(this);

function showOutageList(node) {
   var w = new GlidePane("show_list", "cmdb_ci_outage_list");
   w.setTitle("Related Outages");
   w.addParm('table', 'cmdb_ci_outage_list');
   w.addParm('sysparm_view', 'bsm');
   var query = 'cmdb_ci=' + node.getID();
   w.addParm('sysparm_query', query);
   w.render();
}
