result.script:
// Rerun the currently selected layout algorithm using the given node as if it were the root
runLayout();

function runLayout() {
	// Re-run layout algorithms for nodes and links
    bsmNodeLayout.runCurrentLayout(bsmGraph.current(), item);
    bsmLinkLayout.recalculateAllLinks(bsmGraph.current());

	// Draw to update the graph itself over 1.25 seconds
    bsmRenderer.drawAll(1250);

	// Fit the graph to screen and move the camera to fit the view over 1.00 seconds
    bsmCamera.fitToScreen(bsmGraph.current());
    bsmCamera.moveCamera(1000);
}

focus();

function focus() {
	// Load a graph from the selected node
	bsmRepository.loadGraph(item.id, bsmURL.getParameter('level')).then(function() {
		// After the data is loaded broadcast an event to notify BSM that there is new data to render
		$rootScope.$broadcast('ngbsm.new_graph_loaded');
	});
}

var _loading= function(rootId, selectedItem){
	bsmRepository.loadGraph(rootId, bsmURL.getParameter('level'), selectedItem.collapsedIds.split(','), undefined, selectedItem.isCluster, selectedItem.id).then(function() {
		// Signal BSM to rerender once we have new data
		$rootScope.$broadcast('ngbsm.new_graph_loaded');
	});
};

function _showConfirmationDialog(rootId, selectedItem) {
	var dialogConfig = {
		title : i18n.getMessage('Map update:'),
		msg : i18n.getMessage('If you continue with the current update to the map, changes previously made by a Load More operation – will be lost'),
		buttons:[{
			label : i18n.getMessage('Cancel'),
			css: 'btn-default',
			onClick : function (){}
			},{
				label : i18n.getMessage('Continue'),
				css: 'btn-primary',
				onClick : function (){
					graphWasChanged = false;
					_loading(rootId, selectedItem);
				}
			}
			]
	};
	$rootScope.$broadcast('ngbsm.open_bsm_dialog', dialogConfig);
}

function expand() {
	// Focus and fetch data for the selected CI, passing a list of sys_id's for nodes
	// inside the group to prevent them from being collapsed
	var id = bsmGraph.current().root.id;


	//Need to send a warnning (the sate probably will be lost), in case the graph was changed already by other action (e.g. loadMore)
	if (graphWasChanged){
		_showConfirmationDialog(id , item);		
	} else {
		_loading(id , item);
	}			
}
expand();

// Prompt for modification of a relationship type
modify();

function modify() {
	bsmRelationships.modifyRelationship(item);
}

loadMore();

function loadMore() {
	// Fetch more data as if were were loading the graph from the given CI, but append
	// the loaded data instead of switching to the new data set

	// First check that we are not trying to load from a collapsed nodes(Cluster Virtual& Group)
	if(item.isCollapsed) {
		console.error("MENU ACTION ->'Load More' menu action should not work on collapsed node",item);
	}
	else {
		// Perform a basic load of data from the selected node
		bsmRepository.expandGraph(item.id, bsmURL.getParameter('level'), undefined, undefined , 'loadMore').then(function() {
			// Signal BSM to rerender once we have new data
			$rootScope.$broadcast('ngbsm.new_graph_loaded');
		});
	}
}

function _collapseVirtualGraph(rootId, selectedItem) {
	// Collapsed back to virtual group
	bsmRepository.collapseGraph(rootId, selectedItem).then(function() {
		// Signal BSM to rerender once we have new data
		$rootScope.$broadcast('ngbsm.new_graph_loaded');
	});
}

function _collapseClusterGraph(rootId) {
	// Simply reload the data without any sticky nodes specified
	bsmRepository.loadGraph(rootId, bsmURL.getParameter('level')).then(function() {
		// Signal BSM to rerender once we have new data
		$rootScope.$broadcast('ngbsm.new_graph_loaded');
	});
}

function _showConfirmationDialog(rootId, selectedItem) {
	var dialogConfig = {
		title : i18n.getMessage('Map update:'),
		msg : i18n.getMessage('If you continue with the current update to the map, changes previously made by a Load More operation – will be lost'),
		buttons:[{
			label : i18n.getMessage('Cancel'),
			css: 'btn-default',
			onClick : function (){}
			},{
				label : i18n.getMessage('Continue'),
				css: 'btn-primary',
				onClick : function (){
					graphWasChanged = false;
					_collapseVirtualGraph(rootId, selectedItem);
				}
			}
			]
	};
	$rootScope.$broadcast('ngbsm.open_bsm_dialog', dialogConfig);
}

function collapse() {
		var id = bsmGraph.current().root.id;

		if (item.isCluster){
			_collapseClusterGraph(id);
		} else {
			//Need to send a warnning (the sate probably will be lost), in case the graph was changed already by other action (e.g. loadMore)
			if (graphWasChanged){
				_showConfirmationDialog(id, item );	
			} else {
				_collapseVirtualGraph(id, item);
			}
		}
}
collapse();

// Open a list of outages for this CI
var relatedOutagesString;
var lowerBoundProperty = 'cmdb.recent_outages.days_ago';
var higherBoundProperty = 'cmdb.recent_outages.days_ahead';
var relatedOutages = new GlideRecord('cmdb_outage_ci_mtom');
relatedOutages.addQuery('ci_item', item.id);
relatedOutages.query();
while(relatedOutages.next()) {
	if (relatedOutagesString)
		relatedOutagesString = relatedOutagesString + ',' + relatedOutages.getValue('outage');
	else
		relatedOutagesString = relatedOutages.getValue('outage');
}
var lowerBoundDays;
var higherBoundDays;
var boundDays = new GlideRecord('sys_properties');
boundDays.addQuery('name','IN',lowerBoundProperty+','+higherBoundProperty);
boundDays.query();
while (boundDays.next()) {
	var currentProp = boundDays.getValue('name');
	if(currentProp === lowerBoundProperty)
		lowerBoundDays = boundDays.getValue('value');
	else 
		higherBoundDays = boundDays.getValue('value');

	if (!lowerBoundDays)
		lowerBoundDays = '7';
	if (!higherBoundDays)
		higherBoundDays= '14';	
}

if (relatedOutagesString) {	
	window.g_navigation.open('/cmdb_ci_outage_list.do?sysparm_query=endRELATIVEGE@dayofweek@ago@'+lowerBoundDays+'%5EORendISEMPTY%5EbeginRELATIVELE@dayofweek@ahead@'+higherBoundDays+'%5Ecmdb_ci='+ item.id +'%5EORsys_idIN' + relatedOutagesString,'_blank');
}
else
	window.open('/cmdb_ci_outage_list.do?sysparm_query=endRELATIVEGE@dayofweek@ago@'+lowerBoundDays+'%5EORendISEMPTY%5EbeginRELATIVELE@dayofweek@ahead@'+higherBoundDays+'%5Ecmdb_ci=' + item.id);

// Kick off the relationship creation process from the current node
startAdd();

function startAdd() {
	bsmRelationships.createRelationship(item);
}

// Reset, recalculate and reapply filters
bsmFilters.resetFilters();
bsmFilters.enumerateFilters(bsmGraph.current());
bsmFilters.applyFilters(bsmGraph.current());

// Redraw the graph to refect updated filtering
// 1000 is the duration in milliseconds to animate the rendering
bsmRenderer.drawAll(1000);
bsmFilters.firePostResetEvent();

// Branch based on if there is a selection of multiple CIs or not
if(bsmSelection.getSelection().nodes.length > 1)
	addMultipleAffectedCIs();
else
	addAffectedCI();

// Function to add one CI as an affected CI
function addAffectedCI() {
    var taskId = bsmURL.getParameter('fromTask');
    var cmdbId = item.id;

	// Only process if there is a valid task and CI 
    if (taskId && cmdbId) {
		// Validate that the CI is not already an affected CI
        var affectedCI = new GlideRecord('task_ci');
        affectedCI.addQuery('task', taskId);
        affectedCI.addQuery('ci_item', cmdbId);
        affectedCI.query();
        if (!affectedCI.hasNext()) {
			// Since it did not exist, add it as an affected CI
            affectedCI.task = taskId;
            affectedCI.ci_item = cmdbId;
            affectedCI.insert();

			// Send message notifying that the CI was added
            $rootScope.$broadcast('ngbsm.success', {
                success: 'The CI ' + item.name + ' has been added as an affected CI'
            });
        } else {
			// Send a message that the CI is already an affected CI
			$rootScope.$broadcast('ngbsm.success', {
                success: 'The CI ' + item.name + ' is already an affected CI'
            });
		}
    }
}

// Function to add all selected CIs as affected CIs
function addMultipleAffectedCIs() {
    var taskId = bsmURL.getParameter('fromTask');
	var nodes = bsmSelection.getSelection().nodes;
	var added = '';

	// Only process if there is a valid task ID
    if (taskId) {
		// Add all CI's in the current selection if they are node clusters/collapsed
		for(var i = 0; i < nodes.length; i++) {
			if(!nodes[i].isCollapsed || nodes[i].isCluster) {
				// Attempt to add the CI as an affected CI
				var affectedCI = new GlideRecord('task_ci');
				affectedCI.addQuery('task', taskId);
				affectedCI.addQuery('ci_item', nodes[i].id);
				affectedCI.query();
				if (!affectedCI.hasNext()) {
					affectedCI.task = taskId;
					affectedCI.ci_item = nodes[i].id;
					affectedCI.insert();

					// If added, add it to the list of added CIs
					if(added === '')
						added += nodes[i].name;
					else
						added += ', ' + nodes[i].name;
				}
			}
		}

		// Notify user of which CI's got added or that they didn't get added
		if(added !== '') {
			$rootScope.$broadcast('ngbsm.success', {
				success: added + ' have been added as affected CIs'
			});
		} else {
			$rootScope.$broadcast('ngbsm.success', {
				success: 'Selected CIs are already affected CIs or cannot be added'
			});
		}
    }
}

// Rerun the currently selected layout algorithm using the root node as a starting point
runLayout();

function runLayout() {
	// Re-run layout algorithms for nodes and links
    bsmNodeLayout.runCurrentLayout(bsmGraph.current(), bsmGraph.current().root);
    bsmLinkLayout.recalculateAllLinks(bsmGraph.current());

	// Draw to update the graph itself over 1.25 seconds
    bsmRenderer.drawAll(1250);

	// Fit the graph to screen and move the camera to fit the view over 1.00 seconds
    bsmCamera.fitToScreen(bsmGraph.current());
    bsmCamera.moveCamera(1000);
}

// Prompt for deletion of a relationship
remove();

function remove() {
	bsmRelationships.deleteRelationship(item);
}

// Fit the graph to the viewport in its current configuration
fitToScreen();

function fitToScreen() {
	// Fit the graph to screen and move the camera to fit the view over 1.00 seconds
    bsmCamera.fitToScreen(bsmGraph.current());
    bsmCamera.moveCamera(1000);
}
