function($rootScope, spUtil, $uibModal, $http) {
	'use strict';

	return {
		restrict: 'E',
		replace: true,
		template:   '<span class="ref-picker-container">' +
						'<div class="add-on" ng-if="field.value != \'\'">' +
							'<button type="button" aria-label="${Preview this record {{field.displayValue}}}" class="btn btn-default bg-white lookup" ng-click="openReference(field)" data-toggle="tooltip" data-placement="top" data-original-title="${Preview this record {{field.displayValue}}}"><span class="fa fa-info-circle"></span></button>' +
						'</div>' +
						'<div ng-class="{\'has-error\': !field.isMandatoryFilled}" class="reference">' +
							'<sp-survey-reference-element c="c" is-mobile="isMobile" field="field" sn-select-width="100%" ref-table="field.refTable" ref-id="field.sys_id" glide-form="getGlideForm()" record-values="getEncodedRecordValues()" sn-disabled="field.isReadonly()"/>' +
						'</div>' +
					'</span>',

		controller: function($scope){
			$scope.openReference = function(field) {
				var data = {table: field.ed.reference, sys_id: field.value};
				if (angular.isDefined(field.reference_key))
					data[field.reference_key] = field.value;
				else
					data.sys_id = field.value;

				showForm(data);
			};


			function showForm(data) {
				var url = spUtil.getWidgetURL("widget-form");

				var req = {
					method : 'POST',
					url : url,
					headers : {
						'Accept' : 'application/json',
						'x-portal' : $rootScope.portal_id
					},
					data : data
				}
				$http(req).then(qs, qe);

				function qs(response) {
					var r = response.data.result;
					showModal(r);
				}
				// error
				function qe(error) {
					console.error("Error " + error.status + " " + error.statusText);
				}
			}


			function showModal(form) {
				var opts = {
					size : 'lg',
					templateUrl : 'sp_form_modal',
					controller : ModalInstanceCtrl,
					resolve : {}
				};

				opts.resolve.item = function() {
					return angular.copy({
						form : form
					});
				};

				var modalInstance = $uibModal.open(opts);
				modalInstance.result.then(function() {}, function() {});

				$scope.$on("$destroy", function() {
					modalInstance.close();
				});

				var unregister = $scope.$on('sp.form.record.updated', function(evt, fields) {
					unregister();
					unregister = null;
					modalInstance.close();

					// Do something here to refresh this record...

					if (evt.stopPropagation)
						evt.stopPropagation();
					evt.preventDefault();
				});
			}

			function ModalInstanceCtrl($scope, $uibModalInstance, item) {
				$scope.item = item;

				$scope.ok = function() {
					$uibModalInstance.close();
				};

				$scope.cancel = function() {
					$uibModalInstance.dismiss('cancel');
				};
			}



		}
	};
}

function() {
	return {
		template:
		'<div class="container-fluid">' +
		'<div class="row">' +
		'<div class="col-xs-12 col-sm-6">' +
		'<ul style="list-style-type: none;padding:0" class="sps-ranking-list">'+
			'<li class="row question-ranking-line-space" ng-repeat="c in field.choices track by $index" style="border: 1px solid #ccc;border-radius: 5px;margin-bottom: 5px;">'+
			  '<div class="col-xs-2 wrapper-xs" style="position:static">' +
				'<select elem-id="{{::c.sys_id}}" name="{{c.name}}" aria-label="{{::c.label}}" ng-change="onSelectChange(c)" class="form-control" style="padding:3px;" sn-select-width="auto" ng-model="c.value" ng-disabled="field.isReadonly()">' +
				'<option value="-1" ng-selected="c.value == -1"></option> '+
				'<option ng-repeat="i in field.choices track by $index" value="{{$index + 1}}" ng-selected="$index + 1 == c.value">{{$index + 1}}</option> ' +
				'</select>'+
			  '</div>' +
			  '<div class="col-xs-9 wrapper-sm" style="position:static"><label for="{{::c.sys_id}}" class="sps-checkbox-label">{{c.label}}</label></div>' +
			  '<div class="col-xs-1 wrapper-sm" style="position:static"><span class="fa fa-bars pull-right wrapper-xs"></span></div>'+
			'<li>'+
		'</ul>' +
		'</div>' +
		'<sp-survey-add-info ng-if="field.allow_add_info" field="field"></sp-survey-add-info>' +
		'</div>' +
		'</div>',
		controller: function($scope) {
			$scope.field.choices.sort(function(a, b) {return a.value - b.value;});

			function swap(a, i, j) {
				if (i < 0 || i >= a.length || j < 0 || j >= a.length)
					return;

				var temp = a[i];
				a[i] = a[j];
				a[j] = temp;
			}

			function getCurrentPosition(sys_id) {
				var lis = $($scope.element).find('li');
				for (var i=0; i<lis.length; i++) {
					var forSysId = $(lis[i]).find('label').attr('for');
					if (forSysId && forSysId.trim().indexOf(sys_id.trim()) == 0)
						return (i + 1);
				}
				return -1;
			}

			$scope.onSelectChange = function(choice) {
				$scope.field.choices.forEach(function(c) {
					if (c.value == choice.value && c.sys_id != choice.sys_id)
						c.value = '-1';
				});

				var oldValue = getCurrentPosition(choice.sys_id);
				var order = $scope.sortable.toArray();
				swap(order, parseInt(choice.value) - 1, parseInt(oldValue) - 1);
				$scope.sortable.sort(order);
			}
	   },
		link: function(scope, element) {
			var sortable = new Sortable($(element).find('ul')[0], {
				onUpdate: function () {
					var rankList = $(element).find('select');
					for(var i = 0; i < rankList.length; i++) {
						rankList[i].value = i + 1;
						$(rankList[i]).triggerHandler('change');
					}
				}
			});

			setTimeout(function() {
				$(element).find('li').mouseenter(function(elem) {
					$(this).addClass('hovered');
				})

				$(element).find('li').mouseleave(function(elem) {
					$(this).removeClass('hovered');
				})
			}, 10);

			scope.sortable = sortable;
			scope.element = element;
		}
	};
}

