							'<button class="btn btn-block" ng-class="c.value == field.value ? \'btn-primary\' : \'btn-default\'" ng-click="selectChoice(c)" title="{{::c.label}}">{{::c.label}}</button>' +
		'<sp-survey-end-notes data="data"  ng-if="c.isKioskSurvey">' +
                             '</sp-survey-end-notes>' +
