						var choice1 = $scope.field.choices[i];
					    choice1.isDisabled = true;
				}
					$rootScope.submit($scope.page, $scope.c.state);
				}
			}
		}
	};
}

function() {

    //Scroll the section to given length (domElementTop - section.scrollTop)
    function scrollTo(section, domElementTop, duration) {
        var start = section.scrollTop;
        var change = domElementTop - start;
        var currentTime = 0;
        var increment = 20;
        var animateScroll = function() {
            currentTime += increment;
            var val = easeInOutQuad(currentTime, start, change, duration);
            section.scrollTop = val;
            if (currentTime < duration) {
                setTimeout(animateScroll, increment);
            }
        };
        animateScroll();
    }

    function easeInOutQuad(currentTime, start, change, duration) {
        currentTime /= duration / 2;
        if (currentTime < 1) return change / 2 * currentTime * currentTime + start;
        currentTime--;
        return -change / 2 * (currentTime * (currentTime - 2) - 1) + start;
    };

    return {
        restrict: 'A',
        replace: true,
        link: function(scope, element) {
            var domEelement = element.get(0);
            var panelElement = domEelement.querySelector(".panel-collapse");
            scope.collapsing = false;
            scope.$watch(function() {
                return panelElement.classList.contains("collapsing");
            }, function(status) {
                if (scope.collapsing && !status) {
                //Only scroll the panel which is opened now.
                    if (domEelement.classList.contains("panel-open")) {
                        var sectionElement = document.querySelector("section.page")
                        scrollTo(sectionElement, domEelement.offsetTop - 60, 200);
                    }
                }
                scope.collapsing = status;
            });
        }
    };
}

function(spPreference) {	
	var type;

	function save(data, sys_id) {
		var providers = {};
		if (data.length) {
			angular.forEach(data, function(codeBlock, key) {
				providers[codeBlock.name] = codeBlock.show;															
			});
			spPreference.set("wep-" + type + "-" + sys_id, JSON.stringify(providers));
		}
	}

	function load(data, pref) {
		if (data.length) {		
			var providers = JSON.parse(pref || '{}');			
			angular.forEach(data, function(item) {
				item.show = providers[item.name] || false;								
			});		
		}	
	}

	function clear($select, options) {
		var selectize = $select[0].selectize;
		selectize.clear();
		selectize.clearOptions();
		selectize.load(function(callback) {
			callback(options);
		});		
	}

	function link(scope, element) {		
		type = scope.type;			
		var key = 'name';
		var config = {
			valueField: key,
			labelField: key,
			searchField: key,
			options: scope.options,
			allowEmptyOption: true,
			placeholder: scope.label || 'Dependencies',						
			onChange: function(value) {
				angular.forEach(scope.options, function(item) {
					if (item[key] === value) {
						item.show = true;
						scope.$apply();
					}
				})
			}
		};

		var $select = $(element).selectize(config);

		scope.$on('$sp.wep-' + type, function(e, action, option) {
			if (action === 'close') {
				$select[0].selectize.clear();
				save(scope.options, option);
			}

			if (action === 'save')
				save(scope.options, option);

			if (action === 'load')
				load(scope.options, scope.prefs);

			if (action === 'clear') 
				clear($select, scope.options);

		});

		load(scope.options, scope.prefs);

	}

	return {
		restrict: 'E',
		scope: {
			options: '=',
			prefs: '=',
			label: '=',
			type: '='
		},
		template: '<select class="form-control sp-dep"></select>',
		replace: true,
		link: link
	};

}

