							var obj = $scope.c.data.categories.idMap[catid];
							return {id: catid, display: obj.display, questions: obj.questions,
							catDesc: obj.description, assessableRecord: obj.assessableRecord};	
                    });

					else
						$scope.pages = $scope.c.data.categories.idList.map(function(catid) {
							var obj = $scope.c.data.categories.idMap[catid];
							return {id: catid, display: obj.display, questions: obj.questions,
							catDesc: obj.description, assessableRecord: obj.assessableRecord,showAsrName: obj.showAsrName};	
                    });
				} else {
					if ($scope.pagination == 'question')
						$scope.pages = $scope.c.data.questions.idList.map(function(qid) {return {id: qid, questions: [qid], catDesc: $scope.c.data.questions.idMap[qid].category.description,display: $scope.c.data.questions.idMap[qid].category.display}});		

					else
						$scope.pages = $scope.c.data.categories.idList.map(function(catid) {
							var category = $scope.c.data.categories.idMap[catid];
							return {id: catid, display: category.display, catDesc: category.description,questions: category.questions};
						});
				}

                var signature = $scope.c.data.signature;
                if (signature && !$scope.c.isPublic) {
                    if ($scope.pagination == 'none')
                        $scope.pages[$scope.pages.length - 1].questions.push(signature.sys_id);
                    else
                        $scope.pages.push({id: signature.sys_id, display: $scope.c.data.title, questions: [signature.sys_id]});

                    $scope.c.data.questions.idMap[signature.sys_id] = {
                        type: 'signature',
                        name: 'signature_result',
                        label: signature.label,
                        value: signature.value,
                        sys_id: signature.sys_id,
                        signType: signature.type,
                        depends_on: ''
                    };
                }
            };
            init();

            var mapReduceAllTrue = function(questions, callback) {
                return questions.map(function(qid) {
                    return callback($scope.c.data.questions.idMap[qid]);
                }).reduce(function(x, y) {
                    return x && y;
                }, true);
            };

            var disableNextButtonHelper = function(page) {
                if (!page || !page.questions || !page.questions.length)
                    return true;

                return !$scope.isPageValid(page) || !$scope.isPageMandatoryFilled(page);
            };

            $scope.disableNextButton = function(page) {
                if ($scope.pagination == 'none')
                    return $scope.pages.map(function(x) {return disableNextButtonHelper(x)})
                                .reduce(function(x, y) {return x || y}, false);
                return disableNextButtonHelper(page);
            };

            // Returns true if all questions within this page have a valid response
            var isPageValidHelper = function(page) {
                return mapReduceAllTrue(page.questions, function(question) {return !question.invalidValue});
            };

            $scope.isPageValid = function(page) {
                if ($scope.pagination == 'none')
                    return $scope.pages.map(function(x) {return isPageValidHelper(x)})
                                    .reduce(function(x, y) {return x && y}, true);
                return isPageValidHelper(page);
            };

            $scope.isSignatureFilled = function(page) {
                if ($scope.pagination != 'none' && !page.questions.reduce(function(x, y) {return x || $scope.c.data.questions.idMap[y].type == 'signature'}, false))
                    return true;

                var signature = $scope.data.signature;
                if (!signature || signature.type == 'assertion_only')
                    return true;

                if (signature.type == 'full_name' && !signature.validated) {
                    signature.validationCallback = function(validated) {
                        if (validated)
                            $scope.submit(page, $scope.c.state);
                        else
                            $scope.c.showSignatureMessage();
                    };
                    $scope.c.showSignatureAuthModal();
                    return false;
                }

                if (signature.type == 'checkbox') {
                    if ($scope.c.data.questions.idMap[signature.sys_id].value != 'true')
                        return false;
                    else
                        $scope.c.data.questions.idMap[signature.sys_id].value = 'checked';
                }

                return true;
            };

            // Returns true if all mandatory questions within this page are filled
            var isPageMandatoryFilledHelper = function(page) {
                var questionsList = page.questions.concat([]);
                if (!$scope.isMobile)
                    page.questions.forEach(function(qid) {
                        var question = $scope.c.data.questions.idMap[qid];
                        if (question.type == 'template' && question.template.firstQuestion) {
                            var templateGroup = $scope.c.data.templateGroups[question.templateGroup];
                            questionsList = questionsList.concat(templateGroup.questions.filter(function(tid) {return tid != qid;}));
                        }
                    });
                return mapReduceAllTrue(questionsList, function(question) {return !question.mandatory || $scope.c.isMandatoryFilled(question)});
            };

            $scope.isPageMandatoryFilled = function(page) {
                if ($scope.pagination == 'none')
                    return $scope.pages.map(function(x){return isPageMandatoryFilledHelper(x)})
                                    .reduce(function(x, y) {return x && y}, true);
                return isPageMandatoryFilledHelper(page);
            };

            // Saves questions
            var saveCurrentQuestions = function(questions, action, updateSuccessMessage) {
                var formdata = {"sysparm_instance_id": $scope.c.data.instanceId, "sysparm_action": action};
                questions.forEach(function(qid) { $scope.c.updateQuestion(qid, formdata) });
                formdata['percent_answered'] = $scope.c.getPercentAnswered();
                if (!!updateSuccessMessage)
                    formdata['updateSuccessMessage'] = true;
                $scope.c.submitAjax(formdata);
            };

            // Saves questions on current page or all pages depending on pagination setting
            $scope.saveCurrentPage = function(page, action, updateSuccessMessage) {
                if ($scope.pagination == 'none')
                    return saveCurrentQuestions($scope.pages.reduce(function(p1, p2) {return p1.concat(p2.questions)}, []), action, updateSuccessMessage);
                else if ($scope.pagination == 'question' && !$scope.isMobile) {
                    var question = $scope.data.questions.idMap[page.questions[0]];
                    if (question.type == 'template')
                        return saveCurrentQuestions($scope.data.templateGroups[question.templateGroup].questions, action, updateSuccessMessage);
                }
                return saveCurrentQuestions(page.questions, action, updateSuccessMessage);
            };

            // Get the next (or) previous navigable page
            var nextNavigablePage = function(state, forwardDirection) {
                var current = state;
                while (1) {
                    // Get next value within bounds
                    current = forwardDirection ? current + 1 : current - 1;

                    if (current <= 0)
                        return 0;

                    if (current >= $scope.pages.length)
                        return $scope.pages.length;

                    if (!$scope.isMobile && mapReduceAllTrue($scope.pages[current].questions, function(q) {return q.template && !q.template.firstQuestion}))
                        continue;

                    if (mapReduceAllTrue($scope.pages[current].questions, function(question) {return !!question.depends_on && !$scope.c.isAnyDependencySatisfied(question)})) {
                        if (current == $scope.pages.length - 1)
                            return state;
                    } else
                        return current;
                }
            };

            var doValidations = function(page, saveAndExit) {
                if (!$scope.isPageValid(page)) {
                    $scope.c.showInvalidResponseMessage();
                    return false;
                }

                if (!saveAndExit && !$scope.isPageMandatoryFilled(page)) {
                    $scope.c.showMandatoryMessage();
                    return false;
                }

                if (!saveAndExit && !$scope.isSignatureFilled(page)) {
                    if ($scope.c.data.signature.type != 'full_name')
                        $scope.c.showSignatureMessage();
                    return false;
                }

                return true;
            };

            var nextPageHelper = function(page, state, action, showSuccessMessage) {
                // Clear any existing messages
                $scope.c.hideInlineErrorMessage();

                // Always skip validation if clicking cancel button
                if (action != 'cancel') {
                    // Only do the validations if the user is not trying to save & exit
                    var saveAndExit = (action == 'save' && showSuccessMessage);

                    if ($scope.pagination == 'none') {
                        if ($scope.pages.reduce(function (x, y) {return x || !doValidations(y, saveAndExit)}, false))
                            return false;
                    } else {
                        if (!doValidations(page, saveAndExit))
                            return false;
                    }
                }

                // Save page
                $scope.saveCurrentPage(page, action, !!showSuccessMessage);

                // Navigate to the next page
                if (!!showSuccessMessage) {
                    $scope.c.state = $scope.pages.length;
                    if (action == 'submit')
                    $scope.$emit("$sp.me_take_survey.submitted", 'true');//Assuming mesp portal widget is used for HR use case.
                } else
                    $scope.c.state = nextNavigablePage(state, true);

                // Scroll to the top
                $('section.page').scrollTop(0);
            };

            $scope.nextPage = function(page, state) {
				nextPageHelper(page, state, 'save');
            };

			$scope.submit = function(page,state) {
				nextPageHelper(page, state, 'submit', true);
			};

			$rootScope.submit = function(page, state) {
				nextPageHelper(page, state, 'submit', true);
            };

            $scope.save = function(page, state) {
                nextPageHelper(page, state, 'save', true);
            };

            $scope.cancel = function(page, state) {
                nextPageHelper(page, state, 'cancel', true);
            };

            $scope.previousPage = function(state) {
                $scope.c.state = nextNavigablePage(state, false);

                // Scroll to the top
                $('section.page').scrollTop(0);
            };

           $scope.isLastPage = function(page) {
                var retVal = true;
                var i;
                if ($scope.c.state == $scope.pages.length - 1)
                    return retVal;
				var question = $scope.data.questions.idMap[page.questions[0]];
				var tmpGroup = question.templateGroup;
                if(!$scope.isMobile 
				   && $scope.pagination == 'question' 
				   && question.type == 'template'){
					var numQuestionsOnTemplate = $scope.c.data.templateGroups[tmpGroup].questions.length;
					if($scope.c.state == $scope.pages.length-numQuestionsOnTemplate)
						return retVal;
				}
                for (i=$scope.c.state + 1; i<$scope.pages.length; i++) {
                    var nextPage = $scope.pages[i];

                    if (!mapReduceAllTrue(nextPage.questions, function(question) {return !!question.depends_on && !$scope.c.isAnyDependencySatisfied(question)})) {
                        retVal = false;
                        break;
                    }
                }

                return retVal;
            };
        }
    }
}

function($interval, $timeout) {

	return {

		restrict: 'A',
		scope: true,
		compile: function() {

			return function(scope, element, attributes) {

				var timing,
					timingEffect,
					timingEffectDivideBy = 4,
					start,
					direction;

				if (attributes.direction=='up' || attributes.direction=='down') {
					direction = attributes.direction;
				} else {
					direction = 'up';
				}

				if (attributes.timing) {
					timing = attributes.timing;
					timingEffect = timing / timingEffectDivideBy;
				} else {
					timing = 5000;
					timingEffect = timing / timingEffectDivideBy / timingEffectDivideBy * 2;
				}

				function startInterval(element) {

					var list = element,
						items = element.find('a'),
						itemFirst,
						itemLast;

					if (items.length) {
						list.addClass('active');

						start = $interval(function() {

							items = list.children('a');

							if (direction=='up') {
								itemFirst = angular.element(items[0]);
								itemFirst.addClass('fade-out minus-margin-top');
								$timeout(function() {
									itemFirst.removeClass('minus-margin-top');
									list.append(itemFirst);
									$timeout(function() {
										items.removeClass('fade-out');
									}, timingEffect);

								}, timingEffect);
							} else {
								itemLast = angular.element(items[items.length -1]);
								itemLast.addClass('fade-out');
								$timeout(function() {
									itemLast.addClass('minus-margin-top');
									itemLast.removeClass('fade-out');
									list.prepend(itemLast);							
									$timeout(function() {
										items.removeClass('minus-margin-top');
									}, timingEffect);
								}, timingEffect);
							} 

						}, timing);
					} else {
						console.warn('no items assigned to ticker!');
					}

				}		

				scope.$watch(element, function() {
					startInterval(element);
				});

				element.on('$destroy', function() {
					$interval.cancel(start, 0);
				});

				element.on('mouseover', function() {
					$interval.cancel(start, 0);
				});

				element.on('mouseout', function() {
					startInterval(element);
				});

			};
		}

	};
};

function($timeout) {
    return {
        restrict: 'A',
        link: function(scope, element, attr) {
            if (scope.$last === true) {
                $timeout(function() {
                    scope.makeUibTabAccessible();
                });
            }
			/* method for adding appropriate roles for uib tab set template and also handle arrow key navigation
			  for eg.
				  <ul role="tablist">
					  <li role="presentation">
						  <a role="tab"></a>
					  </li>
				  </ul>
			*/
            scope.makeUibTabAccessible = function() {
                // For easy reference
                var keys = {
                    left: 37,
                    up: 38,
                    right: 39,
                    down: 40
                };
                var tabList = element.parent()[0];
                tabList = angular.element(tabList);
                tabList.attr("role", "tablist");
                // For each individual tab DIV, set class and aria role attributes
                var tabContainer = element.parent().parent().find(".tab-content > div.tab-pane");
                tabContainer.attr({
                    "role": "tabpanel"
                });
                var tabs = tabList.find('li > a');
                angular.forEach(tabs, function(tab, index) {
                    var tabParent = angular.element(tab).parent();
                    var tabElement = angular.element(tab);
                    var tabId = tabParent.attr("heading").replace(/ /g, '_');
                    if (tabParent && tabParent.parent() && tabParent.parent()[0] && tabParent.parent()[0].className.includes("dropdown-menu"))
                        tabId = "dropdown_menu_" + tabId;

                    tabElement.attr({
                        "id": tabId,
                        "role": "tab",
                        "aria-selected": "false",
                        "aria-controls": tabId + "panel",
                        "tabindex": "-1"
                    }).parent().attr("role", "presentation");
                    tabContainer.eq(index).attr({
                        "aria-labelledby": tabId,
                        "id": tabId + "panel"
                    });
                    // Set key events on tabList item for navigating tabs
                    tabElement.keyup(function(event) {
                        var vertical = element.parent().parent().attr("vertical"); // check orientation
                        var proceed = false;
                        var key = event.which;
                        if (vertical === "true") {
                            if (key === keys.up || key === keys.down) {
                                event.preventDefault(); // need to prevent page scroll
                                proceed = true;
                            }
                        } else {
                            if (key === keys.left || key === keys.right) {
                                proceed = true;
                            }
                        }
                        if (proceed && !tabElement.parent().parent()[0].className.includes("dropdown-menu")) { //To avoid if its a dropdown
                            switch (key) {
                                case keys.left:
                                case keys.up:
                                if (tabElement.parent().prev().prev().length != 0 && tabElement.parent().prev()[0].tagName != "LI") {
                                	tabElement.parent().prev().prev().find("> a").click();
                                }
                                else if (tabElement.parent().prev().length != 0 ) {
                                	tabElement.parent().prev().find("> a").click();
                                	}
                                	else {
                                	 tabList.find("li:last > a").click();
                                	}
                                 break;
                                case keys.right:
                                case keys.down:
                                if (tabElement.parent().next().next().length != 0 && tabElement.parent().next()[0].tagName != "LI") {
                                	tabElement.parent().next().next().find("> a").click();
                                	}
                                	else if (tabElement.parent().next().length != 0)
                                		tabElement.parent().next().find("> a").click();
                                	else {
                                	 tabList.find("li:first > a").click();
                                	 }
                                	 break;
                                	 }
                             }
                     });
                    // Set the click event for each tab link
                    tabElement.click(function(event) {
                        tabList.find("> li.active").find("> a").attr({
                            "aria-selected": "false",
                            "tabindex": "-1"
                        });
                        tabElement.attr({
                            "aria-selected": "true"
                        }).removeAttr("tabindex").focus();
                    });
                });
                // Set initial state for the selected tab
                tabList.find("> li.active").find("> a").attr({
                    "aria-selected": "true"
                }).removeAttr("tabindex");
            }
        }
    }
}

function($rootScope, spUtil, $uibModal, $http) {
	'use strict';

	return {
		restrict: 'E',
		replace: true,
		template:   '<span class="ref-picker-container">' +
						'<div class="add-on" ng-if="field.value != \'\'">' +
							'<button type="button" class="btn btn-default bg-white lookup" ng-click="openReference(field)" aria-label="View Information"><span class="fa fa-info-circle"></span></button>' +
						'</div>' +
						'<div class="reference">' +
							'<mesp-survey-reference-element field="field" sn-select-width="100%" ref-table="field.refTable" ref-id="field.sys_id" glide-form="getGlideForm()" record-values="getEncodedRecordValues()" sn-disabled="field.isReadonly()"/>' +
						'</div>' +
					'</span>',

		controller: function($scope){
			$scope.openReference = function(field) {
				var data = {table: field.ed.reference, sys_id: field.value};
				if (angular.isDefined(field.reference_key))
					data[field.reference_key] = field.value;
				else
					data.sys_id = field.value;

				showForm(data);
			};


			function showForm(data) {
				var url = spUtil.getWidgetURL("widget-form");

				var req = {
					method : 'POST',
					url : url,
					headers : {
						'Accept' : 'application/json',
						'x-portal' : $rootScope.portal_id
					},
					data : data
				}
				$http(req).then(qs, qe);

				function qs(response) {
					var r = response.data.result;
					showModal(r);
				}
				// error
				function qe(error) {
					console.error("Error " + error.status + " " + error.statusText);
				}
			}


			function showModal(form) {
				var opts = {
					size : 'lg',
					templateUrl : 'sp_form_modal',
					controller : ModalInstanceCtrl,
					resolve : {}
				};

				opts.resolve.item = function() {
					return angular.copy({
						form : form
					});
				};

				var modalInstance = $uibModal.open(opts);
				modalInstance.result.then(function() {}, function() {});

				$scope.$on("$destroy", function() {
					modalInstance.close();
				});

				var unregister = $scope.$on('sp.form.record.updated', function(evt, fields) {
					unregister();
					unregister = null;
					modalInstance.close();

					// Do something here to refresh this record...

					if (evt.stopPropagation)
						evt.stopPropagation();
					evt.preventDefault();
				});
			}

			function ModalInstanceCtrl($scope, $uibModalInstance, item) {
				$scope.item = item;

				$scope.ok = function() {
					$uibModalInstance.close();
				};

				$scope.cancel = function() {
					$uibModalInstance.dismiss('cancel');
				};
			}



		}
	};
}

function(dateUtils, $rootScope, $document, spDatePickerUtil, i18n) {
    var dateFormat = dateUtils.SYS_DATE_FORMAT;
	var dateTimeFormat = dateUtils.SYS_DATE_TIME_FORMAT;

    if ($rootScope.user && $rootScope.user.date_format)
		dateFormat = $rootScope.user.date_format;

	if ($rootScope.user && $rootScope.user.date_time_format)		
 		dateTimeFormat = $rootScope.user.date_time_format;

    function isValidDate(value, format){
        if (value === '')
            return true;

        return dateUtils.getDateFromFormat(value, format) != 0;
    }

    return {
        template:
            '<div class="row"><div class="col-xs-12 col-sm-6 col-md-4 question-date-time-field">' +
                '<div class="wrapper-xs alert alert-danger" ng-if="field.invalidValue">' +
                    '${Value is invalid}' +
                '</div>' +
                '<div ng-class="{\'input-group\': !snDisabled, \'has-error\': field.invalidValue}" style="width: 100%;">' +
                    '<input type="text" name="{{field.name}}" class="form-control" placeholder="{{::field.placeholder}}" ng-model="formattedDate" ng-model-options="{updateOn: \'blur\', getterSetter: true}" ng-disabled="snDisabled" aria-label="{{::field.label}} Field"/>' +
                    '<span class="input-group-btn question-calendar-icon" ng-hide="snDisabled">' +
                        '<input type="hidden" class="datepickerinput" ng-model="formattedDate" ng-readonly="true" />' +
                        '<button class="btn btn-default question-calendar-button" type="button" aria-label="{{::field.label}} Button">' +
                            '<glyph sn-char="calendar" />' +
                        '</button>' +
                    '</span>' +
                '</div>' +
            '</div>' + '<sp-survey-add-info ng-if="field.allow_add_info" field="field"></sp-survey-add-info>' + '</div>',
        restrict: 'E',
        replace: true,
        require: '?ngModel',
        scope: {
            field: '=',
            snDisabled: '=',
            snIncludeTime: '=',
            snChange: '&'
        },
        link: function(scope, element, attrs, ngModel) {
            var includeTime = scope.snIncludeTime;
			var cabrilloButton = {DONE: 1, CANCEL: 0};
			var cabrilloTitle = document.titleformat;
			var  initDateTimePicker = true;
            var format = includeTime ? dateTimeFormat.trim() : dateFormat.trim();
			format = format.replace(/y/g, 'Y').replace(/d/g, 'D').replace(/a/g, 'A');	
			var dp = element.find('.input-group-btn').datetimepicker({
                keepInvalid: true,
                pickTime: scope.snIncludeTime === true,
                format: "HH:mm:ss"
            }).on('dp.change', onDpChange);

			element.find('.form-control').on('click', function (e) {
				if (!scope.snDisabled) {
                    element.find('.input-group-btn').click();
				}
				e.preventDefault();
				return ;
            });

            function closeOnTouch(evt) {
                if (!jQuery.contains(dp.data('DateTimePicker').widget[0], evt.target)) {
                        dp.data('DateTimePicker').hide();
                }
            }

            function bindTouchClose() {
				if (initDateTimePicker) {
					initDateTimePicker = false;
					attachKeyboardEvent(dp.data('DateTimePicker'));
				}
                $document.on('touchstart', closeOnTouch);
				onShowDatePickerCabrillo(dp.data('DateTimePicker'));
            }

			function attachKeyboardEvent(picker) {
				picker.widget.on('click', '.datepicker .day div', $.proxy(onClickEvt, this, picker));
			}

			function onClickEvt(picker, e) {
				if (snmCabrillo.isNative()) {
					return;
				}
			}

			function onShowDatePickerCabrillo(picker) {
				$(document).find(".sp-page-root").addClass("hide");
				//event for subscribers to know modal open
				$rootScope.$broadcast('mesp.popup.open');
				//do not run when not in native app
				if (!snmCabrillo.isNative()) {
					return;
				}
				snmCabrillo.viewLayout.getTitle().then(function(title) {
					cabrilloTitle = title;
				});
				setCabrilloBottomButton(picker);
				setCabrilloTitle(picker);
				showCabrilloButtons(picker);
			}

			//Temp hide bottom button, need SC cate team response to event to reset bottom button back
			function setCabrilloBottomButton() {
				snmCabrillo.viewLayout.setBottomButtons([], function() {});
			}

			function showCabrilloButtons(picker) {
				var input = spDatePickerUtil.getSPDateickerInput(picker),
				value;

				var buttons = [{
					title: scope.translations["Cancel"],
					buttonStyle: snmCabrillo.viewLayout.REPLACE_BACK_BUTTON_STYLE,
					enabled: true
				},
				{
					title: scope.translations["Done"],
					enabled: true
				}];
				if (input) {
					value = input.val();
				}
				//hide Cabrillo top left back button
 				if(typeof snmCabrillo.viewLayout.hideBackButton == 'function') {
 					snmCabrillo.viewLayout.hideBackButton();
 				}
				snmCabrillo.viewLayout.setNavigationBarButtons(buttons, function (buttonIndex) {
					switch (buttonIndex) {
						case cabrilloButton.DONE:
						onClickCabrilloButton(cabrilloButton.DONE, picker, picker.date);
						break;
						case cabrilloButton.CANCEL:
						onClickCabrilloButton(cabrilloButton.CANCEL, picker, value);
						break;
					}
				});
			}

			function onClickCabrilloButton(button, picker, value) {
				$(document).find(".sp-page-root").removeClass("hide");
				//event for subscribers to reset their header (title, buttons..etc)
				$rootScope.$broadcast('mesp.popup.close');
				//we can remove below once subscribers handle mesp_header_reset event
				snmCabrillo.viewLayout.setTitle(cabrilloTitle);
				snmCabrillo.viewLayout.setNavigationBarButtons([], null);
				//show Cabrillo top left back button
				if(typeof snmCabrillo.viewLayout.showBackButton == 'function') {
					snmCabrillo.viewLayout.showBackButton();
				}
				if(button === cabrilloButton.CANCEL || scope.field.stagedValue == "") {
					scope.formattedDate(value);
				}
				picker.hide();
			}

			function setCabrilloTitle(picker) {
				if(picker.options.pickTime) {
					snmCabrillo.viewLayout.setTitle(i18n.getMessage('Date and time'));
				} else {
					snmCabrillo.viewLayout.setTitle(i18n.getMessage('Date'));
				}
			}

            function unBindTouchClose() {
                $document.off('touchstart', closeOnTouch);
            }
            dp.on('dp.show', bindTouchClose).on('dp.hide', unBindTouchClose);

            function onDpChange(e) {
                scope.formattedDate(e.date.format(format));

                if (!scope.$root.$$phase)
                    scope.$apply();
            }

            function validate(formattedDate) {
                var field = scope.field;
                field.invalidValue = false;
				var format = scope.snIncludeTime ? dateTimeFormat.trim() : dateFormat.trim();
                if (formattedDate == null || formattedDate == '') {
                    dp.data('DateTimePicker').setValue(new Date());
                    return '';
                }

                if (isValidDate(formattedDate, format)) {
                    var d = dateUtils.getDateFromFormat(formattedDate, format);
                    dp.data('DateTimePicker').setValue(scope.formattedDate);
                }
                else {
                    field.invalidValue = true;
                }
                return formattedDate;
            }

            if (ngModel) {
                ngModel.$parsers.push(validate);

                // Called when setting value from g_form.setValue()
                // Shows a red border if the date isn't formatted properly
                ngModel.$render = function() {
                    validate(ngModel.$viewValue);
                };

                // Connect the input field's ng-model to this directive's ng-model
                scope.formattedDate = function(formattedValue) {
                    if (angular.isDefined(formattedValue)) {
                        ngModel.$setViewValue(formattedValue);
                        if (scope.snChange) scope.snChange({newValue: formattedValue});
                    }

                    return ngModel.$viewValue;
                };
            } else {
                scope.formattedDate = function(formattedValue) {
                    if (angular.isDefined(formattedValue)) {
                        scope.field.value = validate(formattedValue);
                        if (scope.snChange) scope.snChange({newValue: formattedValue});
                    }

                    return scope.field.value;
                };
                scope.$watch('field.value', function(newValue, oldValue){
                    if (newValue != oldValue) {
                        validate(newValue);
                    }
                });
            }

            scope.$on('$destroy', function() {
                dp.off('dp.change', onDpChange);
                unBindTouchClose();
            });

			scope.translations = [];
			i18n.getMessages(["Enter date in format", "Use format", "Entered date not valid. Enter date in format", "Show calendar", "Done",
		"Cancel"], function(msgs){
				scope.translations = msgs;
			});
        }
    }
}

