function(){
	return {
		template:
		'<nav class="cm-pagination" aria-label="Knowledge Pagination">'+
		'<ul class="pagination" style="cursor:pointer;">'+
		'<li ng-class="{disabled:currentPage === 1}" class="page-item">'+
		'<a ng-click="changePage(currentPage - 1)" class="page-link" href="javascript:void(0);" aria-label="Previous">'+
		'<i class="fa fa-caret-left" data-toggle="tooltip" title = "${Previous page}" aria-hidden="true"></i>'+
		'<span class="sr-only">${Previous}</span>'+
		'</a>'+
		'</li>'+
		'<li ng-repeat="index in displayPages track by $index" ng-class="{active:currentPage === index}">'+
		'<a href="javascript:void(0);" ng-click="changePage(index)" aria-current="{{currentPage === index ? page : false}}" aria-label="${Page} {{index}}">{{index}}</a>'+
		'</li>'+
		'<li ng-class="{disabled:currentPage === pageCount && pageCount != 1}" class="page-item"> '+
		'<a ng-click="changePage(currentPage + 1)" class="page-link" href="javascript:void(0);" aria-label="Next">'+
		'<i class="fa fa-caret-right" data-toggle="tooltip" title = "${Next page}" aria-hidden="true"></i>'+
		'<span class="sr-only">${Next}</span>'+
		'</a>'+
		'</li>'+
		'</ul>'+
		'</nav>'+
		'<style>'+
		'.tooltip-inner {white-space:nowrap;max-width:none;}'+
		'.tooltip.top{margin-top:-6px;padding-top: 5px;padding-right: 0px;padding-bottom: 5px;padding-left: 0px;}'+
		'</style>',
		scope: {
			pagedata:"=",
			onPageChange:"&",
			getNextSetCount:"&"
		},

		controller : function($scope, $rootScope){
			$scope.countPages = function (totalCount, pageSize) {
				return Math.ceil(totalCount / pageSize);
			};

			$scope.setPageRange = function (startIndex) {
				$scope.displayPages = [];
				var pageCount = $scope.countPages($scope.pagedata.totalCount, $scope.pagedata.pageSize);
				var end = startIndex + ($scope.pagedata.noOfLinks-1);

				while(end > pageCount && pageCount != 1){end--;}

				while(startIndex > 0 && startIndex <= end && (pageCount == 1 || end <= pageCount)){
					$scope.displayPages.push(startIndex++);
				}

				$scope.pageCount = pageCount;
			};

			var unRegReset =$rootScope.$on('sp.kb.updated.pagination.count',function (event,data){
				$scope.initialize();
			});

			var startIndex;
			$scope.initialize = function(){
				//Initialize the pagination
				startIndex = 1;
				$scope.setPageRange(startIndex);
				$scope.currentPage = 1;
				$scope.page = "page";
			}
			$scope.initialize();

			$scope.changePage = function (index) {
				if(index == $scope.currentPage) {
					return;
				}
				if (index < 1 || index > $scope.pageCount) {
					return;
				}
				if(index > $scope.currentPage){
					$scope.currentPage = index;
					if($scope.pagedata.noOfLinks==1){
						startIndex += ($scope.pagedata.noOfLinks);
						$scope.setPageRange(startIndex);
					}else if($scope.currentPage % ($scope.pagedata.noOfLinks-1) == 1 || $scope.pagedata.noOfLinks==2){
						startIndex += ($scope.pagedata.noOfLinks-1);
						$scope.setPageRange(startIndex);
					}
				}else if(index < $scope.currentPage){
					if($scope.pagedata.noOfLinks==1 && $scope.currentPage != 1){
						startIndex -= ($scope.pagedata.noOfLinks);
						$scope.setPageRange(startIndex);
					}else if(($scope.currentPage % ($scope.pagedata.noOfLinks-1) == 1 || $scope.pagedata.noOfLinks==2)&& $scope.currentPage != 1){
						startIndex -= ($scope.pagedata.noOfLinks-1);
						$scope.setPageRange(startIndex);
					}
					$scope.currentPage = index;
				}
				$scope.onPageChange({currentPage: $scope.currentPage});
			};

			var unRegPageResult = $rootScope.$on('sp.kb.updated.pagination.articleCount',function (event,data){
				$scope.pagedata.totalCount = $scope.pagedata.totalCount+data.count;
				$scope.setPageRange(startIndex);
			});

			$scope.$on("$destroy", function() {
				unRegPageResult();
				unRegReset();

			});

		}
	}
}

// <sc-toggle-data sn-data="HTML object" sn-limit="" sn-less-text="Read Less" sn-more-text="Read More"></sc-toggle-data>
function scToggleData(i18n, cabrillo) {
	"use strict";
	return {
		restrict: 'E',
		scope: {
			moreText: '@snMoreText',
			lessText: '@snLessText',
			limit: '<snLimit',
			data: '<snData'
			},

		template: '<now-message key="Show more" value="${Show more}"/>' +
				'<now-message key="Show less" value="${Show less}"/>',

		link: function(scope, elem) {
			var moreText = scope.moreText ||  i18n.getMessage('Show more');
			var lessText = scope.lessText || i18n.getMessage('Show less');

			var moreTextCmp = ' <a class="read-more">' + moreText + '</a>';
			var lessTextCmp = ' <a class="read-less">' + lessText + '</a>';

			if (cabrillo.isNative()) {
					moreTextCmp = ' <div style="display: flex;justify-content: center;align-items: center;" class="m-t"> <a class="btn btn-default read-more" role="button" aria-expanded="false" tabindex="0">' + moreText + '</a></div>';
					lessTextCmp = ' <div style="display: flex;justify-content: center;align-items: center;" class="m-t"> <a class="btn btn-default read-less" role="button" aria-expanded="true" tabindex="0">' + lessText + '</a></div>';
			}

			var limit = angular.isUndefined(scope.limit) || !scope.limit || scope.limit < 1 ? 150 : scope.limit;
			var text = angular.isUndefined(scope.data) ? '' : scope.data;
			var ellipsis = '<span aria-hidden="true">...</span>';

			var textHtml = String(text);
			var textNonHtml = textHtml.replace(/<([^>\s]*)[^>]*>/gm, '');
			var charCount = textNonHtml.length;

			if (charCount > limit && cabrillo.isNative())
				textHtml = htmlSubstring(textHtml, limit, ellipsis, moreTextCmp, lessTextCmp);

			elem.html(textHtml);
			elem.find('.read-more').on('click', function() {
				elem.find('.less-text').hide();
				elem.find('.more-text').show();
				elem.find('.read-less').focus();
				});

			elem.find('.read-less').on('click', function() {
				elem.find('.more-text').hide();
				elem.find('.less-text').show();
				elem.find('.read-more').focus();
				});

			function htmlSubstring(textHtml, limit, ellipsis, moreText, lessText) {
				var tags;
				var tagFinder = /<([^>\s]*)[^>]*>/gm;
				var stack = [];
				var lastTagIndex = 0;
				var result = '';

				//finding tag in each line
				while ((tags = tagFinder.exec(textHtml)) && limit) {
					//get the text between previous iteration tag-end index and current iteration tag-start index.
					var temp = textHtml.substring(lastTagIndex, tags.index).replace(/\s\s+/g,'').substr(0, limit);
					result += temp;
					limit -= temp.length;
					lastTagIndex = tagFinder.lastIndex;	//updating last-index of current iteration

					if (limit) {
						result += tags[0];
						if (tags[1].indexOf('/') === 0)
							stack.pop();
						else if (tags[1].lastIndexOf('/') !== tags[1].length-1)
							stack.push(tags[1]);
					}
				}

				result += ellipsis;
				//fix the unclosed tags
	    	    while (stack.length) {
	    	    	result += '</' + stack.pop() + '>';
	    	    	}
				result += moreText;

	    	    result = '<span class="less-text">' + result +  '</span><span class="more-text" tabindex="-1">' + textHtml + lessText + '</span>';
	    	    return result;

			}
		}
	};

}

function spLazyWidget(spUtil, $timeout) {

	var CACHE = {};
	var MAX_CACHE_ITEMS = 100;
	var SPINNER_DELAY = 300;

	var templateHtml = [
		'<div class="lazywidget" ng-class="{loading: ctrl.loading}">',
		'		<div class="__spinner">',
		'			<i class="fa fa-circle-o-notch fa-2x fa-spin" aria-hidden="true"></i>',
		'		</div>',
		'		<sp-widget widget="ctrl.widgetConfig" ng-if="ctrl.widgetConfig"/>',
		'</div>'
	].join('');

	var styles = [
		'.lazywidget > .__spinner {',
		'		text-align:center;',
		'		display: none;',
		'		color:#b7b7b7;',
		'}',
		'.lazywidget.loading > .__spinner {',
		'		display: block;',
		'}'
	].join('');

	function Controller($scope) {
		this.loading = false;
		this.widgetConfig = null;
		this._importWidget(this.name, this.options);
	}

	Controller.prototype = {

		_importWidget: function(widgetName, widgetOptions) {
			var self = this;
			var cachedConfig = this._getCache(widgetName, widgetOptions);
			if (cachedConfig) {
				this._renderWidget(cachedConfig);
				return;
			}
			var timer = $timeout(function() {
				// show spinner only if wait is longer than 300ms
				self.loading = true;
			}, SPINNER_DELAY);
			spUtil.get(widgetName, widgetOptions).then(function(widgetConfig) {
				$timeout.cancel(timer);
				self._renderWidget(widgetConfig);
				self._setCache(widgetName, widgetOptions, widgetConfig);
			});
		},

		_renderWidget: function(widgetConfig) {
			this.loading = false;
			this.widgetConfig = widgetConfig;
		},

		_setCache: function(widgetName, widgetOptions, widgetConfig) {
			if (Object.keys(CACHE).length > MAX_CACHE_ITEMS) {
				return;
			}
			var cacheKey = this._createCacheKey(widgetName, widgetOptions);
			CACHE[cacheKey] = widgetConfig;
		},

		_getCache: function(widgetName, widgetOptions) {
			var cacheKey = this._createCacheKey(widgetName, widgetOptions);
			return CACHE[cacheKey] || null;
		},

		_createCacheKey: function(widgetName, widgetOptions) {
			widgetOptions = widgetOptions || {};
			var optionTokens = Object.keys(widgetOptions).sort().reduce(function(acc, key) {
				acc.push(key, JSON.stringify(widgetOptions[key]));
				return acc;
			}, []);
			return widgetName + ':' + optionTokens.join(':');
		}

	};

	function injectStyles() {
		var styleEl = document.createElement('style');
		styleEl.type = 'text/css';
		styleEl.innerHTML = styles;
		document.head.appendChild(styleEl);		
	}

	if(document.readyState === 'complete' || document.readyState === 'interactive') {
		injectStyles();
	} else {
		window.addEventListener("DOMContentLoaded", injectStyles);
	}

	return {
		template: templateHtml,
		restrict: 'E',
		controller: Controller,
		scope: {},
		bindToController: {
			name: '<',
			options: '<?'
		},
		controllerAs: 'ctrl'
	};

}

function($rootScope) {
	return {
		template: '<div>' +
		'<div id="notAnswered{{field.sys_id}}">' +
			'<div class="wrapper-xs alert alert-danger" ng-if="!field.isMandatoryFilled">{{c.showMandatoryMessage()}}</div>' +
