				'<span ng-if="field.mandatory && field.value">${Required filled} - </span>' +
				'<span>{{c.getAriaLabel(field)}}</span>' +
				'<span ng-if="!field.isMandatoryFilled">{{c.showMandatoryMessage()}}</span>' +
			'</legend>' +
					'<div  ng-if="!isMobile" class="wrapper-xs row" ng-class="{single_question_survey_image: data.isKioskSurvey}" role="radiogroup">' +
						'<div aria-describedby="details_{{field.sys_id}}" ng-repeat="c in field.choices track by $index" class="autofocus-class col-xs-6  wrapper-xs text-center" ng-class="{\'col-sm-2\': !data.isKioskSurvey, \'col-sm-3\': data.isKioskSurvey, \'col-md-2\': data.isKioskSurvey, \'img-scale-{{::field.sys_id}}\': true, \'has-error\':!field.isMandatoryFilled, \'autofocus-error\':!field.isMandatoryFilled}" style="display:inline-block; cursor: pointer;" role="radio" ng-keydown="selectOption($event, $index)" aria-checked="{{field.value == c.value}}" aria-label="{{::c.label}}" tabindex="{{::getTabIndex($index, c)}}" ng-click="chooseOption($index)">' +
							'<div ng-if="(c.selected_image)">' +
			 					'<div ng-if="(!field.unSelectedImageAllPresent)">' +
			 						'<img src="{{c.image}}?t=small" role="presentation" alt="" ng-class = "{\'img_size\' : !data.isKioskSurvey}" ng-style="{\'opacity\':c.opacity}" ng-click="c.isDisabled || selectChoice($event, c)" ng-disabled="{{c.isDisabled}}" style="margin-bottom:12px;"/>' +
									'<button tabindex="-1" style="overflow: hidden; text-overflow: ellipsis; font-weight:bold" class="btn btn_one_click"   ng-if="c.value == field.value && data.isKioskSurvey" ng-click="selectChoice($event, c)" ng-disabled="{{c.isDisabled}}" title="{{::c.label}}">{{::c.label}}</button>' +
									 '<button tabindex="-1" style="overflow: hidden; text-overflow: ellipsis;" class="btn btn-default btn-block " ng-class=" { \'btn-primary\': c.value == field.value }" ng-if="!data.isKioskSurvey" ng-click="selectChoice($event, c)" title="{{::c.label}}">{{::c.label}}</button>' +
									'<sp-survey-end-notes data="data" ng-if="c.value == field.value">' +
									'</sp-survey-end-notes>' +
								'</div>' +
						    	'<div ng-if="(field.unSelectedImageAllPresent)">' +
						    	   '<img src="{{c.image}}?t=small" role="presentation"  alt="" ng-class = "{\'img_size\' : !data.isKioskSurvey}" ng-click="c.isDisabled || selectChoice($event, c)" ng-disabled="{{c.isDisabled}}" style="margin-bottom:12px;" ng-style="{\'opacity\':c.opacity}"/>' +
								   '<button style="overflow: hidden; text-overflow: ellipsis; font-weight:bold;" tabindex="-1" class="btn btn_one_click disable_button" ng-if="c.value == field.value && data.isKioskSurvey" ng-click="selectChoice($event, c)" ng-disabled="{{c.isDisabled}}" title="{{::c.label}}">{{::c.label}}</button>' +
								   '<button style="overflow: hidden; text-overflow: ellipsis;" tabindex="-1" class="btn btn-default btn-block" ng-class=" { \'btn-primary\': c.value == field.value } " ng-if="!data.isKioskSurvey" ng-click="selectChoice($event, c)" title="{{::c.label}}">{{::c.label}}</button>' +
									'<sp-survey-end-notes data="data" ng-if="c.value == field.value">' +
									'</sp-survey-end-notes>' +
								'</div>' +
							'</div>' +
							'<div ng-if="(!c.selected_image)" style="margin: auto;">' +
								'<div ng-click="c.isDisabled || selectChoice($event, c)" ng-disabled="{{c.isDisabled}}" style="margin: auto; overflow: hidden; text-overflow: ellipsis; line-height: 64px; align: center; vertical-align: middle; border: 1px solid black; width: 64px; text-align: center; height: 64px; vertical-align: middle; margin-bottom: 12px;"  title="{{::c.label}}">{{::c.label}}</div>' +
								'<button style="overflow: hidden; text-overflow: ellipsis;" tabindex="-1" aria-pressed="{{c.value == field.value}}" class="btn btn-default btn-block" ng-class=" { \'btn-primary\': c.value == field.value } " ng-click="selectChoice($event, c)" ng-if="!data.isKioskSurvey" title="{{::c.label}}">{{::c.label}}</button>' +
								'<button style="overflow: hidden; text-overflow: ellipsis;font-weight:bold" class="btn" tabindex="-1"  ng-click="selectChoice($event, c)" ng-if="c.value == field.value && data.isKioskSurvey" ng-disabled="{{c.isDisabled}}" title="{{::c.label}}">{{::c.label}}</button>' +
								'<sp-survey-end-notes data="data"  ng-if="c.value == field.value">' +
								'</sp-survey-end-notes>' +
							'</div>' +
						'</div>' +
					'</div>' +
					'<div  ng-if="isMobile" class="wrapper-xs row">' +
						'<div class="col-xs-12 wrapper-sm text-center">' +
							'<div ng-if="(mobileChoice.image)">' +
							 	'<img src="{{mobileChoice.image}}?t=small" title="{{::mobileChoice.label}}" alt="{{mobileChoice.label}}" style="width:64px; height:64px;" />' +
							 '</div>' +
							'<div ng-if="!(mobileChoice.image)">' +
							 	'<div style="margin: auto; overflow: hidden; text-overflow: ellipsis; line-height: 52px; align: center; vertical-align: middle; border: 1px solid black; width: 64px; text-align: center; height: 52px; vertical-align: middle; margin-bottom: 12px;" title="{{::mobileChoice.label}}">{{mobileChoice.label}}</div>' +
							'</div>' +
						'</div>' +
						'<div ng-repeat="c in field.choices track by $index" class="wrapper-xs text-center">' +
							'<button aria-describedby="details_{{field.sys_id}}" class="btn btn-default btn-block autofocus-class" ng-class="{ \'btn-primary\': c.value == field.value }" aria-pressed="{{c.value == field.value}}" ng-click="selectChoice($event,c)" title="{{::c.label}}">{{::c.label}}</button>' +
		'<sp-survey-end-notes data="data"  ng-if="c.isKioskSurvey">' +
                             '</sp-survey-end-notes>' +
						'</div>' +
					'</div>' +
					'<sp-survey-add-info ng-if="field.allow_add_info && field.type != \'template\'" field="field"></sp-survey-add-info>' +
				'</fieldset>' +
			'</div>',

		scope: {
			field: '=',
			c: '=',
			data: '=',
			isMobile: '='
		},

		controller: function($scope) {
			var keys = {
				LEFT: 37,
				UP: 38,
				RIGHT: 39,
				DOWN: 40,
				SPACE: 32
			};

			var unselectChoice = function(choice, firstLoad) {
				choice.image = (choice.selected_image) ? ((!$scope.field.unSelectedImageAllPresent) ? choice.selected_image  : choice.unselected_image) : choice.selected_image;
				if (firstLoad) {
					if (!$scope.field.unSelectedImageAllPresent)
						choice.opacity = 0.5;
					else
						choice.opacity = 1;
				} else {
					 if ($scope.data.isKioskSurvey) {
						 choice.isDisabled = true;
						 if ($scope.field.value != choice.value)
							 choice.opacity = 0.15;
						 else
							 choice.opacity = 1;
					 } else {
						 if (!$scope.field.unSelectedImageAllPresent) {
							 if ($scope.field.value == choice.value)
								 choice.opacity = 1;
							 else
								 choice.opacity = 0.5;
						 }
					 }
				}
			};

			var unselectAllChoices = function(firstLoad) {
				for (var i=0; i < $scope.field.choices.length; i++) {
						var choice = $scope.field.choices[i];
					    unselectChoice(choice, firstLoad);
				}
				if (firstLoad) {
					for (i=0; i < $scope.field.choices.length; i++) {
						var choice = $scope.field.choices[i];
						$scope.mobileChoice = choice;
						if (!$scope.field.selectedImageOnly && $scope.field.value == choice.value) {
							choice.image = choice.selected_image;
							$scope.mobileChoice = choice;
							break;
						}
						if ($scope.field.selectedImageOnly && $scope.field.value == choice.value) {
							$scope.mobileChoice = choice;
							break;
						}
					}
					if (i === $scope.field.choices.length) {
						$scope.mobileChoice = $scope.field.choices[0];
					}
				}
			};


			$scope.selectChoice = function(event, choice) {
				event.stopPropagation();
				$scope.field.value = choice.value;
				unselectAllChoices(false);
				if (!$scope.field.selectedImageOnly)
					choice.image = choice.selected_image;

				$scope.mobileChoice = choice;
				if ($scope.data.isKioskSurvey && !$scope.isMobile)
					$rootScope.submit($scope.page, $scope.c.state);

				// Update isMandatoryFilled
			   $scope.field.isMandatoryFilled = $scope.c.isMandatoryFilled($scope.field);
				};

			$scope.selectOption = function(event, currentIndex) {
				if (event.which == keys.SPACE) {
					event.preventDefault();
					$scope.selectChoice(event, $scope.field.choices[currentIndex]);
					return;
				}
				var length = $scope.field.choices.length;
				var select = -1;
				switch(event.which) {
					case keys.LEFT:
					case keys.UP:
						if (currentIndex == 0)
							select = length - 1;
						else
							select = currentIndex - 1;
						break;
					case keys.RIGHT:
					case keys.DOWN:
						if (currentIndex == length - 1)
							select = 0;
						else
							select = currentIndex + 1;
						break;

				}
				if (select == -1)
					return;
				event.preventDefault();
				$scope.chooseOption(event, select);
			};

			$scope.chooseOption = function(event, position) {
				var elements = document.getElementsByClassName("img-scale-" + $scope.field.sys_id);
				if (elements && elements[position]) {
					for (var i = 0; i < elements.length; i++) {
						elements[i].tabIndex = -1;
					}
					$scope.selectChoice(event, $scope.field.choices[position]);
					elements[position].tabIndex = 0;
					elements[position].focus();
				}
			};

			$scope.getTabIndex = function(index, choice) {
				if ($scope.field.value)
					return $scope.field.value === choice.value ? "0" : "-1";
				return index === 0 ? "0" : "-1";
			};

			// Do it the first time so that all images show up
			unselectAllChoices(true);
		}
	};
}

function(cabrillo, $rootScope, $timeout, i18n, spAriaUtil) {
	'use strict';
	return {
		restrict: 'E',
		scope: {
			'field': '=',
			'attachmentHandler': '=',
			'c': '='
		},
		template: function() {
			var inputTemplate;
			if (cabrillo.isNative()) {
				inputTemplate = '<a href="#" elem-id="{{::field.sys_id}}_attachment_add" ng-click="showAttachOptions()" class="panel-button sp-attachment-add"><span class="glyphicon glyphicon-camera"></span></a>';
			} else {
				inputTemplate = '<label style="cursor:pointer; font-size:22px; margin-top:-6px; position:relative; bottom:-6px;">' +
									'<input type="file" multiple="true" ng-file-select="attachmentHandler.onFileSelect($files)" class="sp-attachments-input sr-only" tabindex="-1"/>' +
									'<button aria-describedby="details_{{field.sys_id}} notAnswered{{field.sys_id}}" ng-class="{\'has-error\': !field.isMandatoryFilled, \'autofocus-error\':!field.isMandatoryFilled}" elem-id="{{::field.sys_id}}_attachment_add" style="color: inherit" ng-click="attachmentHandler.openSelector($event)" class="sp-attachment-add autofocus-class wrapper-xs btn-link btn chevron-icon" aria-required="{{::field.mandatory}}" data-toggle="tooltip" data-placement="top" data-original-title="${Choose attachments}" aria-label="{{c.getAriaLabel(field)}}">' +
										'<span class="glyphicon glyphicon-paperclip"></span>' +
									'</button>' +
									'</label>';
			}
			return [
				'<span class="file-upload-input">',
					inputTemplate,
				'</span>'
			].join('');
		},
		controller: function($element, $scope) {
			$scope.attachmentClassNames = 'btn-default attachment-btn btn-primary icon-paperclip icon-camera list-group-btn';

			$scope.showAttachOptions = function() {
				var handler = $scope.attachmentHandler;

				cabrillo.attachments.addFile(
					handler.tableName,
					handler.tableId,
					null,
					{maxWidth: 1000, maxHeight: 1000}
				).then(function(data) {
					handler.getAttachmentList();
					$rootScope.$broadcast("added_attachment");
				}, function() {
					console.log('Failed to attach new file');
				});
			};

			$scope.$on('attachment_select_files', function(e) {
				$timeout(function() {
					$($element).find('.sp-attachments-input').click();
				});
			});

			function focusOnAttachmentIcon(questionId) {
				var paperclip = $($element).find("[elem-id='"+ questionId +"_attachment_add']")[0];
				if (paperclip) {
					paperclip.focus();
				}
			}

			$scope.$on('survey.attachment.upload.success', function(evt, args) {

				// Update isMandatoryFilled
			   $scope.field.isMandatoryFilled = $scope.c.isMandatoryFilled($scope.field);


				// timeout is required for screen reader to pick up the message once file upload prompt is dismissed
				$timeout(function() {
				    spAriaUtil.sendLiveMessage(i18n.getMessage('Attachment uploaded successfully'));
				}, 2000);
			});

			$scope.$on('survey.attachment.delete.success', function(evt, args) {

				// Update isMandatoryFilled
			   $scope.field.isMandatoryFilled = $scope.c.isMandatoryFilled($scope.field);

				// timeout is required for screen reader to pick up the message once deletion confirmation modal is dismissed
				$timeout(function() {
				    spAriaUtil.sendLiveMessage(i18n.getMessage('Attachment deleted successfully'));
				}, 2000);
			});

			$scope.$on('survey.attachment.list.update', function(evt, args) {
				// Focus should move back to attachment icon
				var targetQuestionId = args.questionSysId;
				if (targetQuestionId === $scope.field.sys_id) {// Make sure it's the same question
					focusOnAttachmentIcon(targetQuestionId);
				}
			});
		}
	};
}

function() {
	return {
		template:
		'<div class="container-fluid">' +
		'<div class="row">' +
		'<div class="col-xs-12 col-sm-6">' +
		'<div id="notAnswered{{field.sys_id}}" >' +
		'<div class="wrapper-xs alert alert-danger" ng-if="!field.isMandatoryFilled">{{c.showMandatoryMessage()}}</div>' +
		'</div>' +
		'<fieldset>' +
			'<legend class="sr-only">' +
				'<span ng-if="field.mandatory && !c.isMandatoryFilled(field)">${Required} - </span>' +
				'<span ng-if="field.mandatory && c.isMandatoryFilled(field)">${Required filled} - </span>' +
				'<span>{{c.getAriaLabel(field)}} ${ {{field.choices.length}} choices}</span>' +
				'<span ng-if="!field.isMandatoryFilled">{{c.showMandatoryMessage()}}</span>' +
			'</legend>' +

		'<ul style="list-style-type: none;padding:0" class="sps-ranking-list">'+
			'<li class="row" ng-repeat="c in field.choices track by $index" style="border: 1px solid #ccc;border-radius: 5px;margin-bottom: 5px;">'+
				'<div class="col-xs-2 wrapper-xs" style="position:static">' +
					'<select ng-class="{\'has-error\': !field.isMandatoryFilled, \'autofocus-error\':!field.isMandatoryFilled}" elem-id="{{::c.sys_id}}" name="{{c.name}}" aria-label="{{::c.label}}" ng-change="onSelectChange(c)" autocomplete="off" aria-describedby="details_{{field.sys_id}}" class="form-control autofocus-class" style="padding:3px;" sn-select-width="auto" ng-model="c.value" ng-disabled="field.isReadonly()">' +
						'<option value="-1" ng-selected="c.value == -1" aria-label="${Please select a rank}"></option> '+
						'<option ng-repeat="i in field.choices track by $index" value="{{$index + 1}}" ng-selected="$index + 1 == c.value">{{$index + 1}}</option> ' +
					'</select>'+
				'</div>' +
				'<div class="col-xs-9 wrapper-sm" style="position:static" aria-hidden="true"><span id="label_{{::c.sys_id}}" class="sps-checkbox-label">{{c.label}}</span></div>' +
				'<div class="col-xs-1 wrapper-sm" style="position:static;" aria-hidden="true"><span class="fa fa-bars pull-right wrapper-xs"></span></div>'+
			'<li>'+
		'</ul>' +
		'</fieldset>' +
		'</div>' +
		'<sp-survey-add-info ng-if="field.allow_add_info" field="field"></sp-survey-add-info>' +
		'</div>' +
		'</div>',
		controller: function($scope) {
			$scope.field.choices.sort(function(a, b) {return a.value - b.value;});
			function swap(a, i, j) {
				if (i < 0 || i >= a.length || j < 0 || j >= a.length)
					return;
				var temp = a[i];
				a[i] = a[j];
				a[j] = temp;
			}
			function getCurrentPosition(sys_id) {
				var lis = $($scope.element).find('li');
				for (var i=0; i<lis.length; i++) {
					var spanId = $(lis[i]).find('span').attr('id').slice("label_".length);
					if (spanId && spanId.trim().indexOf(sys_id.trim()) == 0)
						return (i + 1);
				}
				return -1;
			}
			$scope.onSelectChange = function(choice) {
				$scope.field.choices.forEach(function(c) {
					if (c.value == choice.value && c.sys_id != choice.sys_id)
						c.value = '-1';
				});
				var oldValue = getCurrentPosition(choice.sys_id);
				var order = $scope.sortable.toArray();
				swap(order, parseInt(choice.value) - 1, parseInt(oldValue) - 1);
				$scope.sortable.sort(order);
				// Auto focus on the last modified action item after changing position
				var rankToIndex = choice.value - 1;
				var lastModifiedChoice = angular.element($($scope.element).find('select')[rankToIndex]);
				lastModifiedChoice.focus();
				$scope.field.isMandatoryFilled = $scope.c.isMandatoryFilled($scope.field);
			}
	   },
		link: function(scope, element) {
			var sortable = new Sortable($(element).find('ul')[0], {
				onUpdate: function () {
					var rankList = $(element).find('select');
					for(var i = 0; i < rankList.length; i++) {
						rankList[i].value = i + 1;
						$(rankList[i]).triggerHandler('change');
					}
				}
			});
			setTimeout(function() {
				$(element).find('li').mouseenter(function(elem) {
					$(this).addClass('hovered');
				})
				$(element).find('li').mouseleave(function(elem) {
					$(this).removeClass('hovered');
				})
			}, 10);
			scope.sortable = sortable;
			scope.element = element;
		}
	};
}

function ($timeout) {
	return {
		restrict: 'C',
		scope: true,
		link: function (scope, element) {
				if (angular.isFunction(element.select2)) {
					element.css("opacity", 0);
					$timeout(function() {
						element.css("opacity", 1);
						element.select2();
						element.parent().find(".select2-focusser").attr("aria-expanded", false);
					});
				}

				element.on("select2-open", function() {
					if (element.data('select2').search.val() === " ")
					    element.select2("search", "");

					element.parent().find(".select2-focusser").attr("aria-expanded", true);

				});

				element.on("select2-close", function() {
					element.parent().find(".select2-focusser").attr("aria-expanded", false);
				});
		}
	}
}

function invokeMultiLevelParentFunc($rootScope, $timeout) {
	"use strict";
	return {
		restrict: 'A',
		scope: {
			invokeEvents: '@',
			invokeEventHandlers: '@',
			invokeWithModel: '='
		},
		link: function (scope, elem, attrs) {
			var eventsToHandle = attrs.invokeEvents.split(',').map(function(eItem){
				return eItem.trim();
			});
			var eventHandlers = attrs.invokeEventHandlers.split(',').map(function(ehItem){
				return ehItem.trim();
			});

			eventsToHandle.forEach(function (e, index) {
				elem.bind(e, function ($event) {
					if (typeof $rootScope[eventHandlers[index]] === 'function') {
						$event.stopPropagation();
						$rootScope[eventHandlers[index]]($event, scope.invokeWithModel);
					}
				});
			});
		}
	};
}

function($timeout, i18n, $rootScope) {
	return {
		template: 
		'<select name="{{field.name}}" autocomplete="off" sn-select-width="auto" ng-model="field.value" ng-disabled="c.isDisabled || field.isReadonly()" aria-label="{{::field.aria_label}}" ng-change="c.isDisabled || selectChoice(c)" ng-readonly="field.readOnly" aria-required="{{::field.mandatory}}">' +
			'<option ng-repeat="c in field.choices track by $index" value="{{c.value}}" ng-selected="isSelected(c)">{{c.label + getChoicePrice(c, variable)}}</option> ' +
		'</select>',
		restrict: 'E',
		replace: true,
		require: '?ngModel',
		scope: {
			'field' : '=',
			'options': '=?',
			'data': '=',
			'c': '=',
			'isMobile': '=',
		},

		controller: function ($scope, $element) {
			var chooseLabel = '-- ' + i18n.getMessage("Choose") + ' --';
			if ($scope.field.choices.length < 1 || $scope.field.choices[0].label != chooseLabel)
				$scope.field.choices = [{label: chooseLabel, value: '', sys_id: 'NONESYSID'}].concat($scope.field.choices);
			$scope.selectChoice = function(choice) {
				if ($scope.data.isKioskSurvey && !$scope.isMobile) {
					choice.isDisabled = true;
					$rootScope.submit($scope.page, $scope.c.state);
				}
			};

			$scope.isSelected = function(choice) {
				var isSelected = $scope.field.value === choice.value
				if(isSelected){
				    $scope.selectedlabel = choice.label 
				}
				return isSelected;
			}
			$scope.$on('nextOrSubmitClick', function(e){
				if (!$scope.field.value && $scope.field.mandatory)
					$element.parent().find('.select2-focusser').addClass('autofocus-error');
			})
		},

		link: function (scope, element, attrs, ngModel) {
			var field = scope.field;
			var fieldOptions;
			scope.$watchCollection('field.optionStack', function() {
				scope.options = applyOptionStack(fieldOptions, field.optionStack);
			});

			setDefaultOptions();

			if (field.choices) {
				setChoiceOptions(field.choices);
			}

			function setDefaultOptions() {
				setChoiceOptions([{
					value: scope.field.value,
					label: scope.field.displayValue || scope.field.placeholder
				}]);
			}

			function setChoiceOptions(options) {

				// force string values
				if (options) {
					options.forEach(function(option) {
						option.value = String(option.value);
					});
				}
				fieldOptions = options;
				scope.options = applyOptionStack(options, scope.field.optionStack);
			}

			function applyOptionStack(options, optionStack) {
				if (!optionStack || optionStack.length == 0) {
					return options;
				}
				var newOptions = angular.copy(options);
				if (!newOptions) {
					newOptions = [];
				}
				optionStack.forEach(function(item) {
					switch (item.operation) {
						case 'add':
							var newOption = {
								label: item.label,
								value: item.value
							};
							if (typeof item.index === 'undefined') {
								newOptions.push(newOption);
							} else {
								newOptions.splice(item.index, 0, newOption);
							}
							break;
						case 'remove':

							// normalize comparison to string values
							var itemValue = String(item.value);
							for (var i = 0, iM = newOptions.length; i < iM; i++) {
								var optionValue = String(newOptions[i].value);
								if (optionValue !== itemValue) {
									continue;
								}
								newOptions.splice(i, 1);
								break;
							}
							break;
						case 'clear':
							newOptions = [];
							break;
						default:
					}
				});
				return newOptions;
			}

			function setModelValue(){
				updateMandatoryFilled();
				ngModel.$setViewValue(element.val());
			}

			if (angular.isFunction(element.select2)) {
				element.css("opacity", 0);

				$timeout(function() {
					element.css("opacity", 1);
					element.select2({
						allowClear: false,
						width: '100%'
					});

					// Set the select2 value when the viewValue changes
					ngModel.$render = function(){
						element.select2('val', ngModel.$viewValue);
						element.val(ngModel.$viewValue);
					};

					// Enable up or down key functionality to open the select
					element.parent().find(".select2-focusser").on("keydown", function(e) {
						if ((e.which === 40 || e.which === 38))
							element.select2('open');
					});
				// Hard-setting an aria-label on the focused input, so both the question name and selected option can be announced by screen reader
				var select2Focusser = $(element).parent().find('.select2-focusser');
				select2Focusser.removeAttr('aria-labelledby');
				select2Focusser.attr('aria-label', getAriaLabel());
				select2Focusser.attr("aria-describedby", "details_" + scope.field.sys_id);
				select2Focusser.addClass("autofocus-class")
				});

				element.on('change', function() {
				// Hard-setting an aria-label on the focused input, so both the question name and selected option can be announced by screen reader
				// Update aria-label ASAP (out of $timeout) - JAWS purpose. DEF0193081
				setAttrsForSelect2Focusser();
					scope.$evalAsync(setModelValue);
				});

				// If the choices change, set the selected value.
				scope.$watch('snChoices', function(newValue, oldValue){
					if (angular.isDefined(newValue) && newValue != oldValue) {
				// Hard-setting an aria-label on the focused input, so both the question name and selected option can be announced by screen reader
				// Update aria-label ASAP (out of $timeout) - JAWS purpose. DEF0193081
                   setAttrsForSelect2Focusser()				
						$timeout(function(){
							setModelValue();
						});
					}
				}, true);
			}

			function getAriaLabel() {
				var label = scope.c.getAriaLabel(field);
				if(field.value) { // "-- choose --" has no value
					label += " " + scope.selectedlabel
				}
				return label;
			}

			function setAttrsForSelect2Focusser() {
				var select2Focusser = element.parent().find(".select2-focusser")
				select2Focusser.attr("aria-label", getAriaLabel());
				select2Focusser.attr("aria-describedby", "details_" + scope.field.sys_id);
			}

			function updateMandatoryFilled() {
				// if is not mandatory question or field has value, update isMandatoryFilled to true
				field.isMandatoryFilled = !field.mandatory || field.value;
				var select2Focusser = element.parent().find(".select2-focusser")
				if(field.isMandatoryFilled){
					select2Focusser.removeClass("autofocus-error")
				}else{
					select2Focusser.addClass("autofocus-error")
				}
			}

		}
	};
}

function (i18n) {
		var inner = '<div class="wrapper-xs alert alert-danger" ng-if="field.invalidValue">' +
						'${Value is invalid or out of range}' +
					'</div>' +
					'<div class="wrapper-xs" ng-if="field.isConverted">' +
						'${The input value has been adjusted to an integer}' +
					'</div>' +
					'<span class="hidden-xs">' +
						'<input class="form-control" aria-label="{{::field.label}}" ng-model="field.value" ng-change="validate()" style="width:75px;margin-right:5px;padding:3px 6px;display:inline-block;" minvalue="{{::field.minValue}}" maxvalue="{{::field.maxValue}}"/>' +
						'<span ng-if="showPercentage || field.type == \'percentage\'">%</span>' +
						'<span class="wrapper-sm">${Range}: {{::field.minValue}} - {{::field.maxValue}}</span>' +
					'</span>' +
					'<span class="visible-xs">' +
						'<input class="form-control question-number-field-border" aria-label="{{::field.label}}" ng-model="field.value" ng-change="validate()" style="width:149px;margin-right:5px;padding:3px 6px;display:inline-block;margin-left:2px;height:40.6px;border-radius:5px;" minvalue="{{::field.minValue}}" maxvalue="{{::field.maxValue}}" placeholder="${Range} : {{::field.minValue}} - {{::field.maxValue}} {{::showPercentage || field.type == \'percentage\' ? \'%\' : \'\' }}" />' +
					'</span>';
		return {
			template: '<div>' +
						'<div class="hidden-xs">' + inner + '</div>' +
						'<div class="visible-xs">' + inner + '</div>' +
						'<sp-survey-add-info ng-if="field.allow_add_info" field="field"></sp-survey-add-info>' +
					  '</div>',

			scope: {
					'field': '=',
					'showPercentage': '='
			},

			controller: function($scope) {
			 	$scope.validate = function() {
			 		var field = $scope.field;
					if ($scope.isFloat(field.value) === true && field.value >= 0) {
						field.value = Math.floor(field.value);
						field.isConverted = true;
					} else
						field.isConverted = false;
					if (field.value == '') {
						field.invalidValue = false;
						return;
					}
					if (isNaN(field.value)) {
						field.invalidValue = true;
						return;
					}
					var value = parseFloat(field.value);
					var maxValue = parseInt(field.maxValue);
					var minValue = parseInt(field.minValue);
					// Set this variable if the entered value is out of either max or min value
					field.invalidValue = (!isNaN(maxValue) && value > maxValue) || (!isNaN(minValue) && value < minValue);
			 	};
				$scope.isFloat = function(n) {
					return n % 1 !== 0;
				};
			}
		};
}

function () {
	return {
		restrict: 'E',
		replace: true,
		scope: {
			icon: "@",
			fname: "@",
			ext: "@"
		},
		template: "<svg><use xlink:href='{{iconName}}'></use></svg>",
		link: function(scope){
			//File icon mapping
			var fileIcons = {
				xls: 'cm-icon-xls',
				xlsx: 'cm-icon-xls',
				doc: 'cm-icon-doc',
				docx: 'cm-icon-doc',
				pdf: 'cm-icon-pdf',
				jpg: 'cm-icon-jpg',
				jpeg: 'cm-icon-jpg',
				png: 'cm-icon-png',
				bmp: 'cm-icon-bmp',
				gif: 'cm-icon-gif',
				zip: 'cm-icon-zip',
				txt: 'cm-icon-txt',
				rtf: 'cm-icon-txt',
				ppt: 'cm-icon-ppt',
				pptx: 'cm-icon-ppt',
				err: 'cm-icon-err',
				def: 'cm-icon-doc'
			};
			scope.$watch('fname', function(){
				if(scope.fname){
					var ext = scope.fname.substr((~-scope.fname.lastIndexOf(".") >>> 0) + 2);
					if(ext)
						ext = ext.toLowerCase();
					scope.iconName = '#' + (fileIcons[ext] ? fileIcons[ext] : fileIcons.def);
				}
			});
			scope.$watch('ext', function(){
				if(scope.ext){
					scope.iconName = '#' + (fileIcons[scope.ext] ? fileIcons[scope.ext] : fileIcons.def);	
				}
			});
			scope.$watch('icon', function(){
				if(scope.icon){
					scope.iconName = '#' + scope.icon;
				}
			});
		}
	};
}
