body {
  color: $color-darkest;
}

.case-title {
	font-size: 1.5rem;
}

.sp-attachment-add {
	font-size: small;
}

// Task or Case Id Number
.task-id, .case-id {
  color: $color-darkest;
}

.status {
  color: $color-dark;
  font-size: small;
}
.date {
  color: $color-light;
  font-size: small;
}

.toggle {
  color: $color-darker;
  font-size: small;
}

// Add additional padding to Kb and Suggested Reading links
.a-list {
	padding: 0 0 0.8rem 0;
    display: block;
}

.tooltips {
     white-space: nowrap;
}

// Define Status Indicators
.overdue {
	color: $overdue;
}
.due-today {
	color: $due-today;
}
.due-later {
	color: $due-later;
}
.in-progress {
	color: $in-progress;
}
.complete {
	color: $complete;
}
.danger, .error {
	color: $danger;
}
.faded {
	color: $color-lighter;
}
.success {
	color: $success;
}
.warning {
	color: $warning;
}

// Show focus rings for Accessibility on button roled tags like divs and spans
[role="button"]:focus {
    outline: -webkit-focus-ring-color auto 0.5rem;
}

.disable-a {
   pointer-events: none;
   cursor: default;
}

a.text-a:hover {
color:$primary;
}

#uiNotificationContainer .alert-success {
    background-color: #dff0d8;
    border-color: #006100;
    color: 	#006100;
}

#uiNotificationContainer .alert-danger {
    background-color: #f2dede;
    border-color: #9D0010;
    color: 	#9D0010;
}

.list-group a.list-group-item {
    min-width: 32.5%;
}

// Define Banner Header
.banner-img {
  min-height: 55rem;
  background-size: cover;
  margin: -1.5rem -1.5rem 3rem -1.5rem;
  background-color: #455464;
  background-image: linear-gradient(30deg, $color-darkest, $color-darker 50%);
  background-image: url('/banner-img.jpg');
}

.wrapper-xl {
  color: $color-lightest;
  margin: 0 auto;
  h1 {
     padding: 15% 0 0 0;
  }
}

// Define Panel Background
.panel {
	border: 0.1rem solid $border;
 	box-shadow: 0 0.1rem 0.1rem darken($border, 5%);
    color: $color-darkest;
}
}
.b {
	border-color: $border;
	box-shadow: 0 0.1rem 0.1rem darken($border, 5%);
}
body .panel-default > .panel-heading, div#widget-tabs-2 > .panel-heading {
	background: $color-lightest;
}

// Fix Rollup Filter Title
.rollup-title {
	line-height: 1.8;
}

.header-label {
  color: $color-darkest;
  font-weight: bold;
  font-size: 1.2rem;
}

.formatDesc {
  background-color:inherit;
  border:0rem;
  color: $color-darker;
  display:block;
  font-family: inherit;
  font-size: inherit;
  padding: 0rem;
  white-space: pre-wrap;
}

/* Ticket page task line item */
.task-activity {
	padding: 1.5rem;

  .task-tagline {
    text-align: left;
    color: $color-dark;
  }

  .task-link {
    font-size:large;
  }

  .skip {
  	margin-top: 1rem;
    display: block;
  }
}

.hr-task-action {
  .task-action-caption {
  	font-size: 1.8rem;
	margin: 0;
  }

  &.task-completed-small {
    text-align: center; 
    padding: 1rem 0;
    background-color: $body-bg;
  }

  &.task-completed {
    text-align: center; 
    padding: 30% 0;
    background-color: $body-bg;
  }
}

.task-complete-footer {
  padding: 1.5rem 0 0 0;
  text-align: right;
}

/*  Document PopOver for the ticket page */
.document-acknowledge-task {
  #close-popup-button {
    vertical-align: top;
  }

  .ie-popover {
    float:right; 
    font-size:large; 
    margin:1rem;
  }

  iframe {
    height:70rem;
    width:100%;
    display: block;
  }

  .password-input {
    display:inline-block; 
    height:3.5rem; 
    margin-right:2rem; 
    width:12rem;
  }

  .pop-footer {
    background-color: $body-bg;
    padding: 1.5rem; 
    text-align: right; 
  }

  #docPop {
    position: fixed;
    left: 0;
    top: 0;
    right: 0;
    bottom: 0;
    z-index: 200;
    background-color: $color-light;
  }

  #docPop .inner {
    position: absolute;
    background-color: $body-bg;
    color: $color-light;
    top: 5.5rem;
    left: 0rem;
    padding: 0;
    right: 0;
    bottom: 1rem;
    box-shadow: 0 0 1.5rem $color-dark;
    border-radius: 0.3rem;

    object {
      width: 100%;
      height: 82%;
      background-color: $color-lightest;
      border: none;
    }
  }

  docPop.@media (max-width: 768px) {
    .pdf-link {
      display: block;
      margin: 3rem 0 0;
      font-size: 1.8rem;
      text-align: center;
    }

    #agree-button {
      margin-top: 2rem; 
      vertical-align: top;
    }

    #docPop .inner.task_link {
      top: 5rem;
    }

    #task_link {
       padding: 2rem;
       margin-bottom: 2rem;
    }  
  }

  #updating {
    position: absolute;
    z-index: 130;
    background-color: $body-bg;
    border: 0.1rem solid $border;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    text-align: center;
    padding: 30%;
  }

  .animated {
    -webkit-transition: all linear 0.5s;
    transition: all linear 0.5s;
  }

  .animated.ng-hide {
    opacity: 0;
  }

  div.pop-header {
    padding: 1rem;
    text-align: right;
    background-color: white;
    border-bottom: 0.1rem solid $color-lighter;
    border-radius: 0.3rem;
  }

  .close-cross {
    font-size: 4.4rem;
    line-height: 1.8rem;
    opacity: 0.4;
    filter: alpha(opacity = 20);
    font-weight: 200;
  }

  .close-cross:hover {
    color: $color-darkest;
    opacity: 0.7;
    cursor: pointer;
  }

  .label {
    font: inherit;
    color: $color-dark;
	}

  /* Signature Pad CSS for signing and acknowledging document on ticket page */

  .sigPad {
    margin: 0;
    padding: 0;
    margin: auto;
    width: 58rem;
    max-width: 100%;
  }

  .pad {
    cursor: url("/images/pen.cur"), crosshair;
    cursor: url("/images/pen.cur") 16 16, crosshair;
    width: 58rem;
    height: 20rem;
    max-width: 100%;
    border-radius: 0.4rem;
    border:0.1rem solid darken($border, 10%);

    -ms-touch-action: none;
  	touch-action: none;
  	-webkit-user-select: none;
  	-moz-user-select: none;
  	-ms-user-select: none;
  	-o-user-select: none;
  	user-select: none;
  }

  .sig {
    display: block;
  }

  .sigWrapper.current {
    border-color: $border;
    background-color: $color-lightest;
    height: 20rem;
    border-radius: 0.4rem;
  }

  .disclaimer {
    color: $color-dark;
    font-weight: 200;
    font-size: 1.2rem;
    text-align: center;
    margin: 0.3rem 0 1rem 0;
    padding: 0 5% 0 5%;
    text-shadow: 0.1rem 0.1rem 0.1rem $color-lightest;
  }

  .signFooter {
    height:40rem;
  }

  /* Type name for accessibility */
  .typeItDesc {
    text-align: center;

      .typedCanvas {
      	display: none;
      }

      .signed_name {
        font-family: cursive;
        color: $color-darkest;
        font-size: 2.4rem;
        border-radius: 0.2rem;
        border: 0.1rem solid darken($border, 10%);

        @media (max-width: 768px) {
          width: 100%;
    		}
      }
  }
}
/*--------- end document preview and signature ---------------*/


/*-------- Begin HR Badge CSS --------------*/

.select2-drop.with-avatar .select2-result-label {
    padding: 1rem 1.5rem;
}

.select2-drop .avatar-container .avatar.soloAvatar .sub-avatar, .select2-container .avatar-container .avatar.soloAvatar .sub-avatar {
  border: 0rem; 
  line-height: 2.8rem;
}

.select2-drop .avatar-container {
  height: 2.8rem;
  width: 2.8rem;
  margin-right: 1rem;
}

.avatar-small-medium.profile-badge {
  .sub-avatar {
    font-weight: bold; 
  	color: $color-darkest;
    background-color: $color-lightest;
  }
  .sub-avatar:not([style]) {
    border: 0.1rem solid #ddd; 
	border-radius: 50%;
  }
}

