/*-------- BEGIN Org Chart select2 avatar CSS --------------*/

.using-hrj-theme {
	display: none;
}

.orgchart-search-select2 .avatar {
    background-size: cover;
    background-color: $color-lightest;
    border-radius: 10rem;
    border: 0.1rem solid $color-light;
    color: $color-darkest;
    display: inline-block;
    font-size: 1rem;
    font-weight: bold;
    margin-right: 0.5rem;
    padding-top: 0.3rem;
    text-align: center;
    width: 2rem;
    height: 2rem;
    position: relative;
    top: -0.1rem;
}
.orgchart-search-select2 span {
  width: 36rem;
  @media screen and (max-width: 420px) {
  	width: 100%;
  }    
}
.orgchart-search-select2 span,
.orgchart-search-select2 .title {
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    display: inline-block;
    margin-bottom: -0.6rem;
}
.orgchart-search-select2 .title {
    display: inline-block;
    color: $color-dark;
    max-width: 33%;
    margin-top: 0.3rem;
    @media screen and (max-width: 420px) {
    	max-width: 45%;
    }     
}
.orgchart-search-select2 .name {
    max-width: 50%;
}
.orgchart-search-select2 .select2-highlighted .title {
    color: $color-lightest;
}
