 $carousel-indicator-active-bg: $brand-primary; 
 
 //== Code
 //
 $code-color: #cd293c; //--now-color_alert--critical-3
 $code-bg: $background-tertiary; 
 
 $kbd-color:
			$text-white; 
 $kbd-bg: $gray-dark;
 
 $pre-bg: $background-secondary; 
 $pre-color: $gray-dark;
 $pre-border-color: $border-tertiary; 
 
 //== Type
 //
 $abbr-border-color: $border-tertiary;
 $headings-small-color: $text-muted;
 $blockquote-small-color: $text-muted;
 $blockquote-border-color: $border-tertiary;
 $page-header-border-color: $border-tertiary;
 $hr-border: $border-tertiary;
 
 $sp-b-border-color: $border;
 $panel-default-border: $border;
 
 //sc help icon color
 $sc-field-error-color: $text-secondary;
 
 //define La Jolla variable that is used for KB Star colors
 $fav-star-color: $brand-warning;
 $fav-star-color-off: #ffffff;
 $fav-star-outline-color: darken($brand-warning, 30%);
 $fav-star-outline: -1px 0 $fav-star-outline-color, 0 1px $fav-star-outline-color, 1px 0$fav-star-outline-color, 0 -1px $fav-star-outline-color;
 
 
 //==AI Search
 $now-sp-font-family-sans-serif: 'Lato', sans-serif;
 $now-sp-tabs--selected--color: $select-primary;
 $now-sp-tabs--color--hover: $select-primary;
 $now-sp-tabs--border-color: $sp-b-border-color;
 $now-sp-tabs--selected--background-color: $select-primary;
 
 //==Chat
 $sp-agent-chat-bg: $brand-primary;
 
//==Standard ticket flex item
 $flex-paperclip-margin-left: 15px;
 
 $line-separator-color: #DEDEDE;
 
 //==Record Producer
 $rp-default-bg: $background-primary;
 
 //==Custom widget
 $label-success-background: #D7EBE5;
 
 //Info message color
 $alert-info-message-bg: #BDDCF2;
 $alert-info-message-border: #5EABDF;
 
 //==Form Label Background Color 
 $form-label-bg-color: #BDDCF2; 
 //given by UX
 
 $kb-tile-border: rgba(23,40,52,0.08); 
 
 //Buttons beside form text box
 $form-textbox-button-height:36px; 
 
 $secondary-button-hover-border-color:#353780; 
 $secondary-button-active-border-color:#1C1D42; 
 
 $navbar-secondary-color:#B0B5BF;
 
 //NASS variable
 $now-sp-nass-color-primary-0: #8789D2!default;
 $now-sp-nass-color-primary-2: #181A1F!default;

$brand-primary:			#0574DE !default;
$brand-success:         #0a5d0c !default;
$brand-info:            #0F7D8B !default;
$brand-warning:         #9E6817 !default;
$brand-danger:          #B12F2C !default;
$state-danger-text:     #64201a;

$accessible-placeholder-gray:       #565656;

$primary:				$brand-primary !default;

$sp-logo-margin-x: 		15px !default;
$sp-tagline-color:		$text-color !default;
$navbar-inverse-bg: 	#22273C !default;
$navbar-inverse-link-color: #bdc0c4 !default;
$navbar-inverse-link-hover-color: #ffffff !default;
$navbar-inverse-link-hover-bg: $brand-primary !default;
$navbar-inverse-link-active-bg: $brand-primary !default;
$body-bg: 				#fff !default;
$dropdown-link-disabled-color: #ccc;
$sp-disabled-breadcrumb: #1a1a1a;
$sp-file-drop-zone-color: #666;

$no-issue-border-color: $brand-success;
$planned-maintenance-border-color: $brand-info;
$service-degredation-border-color: $brand-warning;
$service-outage-border-color: $brand-danger;

//** Global textual link color.
$link-color:            #045AAE !default;
//** Link hover color set via `darken()` function.
$link-hover-color:      darken($link-color, 15%) !default;
//** Link hover decoration.
$link-hover-decoration: underline !default;

//** Panel styles
$panel-primary-heading-bg: #CCE5FF !default;
$panel-primary-border: #CCE5FF !default;
$panel-primary-text: #2E2E2E !default;

$text-muted:			#646567 !default;
$sp-agent-chat-bg: #7070B3;

.label {
	font-weight: normal !important;
    font-size: 12px !important;
}

/* alert overrides */
.alert-success {
    color: $brand-success !important;
}
.alert-info {
    color: $brand-info !important;
}
.alert-warning {
    color: $brand-warning !important;
}
.alert-danger {
    color: $brand-danger !important;
}

[accessibility] {
	a {  
      &:hover {
          text-decoration: underline !important;
      }
      &:focus {  		
          text-decoration: underline !important;
      }
	}
    
    .sp-announcement-root {
    	a {
        	text-decoration: underline !important;
          }
     }
           
    [uib-rating] {
    	.glyphicon-star {
        	color: black !important;
        }
    }
    
    input::-webkit-input-placeholder {
		color: $accessible-placeholder-gray !important;
	}
	input::-moz-placeholder {
    	color: $accessible-placeholder-gray !important;
	}
	input:-ms-input-placeholder {
		color: $accessible-placeholder-gray !important;
	}
}

button:focus {
     outline:none; 
     border:1px solid #4D90FE;
     -webkit-box-shadow: 0px 0px 5px  #4D90FE;
     box-shadow: 0px 0px 5px  #4D90FE;
}

[tabindex]:focus {
	outline:none; 
	border:1px solid #4D90FE;
	-webkit-box-shadow: 0px 0px 5px  #4D90FE;
	box-shadow: 0px 0px 5px  #4D90FE;
}


.navbar-nav {
	.open {
    	a {
        	.label {
            	background-color: #F5F5F5;
                color: $brand-primary;
            }
        }
    }
    
    li {
    	a:hover,
	a:focus {
          .label {
          	background-color: #F5F5F5;
            color: $brand-primary;
          }
        }
    }
}

.panel-primary {
	.label {
    	background-color: #F5F5F5;
        border: 1px solid #dcdcdc;
        color: #2E2E2E;
    }
}

.breadcrumbs-container {
    background-color: #f0f3f4 !important;
}

.timeline-badge {
	&.success {
		background-color: $brand-success !important;
    }
	.avatar-large {
		background-color: $accessible-placeholder-gray !important;
    }
}

.select2-results {
	.select2-highlighted {
    	background: $brand-primary !important;
    }
}

.CodeMirror-linenumber {
	color: #4c4c4c !important;
}

.help-block {
	color: #545454 !important;
}

.selected {
	background-color: #707070;
}

@media (max-width: 767px) {
  .navbar-inverse .navbar-nav .open .dropdown-menu > li > a {
  	.text-muted {
		color: $navbar-inverse-link-color !important;    
    }
  }
}

//** Panel footer is a light gray background, so text color should be dark.
.panel-footer {
	color: #515151 !important;
}
