result.message_receive_advanced_authorization:
function /*boolean*/ authorizeReceive(/*String*/ channel, /*UnmodifiableMap<String, Object>*/ message, /*String*/ sender, /*String*/ recipient) {
 	var target = message.get("group") || message.get("event_target");
 	var groupGR = new GlideRecord("live_group_profile");

 	// Not group related
 	if (!target || !groupGR.get(target))
 		return true;

 	// We already know that a user is a member of the group if we've gotten here.  No reason for further
 	// checks if it's not a document group.
 	if(!groupGR.document_group)
 		return true;

 	var reflectedField = message.get("reflected_field");
 	if (reflectedField) {
        var userID = GlideSession.get().getUID();
 		if (new SNC.LiveFeedSecurityManager().canReadField(userID, groupGR.table, groupGR.document, reflectedField))
 		   return true;

        // Checks if we are in the midst of a Connect Support transfer, the accepting agent
        // may not have access to the record until after the transfer is complete.
        var transferGR = new GlideRecord("chat_queue_entry_transfer");
        transferGR.addQuery("group", target);
        transferGR.addQuery("state", "pending");
        transferGR.addQuery("transfer_to", userID);
        transferGR.query();
        return transferGR.next();
 	}

	return new SNC.LiveFeedSecurityManager().canReadDocument(groupGR);
}

result.channel_subscribe_advanced_authorization:
function /*boolean*/ authorizeSubscribe(/*String*/ channel, /*String*/ user) {
	if(channel === "/connect/support/queues")
		return true; // Handled by separate processor

	var channelIDMatch = channel.match(/\/connect.*\/(\w{32})/);
	if(!channelIDMatch)
		return false;

	if (gs.getUserID() === channelIDMatch[1] || gs.getSessionID() === channelIDMatch[1]) {
		return true;
	}

	// /connect/support/conversation/{group_id}
	// user must be a member
	var liveProfileGR = new GlideRecord("live_profile");
	liveProfileGR.addQuery("table", "sys_user");
	liveProfileGR.addQuery("document", gs.getUserID());
	liveProfileGR.query();
	if (liveProfileGR.next()) {
		var liveGroupMemberGR = new GlideRecord("live_group_member");
		liveGroupMemberGR.addQuery("group", channelIDMatch[1]);
		liveGroupMemberGR.addQuery("member", liveProfileGR.getUniqueValue());
		liveGroupMemberGR.query();
		if (liveGroupMemberGR.next())
			return true;
	}

	return false;
}

result.message_send_advanced_authorization:
function /*boolean*/ authorizeSend(/*String*/ channel, /*Map<String, Object>*/ message, /*String*/ sender) {
	// Don't allow messages to be published from the client.  Connect doesn't ship with any client-side publishing so force it off rather than trying to evaluate seccurity rules for every message someone might try to publish.  Server-generated messages never have a page name starting with amb/connect.  If the session is not interactive then we assume it's server-sent.
	return !gs.isInteractive() || !GlideTransaction.get() || GlideTransaction.get().getPageName().indexOf("amb/connect") != 0;
}

