result.condition_script:
(function executeCondition( /* task glide record */ current) {
    switch (current.getTableName()) {
        case 'incident':
        case 'sc_request':
        case 'sc_req_item':
        case 'change_request':
        case 'interaction':
            return gs.getProperty('sn_sow_collab.enable_requester_in_sidebar_discussion', 'true') == 'false';
        default:
            return false;
    }
})(current);

result.user_query_script:
(function executeCondition(userGr, taskGr) {
	var roleSQ = userGr.addJoinQuery("sys_user_has_role", "sys_id", "user");
    roleSQ.addCondition("role.name", "!=", "snc_internal"); // Exclude users with no role or only snc_internal role 
})(userGr, taskGr);

