}

type KMFRootComponent {
	KEY_SECURE: KMFComponent!
	FILE_KEY_STORE: KMFComponent!
	GLIDE_ENCRYPTER: KMFComponent!
	IKEK: KMFComponent!
	IHMAC: KMFComponent!
