{"body":{"scriptActive":true,"script":"/*
**Access Flow/Action data using the fd_data object. Script must return a value.
**example: var shortDesc = fd_data.trigger.current.short_description;
**return shortDesc;
*/
var requestBodyData = [];
for (var role in fd_data.action_inputs.add_role) {

    var requestBodyPayload = {
        "id": role, "exclude_response_headers": true,
        "headers": [
            { "name": "Content-Type", "value": "application/json" },
            { "name": "Accept", "value": "application/json" }
        ],
    };

    var record = {};
    if (fd_data.action_inputs.is_group) {
        record["group"] = fd_data.action_inputs.group_id;
        record["role"] = fd_data.action_inputs.add_role[role];
    } else {
        record["user"] = fd_data.action_inputs.user_id;
        record["role"] = fd_data.action_inputs.add_role[role];
    }
    requestBodyPayload["url"]= "/api/now/table/"+(fd_data.action_inputs.is_group ?"sys_group_has_role" : "sys_user_has_role"), 
    requestBodyPayload["method"]= "POST";
    requestBodyPayload["body"]= gs.base64Encode(JSON.stringify(record));

    requestBodyData.push(requestBodyPayload);
}


var requestBody = {
    "batch_request_id": "1", 
    "enforce_order": false, 
    "rest_requests": requestBodyData
};

//return JSON.stringify(requestBody); 
return requestBody;"}}

