{"output":{"scriptActive":true,"script":"
var output = {};

var result = JSON.parse(fd_data._1__rest_step.response_body);

// process output

if (result.hasOwnProperty("adult")) {
   output["prohibited"] = result["adult"]["isAdultContent"] || result["adult"]["isRacyContent"] || result["adult"]["isGoryContent"];
} else {
   output["prohibited"] = false;
}

var tags = [];
if (result.hasOwnProperty("tags")) {
   var result_tags = result["tags"];

   for (var idx in result_tags) {
      tags.push(result_tags[idx]["name"]);
   }
}

output["tags"] = tags;

var captions = [];

if (result.hasOwnProperty("description")) {
   var desc = result["description"]["captions"];  

   for (var idx in desc) {
      captions.push(desc[idx]["text"]);
   }
}
output["captions"] = captions;

return output;


"}}

