{"error_type":{"scriptActive":true,"script":"var errorType = '';

if (!fd_data.flow_var.has_any_errors) {
    return errorType
}

var hasInstanceScanError = false;
var hasATFScanError = false;
var instanceScanResults = fd_data.flow_var.instance_scan_results;
var atfTestResults = fd_data.flow_var.atf_test_results;

if (instanceScanResults) {
    Object.keys(instanceScanResults).forEach(function(instanceScanSuiteId) {
        if (instanceScanResults[instanceScanSuiteId]) {
            hasInstanceScanError = hasInstanceScanError || instanceScanResults[instanceScanSuiteId].scan_has_error;
        }
    });
}

if (atfTestResults) {
    Object.keys(atfTestResults).forEach(function(atfScanSuiteId) {
        if (atfTestResults[atfScanSuiteId]) {
            hasATFScanError = hasATFScanError || atfTestResults[atfScanSuiteId].test_suite_has_execute_error;
        }
    });
}

if (hasATFScanError) {
    errorType = 'test_fail';
} else if (hasInstanceScanError) {
    errorType = 'other';
} else {
    errorType = 'deploy_fail'
}

return errorType;"}}

