{"output":{"scriptActive":true,"script":"
var output = {};

try {
    var response = JSON.parse(fd_data._1__rest_step.response_body);
    if (response['errors'] != null && response['errors'].length > 0) {
        output['isSensitive'] = false;
        output['anonymizedText'] = "";
        output['status'] = "error";
        output['message'] = response['errors'][0]['error']['message'];
    } else {
        output['status'] = "success";
        var document = response['documents'][0];
        if (document['entities'] == null || document['entities'].length == 0) {
            output['isSensitive'] = false;
            output['anonymizedText'] = "";
        } else {
            output['isSensitive'] = true;
            output['anonymizedText'] = document["redactedText"];
        }
    }
} catch(e) {
    output['isSensitive'] = false;
    output['anonymizedText'] = "";
    output['status'] = "error";
    output['message'] = 'Unknown error ' + e;
}



return output;
"}}

