{"approvers":{"scriptActive":true,"script":"return getApproversForRole(fd_data.trigger.current.role.name);

function getApproversForRole(role) {
	var roleSysId = getRoleSysId(role);
	if (!roleSysId)
		return null;

	var memberGr = getApproverGroupMemberGr();
	if (!memberGr)
		return null;

	var userSysIds = [];
	var userSysId;
	var aclMgr = new SNC.ScopedReportViewACLManager();
	while (memberGr.next()) {
		userSysId = memberGr.user.sys_id;
		if (aclMgr.isUserAllowedToGrantRole(userSysId, roleSysId))
			userSysIds.push(memberGr.user.sys_id);
	}

	if (userSysIds.length === 0)
		return null;

	var userGr = new GlideRecord('sys_user');
	userGr.addQuery('sys_id', 'IN', userSysIds);
	userGr.query();

	return userGr.getRowCount() > 0 ? userGr : null;
}

function getApproverGroupMemberGr() {
	var memberGr;

	//Default group
	var defaultGroup = '58851c2353322010a480ddeeff7b12b0';
	if (GlideGroup.get(defaultGroup).isValid())
		memberGr = GlideUserGroup.getMembers(defaultGroup);

	if (memberGr && memberGr.getRowCount() > 0)
		return memberGr;

	//Group defined by property
	var propGroup = gs.getProperty('glide.report.request_approver_group');
	if (!!propGroup && GlideGroup.get(propGroup).isValid())
		memberGr = GlideUserGroup.getMembers(propGroup);

	if (memberGr && memberGr.getRowCount() > 0)
		return memberGr;

	return null;
}

function getRoleSysId(role) {
	var gr = new GlideRecord('sys_user_role');
	gr.get('name', role);

	return gr.sys_id;
}
"}}

