{"timeout_duration":{"scriptActive":true,"script":"var seconds = fd_data.subflow_inputs.timeout;

var date = new Date(1000 * seconds);

var year = date.getUTCFullYear();
var month = pad(date.getUTCMonth() + 1);
var dayOfMonth = pad(date.getUTCDate());

var hour = pad(date.getUTCHours());
var minute = pad(date.getUTCMinutes());
var second = pad(date.getUTCSeconds());

var timeout = year + '-' + month + '-' + dayOfMonth + ' ' + hour + ':' + minute + ':' + second;

return timeout;

function pad(value) {
  return (value < 10) ? ('0' + value) : value;
}"}}

