var fTargetField = 'trigger';
var fTargetTable = 'ais_rule';

var fReplacements = {
    'domain=': 'domains_id=',
    'location=': 'location_id=',
    'roles=': 'roles_id='
};

var keys = Object.keys(fReplacements);


// Find rules using old condition values
var gr = new GlideRecord(fTargetTable);
var grOr = gr.addORQuery(fTargetField, "CONTAINS", keys[0]);
for (var i = 1; i < keys.length; i++)
    grOr.addORQuery(fTargetField, "CONTAINS", keys[i]);
gr.query();

var profileIdsToPublish = [];
var profileNamesToPublish = [];

while (gr.next()) {
    // Find already published profiles to republish later
    var profileId = gr.getValue('profile');
    if (!profileIdsToPublish.includes(profileId)) {
        var profile = new GlideRecord('ais_search_profile');
        profile.get(profileId);
        if (profile.getValue('state') == "PUBLISHED")
            profileNamesToPublish.push(profile.getValue('name'));
    }

    // Replace old condition values to new
    var newCondition = gr.getValue(fTargetField);
    for (var i = 0; i < keys.length; i++)
        newCondition = newCondition.replace(keys[i], fReplacements[keys[i]]);
    gr.setValue(fTargetField, newCondition);
    gr.update();
}

// Republish already published profiles
if (profileNamesToPublish.length > 0) {
    var synchronizer = new sn_ais.Synchronizer();
    for (var i = 0; i < profileNamesToPublish.length; i++)
        synchronizer.publishProfile(profileNamesToPublish[i])
}

(function() {
	var gr = new GlideRecord('sys_sg_item_view');
	gr.query();
	while (gr.next()) {
		var itemViewJson = gr.getValue('item_view_json');
		if (!itemViewJson)
			continue;

		try {
			var elementProcessor = new ItemViewElementProcessor();
			var item_view_json = elementProcessor.identifyItemViewElements(itemViewJson);
			gr.setValue('item_view_json', item_view_json);
			setDependencyFields(gr, item_view_json);
			var result = gr.update('Add identifiers to item view JSON');
			if (result)
				gs.warn(gs.getMessage('ItemView {0} updated successfully', gr.getUniqueValue()));
		} catch (err) {
			gs.warn(gs.getMessage('Error while processing ItemView {0}: {1}', [gr.getUniqueValue(), err.message]));
		}
	}

	gs.warn(gs.getMessage('Execution finished'));

	function setDependencyFields(glideRecord, item_view_json){
		elementList = elementProcessor.getItemViewElementsIds(item_view_json);
		var dependency_fields = [];
		for(var index=0;index<elementList.length;index++) {
			if(!elementList[index].cellId)
				continue;

			dependency_fields.push(elementList[index].cellId+"-"+elementList[index].id);
		}
		glideRecord.dependency_fields = dependency_fields.join(",");
	}	

})();

