function deactivate() {
    if (!isAPIPresent() || !isSystemReady())
        return;
    var denyACLIds = getDenyACLs();
    var batchId = gs.generateGUID();
    gs.info(
        'BatchId [' + batchId + ']. \n' +
        'Playbook Designer deactivate Deny Unless ACLs on upgrade. \n' +
        'Attempting to deactivate [' + denyACLIds.join(',') + '].'
    );
    var skippedIds = [];
