	docGr.addNullQuery('hint').addOrCondition('hint', '!=', newHint);
	docGr.query();
	while (docGr.next()) {
		docGr.hint = newHint;
		docGr.plural = newPlural;
		docGr.update();
		gs.log('Updated hint and plural of ' + fieldName + ' on ' + tableName, 'RenameLatenessToWaitTime');
