    if (sectionsList.length) {
        var tabWidgetMapGr = new GlideRecord("sn_employee_tab_widget_mapping");
        tabWidgetMapGr.addQuery("widget", "56fc512453333010fedfddeeff7b1273"); // Query for Employee Profile Overview widget only
