Provide a template with mapping of table fields to component properties and configuration for actions and static values <br/>
<b>Example: </b>
<pre>
{
	"component": "now-card-evam-record",
	"staticValues": {
		"highlightedHeaderIcon": {
			"translatable": false,
			"key": "clock-outline"
		},
		"highlightedHeaderBkgColor": {
			"translatable": false,
			"key": "moderate"
		},
		"subtitleIcon": {
			"translatable": false,
			"key": "calendar-fill"
		},
		"imageType": {
			"translatable": false,
			"key": "image"
		},
		"subtitleImageType": {
			"translatable": false,
			"key": "avatar"
		},
		"detailLabelOne": {
			"translatable": true,
			"key": "Email address"
		},
		"detailLabelTwo": {
			"translatable": true,
			"key": "Works for"
		},
		"detailLabelThree": {
			"translatable": true,
			"key": "Location/address"
		},
		"showCardShadow": {
			"translatable": false,
			"key": false
		}

	},
	"mappings": {
		"highlightedHeaderLabel": "title",
		"textHeaderLabel": "department.name",
		"titleLabel": "name",
		"imageURL": "photo",
		"subtitle": "user_name",
		"subtitleAvatarName": "name",
		"subtitleAvatarURL": "avatar",
		"detailValueOne": "email",
		"detailValueTwo": "company.name",
		"detailValueThree": "location.name"
	},
	"actionMappings": {
		"mainActions": ["activate_user", "call_number"],
		"clickAction": "navigation"
	}
}
</pre>

