				setDateTimeFormat: function(newFormat) {
					dateTimeFormat = newFormat;
				},

				setTimeFormat: function(newFormat) {
					timeFormat = newFormat;
				},

				getValue: function (o) {
					if (typeof o === 'object') {
						if ('value' in o)
