						return null;
					var m = /^([0-9]{4})-([0-9]{2})-([0-9]{2}) ([0-9]{2}):([0-9]{2}):([0-9]{2})$/.exec(val);
					if (!m) {
						$log.error('Unexpected. Date value format returned by REST is unrecognizable');
						return;
