						d = d.getFullYear() + '-' + getZeroPadded(d.getMonth() + 1) + '-' + getZeroPadded(d.getDate()) + ' ' + getZeroPadded(d.getHours()) + ':' + getZeroPadded(d.getMinutes()) + ':' + getZeroPadded(d.getSeconds());
						return moment.tz(d, "YYYY-MM-DD HH:mm:ss", timezone);
