					var c = [];
					var ca = 0;
					var cb = 0;
					var va;
					var vb;
					var aNotEmpty;
					var bNotEmpty;
					while (ca < a.length || cb < b.length) {
						aNotEmpty = ca < a.length;
						bNotEmpty = cb < b.length;
						var finalVal;
						if (aNotEmpty) {
							va = a[ca];
						}
						if (bNotEmpty) {
							vb = b[cb];
						}
						if (aNotEmpty && bNotEmpty) {
							if (comparator(va, vb) <= 0) {
								c.push(va);
								ca++;
							} else {
								c.push(vb);
								cb++;
							}
						} else if (aNotEmpty) {
							c.push(va);
							ca++;
						} else {
							c.push(vb);
							cb++;
						}
					}
					return c;
				}
			};
		}];
	})
