var g_first_day_of_week = parseInt(gs.getProperty('glide.ui.date_format.first_day_of_week', 1));
function formatDate(date, format) {
    var MONTH_NAMES = new Array('January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December', 'Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec');
    var DAY_NAMES = new Array('Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat');
    if (format.indexOf("z") > 0)
        return formatDateServer(date, format);

    format = format + "";
    var result = "";
    var i_format = 0;
    var c = "";
    var token = "";
    var y = date.getFullYear() + "";
    var M = date.getMonth() + 1;
    var d = date.getDate();
    var E = date.getDay();
    var H = date.getHours();
    var m = date.getMinutes();
    var s = date.getSeconds();
    var yyyy, yy, MMM, MM, dd, hh, h, mm, ss, ampm, HH, H, KK, K, kk, k;
    var value = new Object();

    value["M"] = M;
    value["MM"] = LZ(M);
    value["MMM"] = MONTH_NAMES[M + 11];
    value["NNN"] = MONTH_NAMES[M + 11];
    value["MMMM"] = MONTH_NAMES[M - 1];
    value["d"] = d;
    value["dd"] = LZ(d);
    value["DD"] = LZ(d);
    value["E"] = DAY_NAMES[E + 7];
    value["EE"] = DAY_NAMES[E];
    value["H"] = H;
    value["HH"] = LZ(H);

    if (format.indexOf('w') != -1) {
        var wk = getWeek(date);
        if (wk >= 52 && M == 1) {
            y = date.getFullYear();
            y--;
            y = y + "";
        }
        if (wk == 1 && M == 12) {
            y = date.getFullYear();
            y++;
            y = y + "";
        }
        value["w"] = wk;
        value["ww"] = LZ(wk);
    }
    var dayOfWeek = (7 + (E + 1) - (g_first_day_of_week - 1)) % 7;
    if (dayOfWeek == 0)
        dayOfWeek = 7;

    value["D"] = dayOfWeek;

    if (y.length < 4) {
        y = "" + (y - 0 + 1900);
    }
    value["y"] = "" + y;
    value["Y"] = "" + y;
    value["yyyy"] = y;
    value["YYYY"] = y;
    value["yy"] = y.substring(2, 4);
    value["YY"] = y.substring(2, 4);


    if (H == 0) {
        value["h"] = 12;
    } else if (H > 12) {
        value["h"] = H - 12;
    } else {
        value["h"] = H;
    }
    value["hh"] = LZ(value["h"]);

    if (H > 11) {
        value["K"] = H - 12;
    } else {

        value["K"] = H;
    }
    value["k"] = H + 1;
    value["KK"] = LZ(value["K"]);
    value["kk"] = LZ(value["k"]);

    if (H > 11) {
        value["a"] = "PM";
    } else {
        value["a"] = "AM";
    }
    value["m"] = m;
    value["mm"] = LZ(m);
    value["s"] = s;
    value["S"] = s;
    value["ss"] = LZ(s);
    value["SS"] = LZ(s);

    while (i_format < format.length) {
        c = format.charAt(i_format);
        token = "";
        while ((format.charAt(i_format) == c) && (i_format < format.length)) {
            token += format.charAt(i_format++);
        }
        if (value[token] != null) {
            result = result + value[token];
        } else {
            result = result + token;
        }
    }
    return result;
}

function getDateFromFormat(val, format) {
    var MONTH_NAMES = new Array('January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December', 'Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec');
    var DAY_NAMES = new Array('Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat');
    val = val + "";
    format = format + "";
    var i_val = 0;
    var i_format = 0;
    var c = "";
    var token = "";
    var token2 = "";
    var x, y;
    var now = new Date();
    var year = now.getYear();
    var month = now.getMonth() + 1;
    var date = 0;
    var hh = now.getHours();
    var mm = now.getMinutes();
    var ss = now.getSeconds();
    var ampm = "";
    var week = false;
    while (i_format < format.length) {
        c = format.charAt(i_format);
        token = "";
        while ((format.charAt(i_format) == c) && (i_format < format.length)) {
            token += format.charAt(i_format++);
        }
        if (token == "yyyy" || token == "yy" || token == "y" || token == "YYYY" || token == "YY" || token == "Y") {
            if (token == "yyyy" || token == "YYYY") {
                x = 4;
                y = 4;
            }
            if (token == "yy" || token == "YY") {
                x = 2;
                y = 2;
            }
            if (token == "y" || token == "Y") {
                x = 2;
                y = 4;
            }
            year = _getInt(val, i_val, x, y);
            if (year == null) {
                return 0;
            }
            i_val += year.length;
            if (year.length == 2) {
                if (year > 70) {
                    year = 1900 + (year - 0);
                } else {
                    year = 2000 + (year - 0);
                }
            }
        } else if (token == "MMM" || token == "NNN") {
            month = 0;
            for (var i = 0; i < MONTH_NAMES.length; i++) {
                var month_name = MONTH_NAMES[i];
                if (val.substring(i_val, i_val + month_name.length).toLowerCase() == month_name.toLowerCase()) {
                    if (token == "MMM" || (token == "NNN" && i > 11)) {
                        month = i + 1;
                        if (month > 12) {
                            month -= 12;
                        }
                        i_val += month_name.length;
                        break;
                    }
                }
            }
            if ((month < 1) || (month > 12)) {
                return 0;
            }
        } else if (token == "EE" || token == "E") {
            for (var i = 0; i < DAY_NAMES.length; i++) {
                var day_name = DAY_NAMES[i];
                if (val.substring(i_val, i_val + day_name.length).toLowerCase() == day_name.toLowerCase()) {
                    if (week) {
                        if (i == 0 || i == 7)
                            date += 6;
                        else if (i == 2 || i == 9)
                            date += 1;
                        else if (i == 3 || i == 10)
                            date += 2;
                        else if (i == 4 || i == 11)
                            date += 3;
                        else if (i == 5 || i == 12)
                            date += 4;
                        else if (i == 6 || i == 13)
                            date += 5;
                    }
                    i_val += day_name.length;
                    break;
                }
            }
        } else if (token == "MM" || token == "M") {
            month = _getInt(val, i_val, token.length, 2);
            if (month == null || (month < 1) || (month > 12)) {
                return 0;
            }
            i_val += month.length;
        } else if (token == "dd" || token == "d" || token == "DD") {
            date = _getInt(val, i_val, token.length, 2);
            if (date == null || (date < 1) || (date > 31)) {
                return 0;
            }
            i_val += date.length;
        } else if (token == "hh" || token == "h") {
            hh = _getInt(val, i_val, token.length, 2);
            if (hh == null || (hh < 1) || (hh > 12)) {
                return 0;
            }
            i_val += hh.length;
        } else if (token == "HH" || token == "H") {
            hh = _getInt(val, i_val, token.length, 2);
            if (hh == null || (hh < 0) || (hh > 23)) {
                return 0;
            }
            i_val += hh.length;
        } else if (token == "KK" || token == "K") {
            hh = _getInt(val, i_val, token.length, 2);
            if (hh == null || (hh < 0) || (hh > 11)) {
                return 0;
            }
            i_val += hh.length;
        } else if (token == "kk" || token == "k") {
            hh = _getInt(val, i_val, token.length, 2);
            if (hh == null || (hh < 1) || (hh > 24)) {
                return 0;
            }
            i_val += hh.length;
            hh--;
        } else if (token == "mm" || token == "m") {
            mm = _getInt(val, i_val, token.length, 2);
            if (mm == null || (mm < 0) || (mm > 59)) {
                return 0;
            }
            i_val += mm.length;
        } else if (token == "ss" || token == "s" || token == "SS" || token == "S") {
            ss = _getInt(val, i_val, token.length, 2);
            if (ss == null || (ss < 0) || (ss > 59)) {
                return 0;
            }
            i_val += ss.length;
        } else if (token == "a") {
            if (val.substring(i_val, i_val + 2).toLowerCase() == "am") {
                ampm = "AM";
            } else if (val.substring(i_val, i_val + 2).toLowerCase() == "pm") {
                ampm = "PM";
            } else {
                return 0;
            }
            i_val += 2;
        } else if (token == "w" || token == "ww") {
            var weekNum = _getInt(val, i_val, token.length, 2);
            week = true;
            if (weekNum != null) {
                var temp = new Date(year, 0, 1, 0, 0, 0);
                setWeek(temp, parseInt(weekNum, 10));
                year = temp.getFullYear();
                month = temp.getMonth() + 1;
                date = temp.getDate();
            }
            weekNum += "";
            i_val += weekNum.length;
        } else if (token == "D") {
            if (week) {
                var day = _getInt(val, i_val, token.length, 1);
                if ((day == null) || (day <= 0) || (day > 7))
                    return 0;
                var temp = new Date(year, month - 1, date, hh, mm, ss);
                var dayOfWeek = temp.getDay();

                day = parseInt(day, 10);
                day = (day + g_first_day_of_week - 1) % 7;
                if (day == 0)
                    day = 7;
                day--;

                if (day < dayOfWeek)
                    day = 7 - (dayOfWeek - day);
                else
                    day -= dayOfWeek;
                if (day > 0) {
                    temp.setDate(temp.getDate() + day);

                    year = temp.getFullYear();
                    month = temp.getMonth() + 1;
                    date = temp.getDate();
                }
                i_val++;
            }
        } else if (token == "z")
            i_val += 3;
        else {
            if (val.substring(i_val, i_val + token.length) != token) {
                return 0;
            } else {
                i_val += token.length;
            }
        }
    }

    if (format.indexOf('z') == -1 && i_val != val.length) {
        return 0;
    } else if (val.length < i_val || val.length > i_val + 2) {
        return 0;
    }
    if (month == 2) {
        if (((year % 4 == 0) && (year % 100 != 0)) || (year % 400 == 0)) {
            if (date > 29) {
                return 0;
            }
        } else {
            if (date > 28) {
                return 0;
            }
        }
    }
    if ((month == 4) || (month == 6) || (month == 9) || (month == 11)) {
        if (date > 30) {
            return 0;
        }
    }
    if (hh < 12 && ampm == "PM") {
        hh = hh - 0 + 12;
    } else if (hh > 11 && ampm == "AM") {
        hh -= 12;
    }
    var newdate = new Date(year, month - 1, date, hh, mm, ss);
    return newdate.getTime();
}

function _getInt(str, i, minlength, maxlength) {
    for (var x = maxlength; x >= minlength; x--) {
        var token = str.substring(i, i + x);
        var intPart = _getInteger(token);
        if (intPart != "")
            return intPart;
    }
    return null;
}

function _getInteger(val) {
    var digits = "1234567890";
    for (var i = 0; i < val.length; i++) {
        if (digits.indexOf(val.charAt(i)) == -1)
            return val.substring(0, i);
    }
    return val;
}

function LZ(x) {
    return (x < 0 || x > 9 ? "" : "0") + x;
}

// Function with Glide Ajax. This has to be defined in properties list after Glide Ajax.
function formatDateServer(date, format) {
    var AjaxProxy = csGlobals['GlideAjax'];
    var ga = new AjaxProxy("DateTimeUtils");
    ga.addParam("sysparm_name", "formatCalendarDate");
    var browserOffset = date.getTimezoneOffset() * 60000;
    var g_tz_offset = 19800000;
    var utcTime = date.getTime() - browserOffset;
    var userDateTime = utcTime - g_tz_offset;
    ga.addParam("sysparm_value", userDateTime);
	return ga.getXMLAnswer(function(response) {
		gs.info("Glide ajax Response:"+response);
	});
}

function getWeek(date) {
    var newYear = new Date(date.getFullYear(), 0, 1);
    var day = newYear.getDay() - (g_first_day_of_week - 1);
    day = (day >= 0 ? day : day + 7);
    var dayNum = Math.floor((date.getTime() - newYear.getTime() - (date.getTimezoneOffset() - newYear.getTimezoneOffset()) * 60000) / 86400000) + 1;
    var weekNum;
    if (day < 4) {
        weekNum = Math.floor((dayNum + day - 1) / 7) + 1;
        if (weekNum > 52)
            weekNum = _checkNextYear(date);

        return weekNum;
    }
    weekNum = Math.floor((dayNum + day - 1) / 7);
    if (weekNum < 1)
        weekNum = _lastWeekOfYear(date);
    else if (weekNum > 52)
        weekNum = _checkNextYear(date);
    return weekNum;
}

function _checkNextYear(date) {
    var nYear = new Date(date.getFullYear() + 1, 0, 1);
    var nDay = nYear.getDay() - (g_first_day_of_week - 1);
    nDay = nDay >= 0 ? nDay : nDay + 7;
    return nDay < 4 ? 1 : 53;
}

function _lastWeekOfYear(date) {
    var newYear = new Date(date.getFullYear() - 1, 0, 1);
    var endOfYear = new Date(date.getFullYear() - 1, 11, 31);
    var day = newYear.getDay() - (g_first_day_of_week - 1);
    var dayNum = Math.floor((endOfYear.getTime() - newYear.getTime() - (endOfYear.getTimezoneOffset() - newYear.getTimezoneOffset()) * 60000) / 86400000) + 1;
    return day < 4 ? Math.floor((dayNum + day - 1) / 7) + 1 : Math.floor((dayNum + day - 1) / 7);
}


function setWeek(date, weekNum) {
    weekNum--;
    var startOfYear = new Date(date.getFullYear(), 0, 1);
    var day = startOfYear.getDay() - (g_first_day_of_week - 1);
    if (day > 0 && day < 4) {
        date.setFullYear(startOfYear.getFullYear() - 1);
        date.setDate(31 - day + 1);
        date.setMonth(11);
    } else if (day > 3)
        date.setDate(startOfYear.getDate() + (7 - day));
    date.setDate(date.getDate() + (7 * weekNum));
}

csGlobals['formatDate'] = formatDate;
csGlobals['getDateFromFormat'] = getDateFromFormat;

function getMessages(messages) {
    var result = {};
    if (messages) {
        messages.forEach(function(message) {
            result[message] = gs.getMessage(message);
        });
    }
    return result;
}

