										if (!viewStartDate)
											return false;
										if (e.end_date.getTime() > viewStartDate.getTime() && e.start_date.getTime() < viewEndDate.getTime()) {
											$scope.relatedChangeLabels.push({
												key: e.timeline_section,
												label: getChangeLabel(e.change)
