							if (viewStartDate && currentChangeEvent.start_date.getTime() < viewStartDate.getTime())
								c += ' left_extends_beyond';
							if (viewEndDate && currentChangeEvent.end_date.getTime() > viewEndDate.getTime())
								c += ' right_extends_beyond';
