var app = angular.module("sn.chg_model_ui.landing");
app.constant("ChgModelConstants", {
	"CHG_MODELS" : "/api/sn_chg_model_ui/landing/chgmodels",
	"USER_PREFERENCE_PINNED" : "sn_chg_model_ui.landing_page.pinned_change_models",
	"KEYS" : {
		"TABKEY"          : 9,
		"ENTER"           : 13,
		"ESCAPE"          : 27
	}
});
app.service("chgModelService", [ "$window", "$http", "$q", "modelUtil", "userPreferences", "ChgModelConstants",
function($window, $http, $q, modelUtil, userPreferences, ChgModelConstants) {
	var chgModelService = this;
	chgModelService.changeModels = {};

	chgModelService.initialHit = function(filter, orderby, limit, textSearch) {
		var offset = -1;
		chgModelService.changeModels = {};
		return chgModelService.getChangeModels(filter, orderby, offset, limit, textSearch);
	};

	chgModelService.getChangeModels = function(filter, orderBy, offset, limit, textSearch) {
		var deferred = $q.defer();
		textSearch = (!textSearch) ? "" : textSearch.trim();
		var url = ChgModelConstants.CHG_MODELS + "?sysparm_ck=" + $window.g_ck + "&offset=" + offset + "&chg_model_limit=" + limit + "&filter=" + filter + "&orderby=" + orderBy + "&text_search=" + textSearch;
		$http.get(url).then(function(response) {
			updateDataModel(response.data.result, filter);
			deferred.resolve(response.data.result);
		}, function(response) {
			modelUtil.failNicely(response);
			deferred.reject(response);
		});
		return deferred.promise;
	};

	chgModelService.setScheduleLimit = function(newLimit, filter) {
		chgModelService.changeModels[filter].limit = parseInt(newLimit);
		if (!chgModelService.changeModels[filter].limit || isNaN(chgModelService.changeModels[filter].limit) || chgModelService.changeModels[filter].limit > chgModelService.changeModels[filter].maxLimit)
			chgModelService.changeModels[filter].limit = chgModelService.changeModels[filter].maxLimit;
	};

	chgModelService.showSplashMsg = function(filter) {
		if (!chgModelService.changeModels.hasOwnProperty("__struct"))
			return false;
		return chgModelService.changeModels.__struct[filter].sysIds.length === 0;
	};

	// modifies the array, does not copy
	function togglePinned(pinnedArray, sysId) {
		if (!pinnedArray || !Array.isArray(pinnedArray) || !sysId)
			return pinnedArray;

		// remove empty strings WITHOUT a copy of array i.e. do NOT pinnedArray.filter(pinnedSysId => pinnedSysId);
		var len = pinnedArray.length;
		for(var i = 0; i < len; i++ )
			pinnedArray[i] && pinnedArray.push(pinnedArray[i]);

		pinnedArray.splice(0 , len);

		var index = pinnedArray.indexOf(sysId);
		if (index !== -1)
			pinnedArray.splice(index, 1);
		else
			pinnedArray.push(sysId);

		return pinnedArray;
	}

	chgModelService.togglePinned = function(sysId) {
		togglePinned(chgModelService.changeModels.__struct['pinned'].sysIds, sysId);

		if (typeof $window.NOW.chg_model_landing.pinned_change_models !== 'string')
			$window.NOW.chg_model_landing.pinned_change_models = '';

		var newUserPrefPinnedChgModels = togglePinned($window.NOW.chg_model_landing.pinned_change_models.split(','), sysId).join(',');
		if (newUserPrefPinnedChgModels !== $window.NOW.chg_model_landing.pinned_change_models) {
			$window.NOW.chg_model_landing.pinned_change_models = newUserPrefPinnedChgModels;
			userPreferences.setPreference(ChgModelConstants.USER_PREFERENCE_PINNED, $window.NOW.chg_model_landing.pinned_change_models);
		}
	};

	function updateDataModel(result, filter) {
		var limit = 0;
		if (!chgModelService.changeModels.chg_model) {
			chgModelService.changeModels = result;
			limit = chgModelService.changeModels.__struct[filter].sysIds.length;
		} else {
			chgModelService.changeModels.__struct[filter].__more = result.__more;
			result.chg_model.forEach(function(chgModel) {
				var chgModelId = chgModel.sys_id.value;
				chgModelService.changeModels.chg_model[chgModelId] = chgModel;
				if (chgModelService.changeModels.__struct[filter].sysIds.indexOf(chgModelId) === -1)
					chgModelService.changeModels.__struct[filter].sysIds.push(chgModelId);
				if (filter !== 'all' && chgModelService.changeModels.__struct['all'].sysIds.indexOf(chgModelId) === -1)
					chgModelService.changeModels.__struct['all'].sysIds.push(chgModelId);
			});
		}
		chgModelService.changeModels.__struct[filter].offset = chgModelService.changeModels.__struct[filter].offset + chgModelService.changeModels.__struct[filter].limit;
	}

	chgModelService.hasMoreChangeModels = function(filter) {
		return chgModelService.changeModels.__struct[filter].__more;
	};

	chgModelService.hasReachedLimit = function(filter) {
		return chgModelService.changeModels.__struct[filter].sysIds.length >= chgModelService.changeModels.__struct[filter].maxLimit;
	};

	function compareBy(a, b, field, prop) {
		if (!field || !prop || !a[field] || !a[field][prop] || !b[field] || !b[field][prop])
			return 0;

		if (a[field][prop] < b[field][prop])
			return -1;

		if (a[field][prop] > b[field][prop])
			return 1;

		return 0;
	}

	function compareByName(a, b) {
		return compareBy(a, b, "name", "display_value");
	}

	function compareByDescription(a, b) {
		return compareBy(a, b, "description", "display_value");
	}

	function sort(filter, sortBy) {
		var arrToSort = [];
		chgModelService.changeModels.__struct[filter].sysIds.forEach(function(key) {
			arrToSort.push(chgModelService.changeModels.chg_model[key]);
		});

		if (sortBy === "name")
			arrToSort.sort(compareByName);
		else
			arrToSort.sort(compareByDescription);

		var sysIds = [];
		arrToSort.forEach(function(obj) {
			sysIds.push(obj.sys_id.value);
		});
		return sysIds;
	}

	chgModelService.sortArray = function(sortBy) {
		chgModelService.changeModels.__struct["pinned"].sysIds = sort("pinned", sortBy);
		chgModelService.changeModels.__struct["change_models"].sysIds = sort("change_models", sortBy);
		chgModelService.changeModels.__struct["preapproved"].sysIds = sort("preapproved", sortBy);
		chgModelService.changeModels.__struct["all"].sysIds = sort("all", sortBy);
	};
}]);

function goog_auth_on_load(formId) {
	var currForm = document.getElementById(formId);
	var verificationId = currForm.elements['verification_id'].value;
	var userId = currForm.elements['user_id'].value;	
	loadContent();
}

function goog_auth_on_submit(formId) {
	var currForm = document.getElementById(formId);	
	var mandatory = currForm.elements['mandatory'].value == 'true';

	currForm.elements['can_submit'].value = 'true';
	var valid = currForm.elements['goog_auth_valid'].value == 'true';	

	if (!valid) {
		// If form is invalid and mandatory, show message and select the tab to draw the user's attention.
		if (mandatory) {
			var tabId = 'tablabel' + currForm.elements['tab_index'].value;
		    var tab = gel(tabId);
		    tab.click();
		    displayErrorMessage(getMessage('You must authorize at least one device with Authenticator App'));
		    return false;
		}
		// if not mandatory we dont want the form submitted and enrollment inserted
		else {
			currForm.elements['can_submit'].value = 'false';
		}
	}

	return true;
}

function loadContent(){
	var ajax = new GlideAjax("PwdGoogleAuthenticator");
    ajax.addParam("sysparm_name", "loadContent");
	ajax.addParam("sysparm_userId", getUserID());
	ajax.getXML(this.processContent.bind(this));
}

function processContent(response){
	if (!securityCheck(response))
		return false;

	var isEnabledResponse = response.responseXML.getElementsByTagName("isEnabled");
    var isEnabled = isEnabledResponse[0].getAttribute("isEnabled");
	var result = response.responseXML.getElementsByTagName("result");

	if (isEnabled == "false") 
		showEnableContent();
	else if(result && result.length > 0){
		var validated = result[0].getAttribute("validated");
		var qrCodeURL = result[0].getAttribute("qrCodeURL");
		var qrCodeText = result[0].getAttribute("qrCodeText");
		var canDisable = result[0].getAttribute("canDisable");

		if(validated == "true")
			showSuccessContent();
		else {
			showValidateContent();
			gel('imgCode').src = qrCodeURL;
			gel('lblCode').innerHTML = new GwtMessage().getMessage("Or type in: {0}",qrCodeText);
		}
	}	
}

function validateResponse(formId){
	var userResponse = document.getElementById("txtResponse").value.trim();
	if (userResponse.length != 6 || isNaN(userResponse))
		showError();

	var currForm = document.getElementById(formId);
	var verificationId = currForm.elements['verification_id'].value;

	var ajax = new GlideAjax("PwdGoogleAuthenticator");
    ajax.addParam("sysparm_name", "validateEnrollmentResponse");
	ajax.addParam("sysparm_response", userResponse);
	ajax.addParam("sysparm_userId", getUserID());
	ajax.getXML(this.processResponse.bind(this));
}

function processResponse(response){
	if (!securityCheck(response))
		return false;

	var result = response.responseXML.getElementsByTagName("result");
	if (result && result.length > 0 ) {
		var validated = result[0].getAttribute("validated");
		if (validated == 'true')
			loadContent();
		else
			showError();
	} else
		showError();
}

function enableSetup() {
	var ajax = new GlideAjax("PwdGoogleAuthenticator");
	ajax.addParam("sysparm_name", "enableGoogleAuth");
	ajax.addParam("sysparm_userId", getUserID());
	ajax.getXML(this.processContent.bind(this));
}

function disableSetup() {
	var userId = getUserID();
	var areYouSure = confirm(new GwtMessage().getMessage("Are you sure you want to disable Multi-factor Authentication?"));
	if(areYouSure == true){
		g_form.clearMessages();
		g_form.addInfoMessage("The Multifactor Authentication property has been disabled for your user");

		var ajax = new GlideAjax("PwdGoogleAuthenticator");
		ajax.addParam("sysparm_name", "disableGoogleAuth");
		ajax.addParam("sysparm_userId", userId);
		ajax.getXML(this.processContent.bind(this));
	}
}

function resetCode() {
	var areYouSure = confirm(new GwtMessage().getMessage("Are you sure you want to generate a new code?"));
	if(areYouSure == true){
		var userId = getUserID();
		var ajax = new GlideAjax("PwdGoogleAuthenticator");
		ajax.addParam("sysparm_name", "resetMultifactorCode");
		ajax.addParam("sysparm_userId", userId);
		ajax.getXML(this.processContent.bind(this));
	}
}

function getUserID() {
	var user_id =  gel("userId").value;
	return user_id;
}

function showError(){
	g_form.clearMessages();
	g_form.addErrorMessage(getMessage("The Authenticator App code that you entered is invalid. Try again."));
	return false;
}

function securityCheck(response) {
	var securityError = response.responseXML.documentElement.getAttribute("error");
	if (securityError == "unauthorized") {
		g_form.clearMessages();
	    g_form.addErrorMessage(getMessage("You are not authorized to perform that action"));
	    return false;
	} else
		return true;
}

function showEnableContent() {
	toggleContent(true, false, false, false);
}

function showValidateContent() {
    gel("txtResponse").value = "";
	toggleContent(false, true, false, false);
}

function showSuccessContent() {
	var successMsg = new GwtMessage().getMessage("Your device was paired successfully and you can use it to verify your identity while resetting your password.");
	var warningRstMsg = new GwtMessage().getMessage("In addition, you can use the device to implement multi-factor authentication with other applications. If you generate a new code and pair the device using the code, the current multi-factor authentication data is replaced.");

	toggleContent(false, false, true, true);
	g_form.addInfoMessage(successMsg + "<br/><br/>" + warningRstMsg);
}

function toggleContent(enableContent, validateContent, successContent, authValid) {
	g_form.clearMessages();
	$j("#tdEnable").toggle(enableContent);
	$j(".pwd-auth-validate").toggle(validateContent);
	$j("#tdSuccess").toggle(successContent);
	gel("goog_auth_valid").value = authValid? "true":"false";
}

angular.module("sn.chg_soc.accessibility", [])
	.directive("focusHere", ["$timeout", function ($timeout) {
		return {
			restrict: "A",
			link: function ($scope, $element, $attr) {
				$timeout(function(){
					$element[0].focus();
				}, 0);
			},
			controller: function ($scope) {
				$scope.$on("sn.aside.change_soc_side.open", function() {
					var firstTabbable = angular.element("[focus-here='']");
					if (firstTabbable.length)
						firstTabbable.focus();
				});
			}
		};
	}])
	.directive("trapTabFocus", ["SOC", function (SOC) {
		return {
			restrict: "A",
			link: function ($scope, $element, $attr) {
				$element.on("keydown", function(event) {
					if (event.keyCode === SOC.KEYS.TABKEY) {
						var tabEls = $element.find(":tabbable");
						var firstEl = tabEls.first();
						var lastEl = tabEls.last();
						// tab and last element then focus on first element
						if (!event.shiftKey && event.target === lastEl[0]) {
							event.preventDefault();
							firstEl.focus();
						}
						// shift+tab and first element then focus on last element
						if (event.shiftKey && event.target === firstEl[0]) {
							event.preventDefault();
							lastEl.focus();
						}
					}
				});
			}
		};
	}]);

