angular.module("sn.change_management.cab.runtime_state")
	.service("runtimeState", ['CAB', 'modelUtil', 'initialLoad', '$http', '$q', '$rootScope', 'cabWatcher', function(CAB, modelUtil, initialLoad, $http, $q, $rootScope, cabWatcher) {			
		var runtimeState = this;  // For convenience
		var rSt = {'localState': {}}; // Runtime state
		var mId = null; // Meeting Id
		var mIdReq = null; // Requested meeting ID
		var defReq = null; //Deferred request

		// The latest modification from Record Watcher
		var rwMod = {
			"cab_runtime_state": null,
			"cab_meeting": null
		};

		// Builds the state from the REST service return
		function buildState(response) {

			// Clear the object without breaking references
			for (var key in rSt)
				if (rSt.hasOwnProperty(key))
					delete(rSt[key]);

			rSt.localState = {};

			modelUtil.patchObject(rSt, response.data.result);
			// Add MS values for all dates in the nested CAB Meeting record
			modelUtil.addValueMSToAll(rSt.cab_meeting.record);
			modelUtil.glideListToArray(rSt.cab_meeting.record.delegates);

			rSt.localState.time_offset = Date.now() - rSt.request_time_ms;

			mId = rSt.cab_meeting.value;

			cabWatcher
				.watch(mId)
				.subscribe(function(message) {
					var dataTable = message.data.table_name;
					if (dataTable === CAB.RUNTIME_STATE) {
						$rootScope.$applyAsync(function(scope) {
							modelUtil.patchObject(rSt, message.data.record);
							rwMod[CAB.RUNTIME_STATE] = message;
						});
					}
					if (dataTable === CAB.MEETING) {
						$rootScope.$applyAsync(function(scope) {
							if (!rSt.cab_meeting.record)
								rSt.cab_meeting.record = {};

							modelUtil.patchObject(rSt.cab_meeting.record, message.data.record);
							modelUtil.glideListToArray(rSt.cab_meeting.record.delegates);
							rwMod[CAB.MEETING] = message;
						});
					}
				});
		}

		// API
		/**
		 * Sets the current agenda item for the meeting. Patches the runtime
		 * state if amb is not turned on
		 */
		runtimeState.nextAgendaItem = function(nextAgendaItemId) {
			if (nextAgendaItemId && !modelUtil.isValidSysID(nextAgendaItemId))
				return $q.reject("Invalid Agenda Item Id");

			return $http.get(CAB.SERVICE.RUNTIME+ "next_item/" + mId + "/" + nextAgendaItemId)
				.then(null, modelUtil.failNicely);
		};

		runtimeState.demoteCurrentNextAgendaItem = function(nextAgendaItemId) {
			if (nextAgendaItemId && !modelUtil.isValidSysID(nextAgendaItemId))
				return $q.reject("Invalid Agenda Item Id");

			return $http.get(CAB.SERVICE.RUNTIME+ "demote_current_item/" + mId + "/" + nextAgendaItemId)
				.then(null, modelUtil.failNicely);
		};

		runtimeState.noDecisionNextAgendaItem = function(nextAgendaItemId) {
			if (nextAgendaItemId && !modelUtil.isValidSysID(nextAgendaItemId))
				return $q.reject("Invalid Agenda Item Id");

			return $http.get(CAB.SERVICE.RUNTIME+ "nd_next_item/" + mId + "/" + nextAgendaItemId)
				.then(null, modelUtil.failNicely);
		};

		runtimeState.pauseCurrentAgendaItem = function() {
			return $http.get(CAB.SERVICE.RUNTIME+ "pause_current_item/" + mId)
				.then(null, modelUtil.failNicely);
		};

		runtimeState.resumeCurrentAgendaItem = function() {
			return $http.get(CAB.SERVICE.RUNTIME+ "resume_current_item/" + mId)
				.then(null, modelUtil.failNicely);
		};

		/**
		 * If the user is following the current agenda item
		 */
		runtimeState.isViewingCurrent = function() {
			if (rSt.localState && rSt.localState.viewing && rSt.current_agenda_item)
				return rSt.localState.viewing == rSt.current_agenda_item.value;
			return true;
		};

		/**
		 * Starts the meeting and selects next pending agenda as current agenda
		 */
		runtimeState.startMeetingAndSelectNextAgenda = function() {
			if (!mId)
				$q.reject("No meeting Id");

			return runtimeState.startMeeting();
		};

		/**
		 * Set the agenda item the user is following
		 */
		runtimeState.setViewAgendaItem = function(agendaItemId) {
			$rootScope.$applyAsync(function() {
				rSt.localState.viewing = agendaItemId;
			});
		};

		/**
		 * Returns the id if the agenda item being viewed
		 */
		runtimeState.getViewAgendaItem = function() {
			if (rSt.localState && rSt.localState.viewing)
				return rSt.localState.viewing;

			if (rSt.current_agenda_item)
				return rSt.current_agenda_item.value;
		};

		/**
		 * Returns true if the given user is the cab manager
		 */
		runtimeState.isCABManager = function(userSysId) {
			if (rSt.host && rSt.host.value) 
				return rSt.host.value === userSysId;

			if (rSt.cab_meeting && rSt.cab_meeting.record)
				return rSt.cab_meeting.record.manager.value === userSysId;

			return false;
		};

		runtimeState.isHost = function(userSysId) {
			if (rSt.host && rSt.host.value)
				return rSt.host.value === userSysId;

			return false;
		};

		/**
		 * Return true if the given user id is a delegate.
		 */
		runtimeState.isCABManagerDelegate = function(userSysId) {
			if (!(rSt.cab_meeting && rSt.cab_meeting.record))
				return false;

			// If they're the CAB Manager, the're always a delegate
			if (rSt.cab_meeting.record.manager.value == userSysId)
				return true;

			// If they're one of the delegates, they're a delegate
			if (rSt.cab_meeting.record.delegates.value.indexOf(userSysId) > -1)
				return true;

			return false;
		};

		/**
		 * Returns true if the given user is current notes editor
		 */
		runtimeState.isNotesEditor = function(userSysId) {
			if (rSt.notes_editor && rSt.notes_editor.value)
				return rSt.notes_editor.value === userSysId;

			return false;
		};

		runtimeState.canEditNotes = function(userSysId){
			return (runtimeState.isCABManager(userSysId) || runtimeState.isCABManagerDelegate(userSysId))
					&& rSt.notes_editor.value === userSysId;
		};

		runtimeState.setNotesEditor = function(useId){
			return $http.post(CAB.SERVICE.NOTES_EDITOR + mId, { notesEditor: useId });
		};

		/**
		 * Starts the meeting if it's not already in progress
		 */
		runtimeState.startMeeting = function() {
			if (rSt.cab_meeting && rSt.cab_meeting.record && rSt.cab_meeting.record.state.value == CAB.PENDING)
				return $http.get(CAB.SERVICE.RUNTIME+ "start/" + mId).then(null, modelUtil.failNicely);

			return $q.reject();
		};


		runtimeState.hostMeeting = function(newHostId){
			if (!(mId && rSt.cab_meeting)) //If the meeting is not set up don't allow host change
				return $q.reject();

			return $http.post(CAB.SERVICE.HOST_MEETING + mId, { newHost: newHostId }).then(null, modelUtil.failNicely);
		};

		/**
		 * Ends the meeting if it's in progress
		 */
		runtimeState.endMeeting = function() {
			if (rSt.cab_meeting && rSt.cab_meeting.record && rSt.cab_meeting.record.state.value == CAB.IN_PROGRESS)
				return $http.get(CAB.SERVICE.RUNTIME+ "end/" + mId).then(null, modelUtil.failNicely);

			return $q.reject();
		};

		// LIFE CYCLE ----------
		/**
		 * Cleanup the service.
		 */
		runtimeState.cleanup = function() {
			if (wChan)
				wChan.unsubscribe();

			if (wMtgChan)
				wMtgChan.unsubscribe();

			for (var key in rSt)
				if (rSt.hasOwnProperty(key))
					delete(rSt[key]);

			rSt.localState = {};
		};

		/**
		 * Initialise the meeting state. Leaves the meeting state in it's
		 * original state if it fails.
		 */
		function init(meetingSysId) {
			if (!meetingSysId || !modelUtil.isValidSysID(meetingSysId))
				return $q.reject("Invalid meetingId");

			// Request setup
			mIdReq = meetingSysId;  //requested meeting id
			var deferred = defReq || $q.defer();  //Check for placeholder deferred request
			defReq = deferred;

			initialLoad.runtimeState(meetingSysId)
			.then(
				function(response){
					buildState(response);
					mIdReq = null;
					defReq = null;
					deferred.resolve(rSt);
				}, 
				function(response) {
					modelUtil.failNicely(response);
					mIdReq = null;
					defReq = null;
					deferred.reject(response);
				}
			);
			return deferred.promise;
		}

		/**
		 * Refreshes the current meeting state using the REST service
		 */
		runtimeState.refresh = function() {
			var deferred = $q.defer();
			$http.get(CAB.SERVICE.RUNTIME + this.mId)
			.then(
				function(response) {
					modelUtil.patchObject(rSt, response.data.result);
					modelUtil.addMSValueToAll(rSt.cab_meeting.record);
					deferred.resolve(rSt);
				},
				function(response) {
					modelUtil.failNicely(response);
					deferred.reject(response);
				}
			);
			return deferred.promise;
		};

		/**
		 * Get the meeting state for the given sys id. Returns the current state
		 * if the meetingId matches, initialises against the new id if not.
		 */
		runtimeState.get = function(meetingSysId) {
			// If there's nothing to go on, wait until there is
			if (!meetingSysId && !mId && !defReq) {
				defReq = $q.defer();
				return defReq.promise;
			}

			// If no meeting has been requested but we're waiting until we have a meeting id.
			if (!meetingSysId && !mIdReq && defReq)
				return defReq.promise;

			// If meeting info has been requested
			if ((!meetingSysId || meetingSysId === mIdReq) && defReq)
				return defReq.promise;

			// If we already have the meeting info
			if ((meetingSysId === mId) || (!meetingSysId && mId))
				return $q.resolve(rSt);

			// Init if we have a meeting id
			if (meetingSysId)
				return init(meetingSysId);

			// Default reject
			return $q.reject();
		};

		/**
		 * Returns the Record Watcher modifications object.
		 * Record watcher responses are set against named values to determine when data from record watcher
		 * was received.  Name of the trigger is usually the table name of the modified table.
		 */
		runtimeState.getRWMod = function() {
			return rwMod;
		};

	}]);

(function ($) {
	'use strict';
	function removeLastWords(txt, count) {
		if (txt && count) {
			txt = txt.replace(/[\t\n\r\s]+/g, ' ').trim();
			var idx = txt.length;
			for (var i = txt.length - 1; i >= 0; i--) {
				var c = txt.charAt(i);
				if (c === ' ')
					count--;
				if (count === 0) {
					return txt.substring(0, i);
				}
			}
		}
		return txt;
	}

	angular.module('sn.itsm.vcab.common').directive('snCabLineClamp', function ($timeout) {
		return {
			restrict: 'E',
			scope: {
				text: '@',
				maxLines: '@'
			},
			template: '<div>{{truncatedText}}</div>',
			link: function (scope, element, attrs) {
				var maxLines = parseInt(scope.maxLines || 1);
				var isClamped = false;
				var $el = $(element).children('div');
				var isWidthSet = false;
				if($(element).closest('.snc-common-calendar').length == 0)
					isWidthSet = true;
				function tryText() {
					if(!isWidthSet) {
						var $calHeader = $('.dhx_cal_header > div > div:first-child');
						if($calHeader.length > 0) {
							isWidthSet = true;
							var marginRight = 10;
							var left = $calHeader.position().left;
							var maxWidth = left - element.position().left - marginRight;
							$el.closest('td').css({
								'width': (left + 1) + 'px'
							});
							$el.closest('p').css({
								'width': maxWidth + 'px',
								'margin-right': marginRight + 'px'
							});
						}
					}

					$timeout(function () {
						if ($el.get(0).scrollHeight > (scope.maxAllowedHt + 5)) {
							scope.truncatedText = removeLastWords(scope.text, ++scope.counter) + ' ';
							$el.attr('title', scope.text);
							tryText();
						} else if (scope.counter === 0) {
							scope.truncatedText = scope.text;
							$el.removeAttr('title');
						}
					}, 1);
				}
				function clamp () {
					if (!isClamped) {
						scope.maxAllowedHt = parseInt($el.css('line-height')) * maxLines;
						$el.css({
							'max-height': scope.maxAllowedHt + 'px',
						});
						isClamped = true;
					}
					scope.counter = 0;
					scope.truncatedText = scope.text;
					tryText();
				}
				scope.$watch('text', clamp);
			}
		};
	});
}(jQuery));

function initializeConflictDetection() {

	var trackerId = $('tracker_id').value;
	if (trackerId !== "") {
		runBackgroundPoll(trackerId);
		showProgressButton(true);
	}

	// Ensure the warning message and check conflicts button are
	// correctly shown/hidden (Poll every 2 seconds in case User has
	// cleared one of the 3 mandatory conflict detection fields)
	setInterval(updateWarningBoxAndConflictButton, 2000);

	setTimeout(function() {
		if (!g_form.isNewRecord() && ($('tracker_id').value !== "")) {
			if (updateWarningBoxAndConflictButton()) {
				updateConflictStatus(0);
				refreshConflicts();
			}
		}
	}, 1000);

	$("conflict_action_run").observe("click", function(event) {
		event.stop();

		// If we have unfilled mandatory fields then do not progress
		if (!g_form.mandatoryCheck())
			return;

		if (g_form.isNewRecord() || g_form.modified){
			// Add a hidden parameter to indicate that the run conflict detection should be run after the form has saved
			var afterSaveURL = new GlideURL();

			if (g_form.isNewRecord())
				afterSaveURL.setFromString(g_form.getTableName() + '.do?sys_id=' + g_form.getUniqueValue());
			else
				afterSaveURL.setFromCurrent();

			addHidden(g_form.getFormElement(), 'sysparm_goto_url', afterSaveURL.getURL().replace(g_form.getTableName() + '.do','change_conflict_ui.do'));
			g_form.save();
		} else
		runConflictDetection();
	});

	//Add event listener for list v2 changes for Affected CI's
	CustomEventManager.observe('partial.page.reload', function (elem, e) {
		if (e && e.listID === g_form.getTableName() + '.task_ci.task')
			$('affected_item_count').value = e.totalRows;
	});

	//Add event listener for list v3 changes for Affected CI's
	CustomEventManager.observe('g_list.api.refresh', function (e) {
		if (e && e.listId === g_form.getTableName() + '.task_ci.task')
			$('affected_item_count').value = GlideList.get(g_form.getTableName() + '.task_ci.task').getRowCount();
	});

	var runTrackerEl = $('run_tracker_ui');
	if (runTrackerEl && runTrackerEl.value == 'true')
		runConflictDetection();
}

function getSchedulingConflictMessage() {
	var startDateReadOnly = g_form.isReadOnly("", g_form.getControl("start_date"));
	var endDateReadOnly = g_form.isReadOnly("", g_form.getControl("end_date"));

	if (startDateReadOnly || endDateReadOnly)
		return new GwtMessage().getMessage("Scheduling conflict detected. Review the Conflicts form section.");
	return new GwtMessage().getMessage("Scheduling conflict detected. Use the <a href='javascript:void(0);' onclick='openNextAvailableTime()'>Scheduling Assistant</a> to avoid conflicts.");
}

function runConflictDetection() {
	var cancelButtonId = 'cancelButton';
	var backgroundlButtonId = 'runInBackgroundButton';

	var dd = new GlideModal("simple_progress_viewer", true, "40em", "10.5em");
	dd.setTitle(new GwtMessage().getMessage("Checking conflicts"));
	dd.setPreference('sysparm_progress_name', "Conflict Checker");
	dd.setPreference("sysparm_renderer_expanded_levels", "0");
	dd.setPreference("sysparm_renderer_hide_drill_down", true);

	if ($('tracker_id').value == "") {
		dd.setPreference("sysparm_ajax_processor", "ChangeConflictAJAXProcessor");
		dd.setPreference("sysparm_sysid", g_form.getUniqueValue());
	} else

	dd.setPreference('sysparm_renderer_execution_id', $('tracker_id').value + "");
	dd.setPreference("sysparm_button_close", new GwtMessage().getMessage("Close"));

	dd.on("executionComplete", function(trackerObj) {
		receiveMessage(trackerObj);

		if (trackerObj.state == 4){
			var progressContainer = $("simple_progress_viewer");
			if (progressContainer)
				progressContainer.down("#container").innerHTML = $("conflict_canceled_progress_status").innerHTML;
		}

		var closeBtn = $("sysparm_button_close");
		if (closeBtn) {
			closeBtn.onclick = function() {
				showProgressButton(false);
				dd.destroy();
			};
		}

		var cancelButton = $(cancelButtonId);
		if (cancelButton)
			cancelButton.hide();

		var backgroundButton = $(backgroundlButtonId);
			if (backgroundButton)
				backgroundButton.hide();
	});

	dd.on("beforeclose", function() {
		runBackgroundPoll($('tracker_id').value);
	});

	dd.on("renderStatus", function(trackerObj) {
		// Record the value of the tracker for future use
		$('tracker_id').value = trackerObj.sys_id;

		// Add the cancel button
		if (!dd.hasCancelButton){
			// Create a button that closes the dialog when it is clicked
			// (But continues to run conflict detection in the background)
			var button = new Element('button', {'id': cancelButtonId, 'class': 'btn btn-default', 'style': 'margin: 0px 4px 0px 4px;'});
			button.update(new GwtMessage().getMessage("Cancel"));
			button.observe("click", function(event) {
				var backgroundButton = $(backgroundlButtonId);
				if (backgroundButton)
					backgroundButton.disable();

				cancelConflictDetection($('tracker_id').value);
			});

			// Add the button to the dialog and make a note of the fact that you added it
			var buttonPanel = $('buttonsPanel');
			buttonPanel.appendChild(button);
			dd.hasCancelButton = true;
		}

		// Add the Run in background button
		if (!dd.hasRunInBackgroundButton){
			// Create a button that closes the dialog when it is clicked
			// (But continues to run conflict detection in the background)
			var button = new Element('button', {'id': backgroundlButtonId, 'class': 'btn btn-primary', 'style': 'margin: 0px 4px 0px 4px;'});
			button.update(new GwtMessage().getMessage("Run in Background"));
			button.observe("click", function(event) {
				var cancelButton = $(cancelButtonId);
				if (cancelButton)
					cancelButton.disable();

				runBackgroundPoll($('tracker_id').value);
				dd.destroy();
			});

			// Add the button to the dialog and make a note of the fact that you added it
			var buttonPanel = $('buttonsPanel');
			buttonPanel.appendChild(button);
			dd.hasRunInBackgroundButton = true;
		}
	});

	showProgressButton(true);
	dd.render();
}

function updateWarningBoxAndConflictButton() {
	// Get values of all fields to determine if conflict detection should be allowed or not
	var cmdbCi = g_form.getValue('cmdb_ci');
	var startDate = g_form.getValue('start_date');
	var endDate = g_form.getValue('end_date');
	var conflictMode = $('conflict_mode').value;
	var affectedItemCount = $('affected_item_count').value;

	// Get references to the two gui widgets you intend to enable/disable
	var warningBox = $('conflictDetectionStatus');
	var checkConflictsButton = $('conflict_action_run');

	// Case1: Basic Mode + Configuration Item + Planned Start Date + Planned End Date
	if (conflictMode == 'basic' && cmdbCi != '' && startDate != '' && endDate != '') {
		warningBox.hide();
		checkConflictsButton.enable();
		return true;
	}

	// Case2: Advanced Mode + Configuration Item + Planned Start Date + Planned End Date
	if (conflictMode == 'advanced' && cmdbCi != '' && startDate != '' && endDate != '') {
		warningBox.hide();
		checkConflictsButton.enable();
		return true;
	}

	// Case3: Advanced Mode + Affected Items + Planned Start Date + Planned End Date
	if (conflictMode == 'advanced' && parseInt(affectedItemCount) != 0 && startDate != '' && endDate != '') {
		warningBox.hide();
		checkConflictsButton.enable();
		return true;
	}

	// Default: Do not allow User to run conflict detection
	warningBox.show();
	checkConflictsButton.disable();
	return false;
}

function showProgressButton(boolValue) {
	if (boolValue) {
		$("conflict_run_state").value = "executing";
		$("conflict_action_run").innerHTML = new GwtMessage().getMessage("View Progress");
	} else {
		$("conflict_run_state").value = "ready";
		$("conflict_action_run").innerHTML = new GwtMessage().getMessage("Check Conflicts");
	}
}

function runBackgroundPoll(sysId) {
	if (sysId != "") {
		var ga = new GlideAjax('ChangeConflictAJAXProcessor');
		ga.addParam('sysparm_name', 'getTrackerRecord');
		ga.addParam('sysparm_tracker_id', sysId);
		ga.getXMLAnswer(function (answer){
			receiveMessage(answer.evalJSON());
		});
	}
}

function cancelConflictDetection(sysId) {
	if (sysId != "") {
		var ga = new GlideAjax('ChangeConflictAJAXProcessor');
		ga.addParam('sysparm_name', 'cancel');
		ga.addParam('sysparm_tracker_id', sysId);
		ga.getXML();
	}
}

function everyTenPercent(percent, handler) {
	this.data = this.data || [false, false, false, false, false, false, false, false, false, false, false];
	var slot = ((Math.round(percent / 10) * 10) / 10);
	if (this.data[slot])
		return;
	this.data[slot] = true;
	handler();
}

function refreshConflicts() {
	var conflictLists = GlideList2.getListsForTable("conflict");
	var impactedCIs = GlideList2.get(g_form.getTableName() + '.task_cmdb_ci_service.task');
	conflictLists.push(impactedCIs);
	for (var i = 0; i < conflictLists.length; ++i)
		if (conflictLists[i]) //Check that the list is valid before trying to refresh
			conflictLists[i].refresh();
}

function receiveMessage(tracker) {
	/**
	* 0 - Pending
	* 1 - Running
	* 2 - Successful
	* 3 - Failed
	* 4 - Cancelled
	*/
	if (tracker.state == 0 || tracker.state == 1) {
		everyTenPercent(tracker.percent_complete, refreshConflicts);
		$("conflict_status").update(new GwtMessage().getMessage("{0}% completed", tracker.percent_complete));
		setTimeout(function() {
			this.runBackgroundPoll($("tracker_id").value);
		}, 2000);
	} else {
		$("tracker_id").value = "";
		showProgressButton(false);

		switch (tracker.state - 0){
			case 2:
				refreshConflicts();
				updateConflictLastRun(tracker.timestamp);
				updateConflictStatus(tracker.state - 0);
				$("conflict_status").update(new GwtMessage().getMessage("Completed"));
				break;
			case 3:
			case 4:
				deleteConflicts(tracker);
				break;
		}

		setTimeout(function() {
			$("conflict_status").update("");
		}, 5000);
	}
}

function deleteConflicts(trackerObj){
	if (trackerObj.state == 3)
		$("conflict_status").update(new GwtMessage().getMessage("Failed"));

	if (trackerObj.state == 4)
		$("conflict_status").update(new GwtMessage().getMessage("Canceled"));

	var ga = new GlideAjax('ChangeConflictAJAXProcessor');
	ga.addParam('sysparm_name', 'deleteConflicts');
	ga.addParam('sysparm_sys_id', g_form.getUniqueValue());
	ga.addParam('sysparm_tracker_id', trackerObj.sys_id);
	ga.getXMLAnswer(function(answer){
		refreshConflicts();
		if (answer + "" !== "NEWER_TRACKER_FOUND") {
			updateConflictLastRun("");
			g_form.setValue('conflict_status', 'Not Run');
		}
	});
}

function updateConflictLastRun(timestamp) {
	// update the field which gets displayed when time displayed in 'calendar' or 'both' modes
	var conflictLastRunCalendar = $('sys_readonly.' + g_form.getTableName() + '.conflict_last_run');
	if (conflictLastRunCalendar)
		conflictLastRunCalendar.setValue(timestamp);

	// update the field which gets displayed when time displayed in 'timeAgo' mode
	var conflictLastRunTimeAgo = $(g_form.getTableName() + '.conflict_last_run.timeago');
	if (conflictLastRunTimeAgo)
		conflictLastRunTimeAgo.update(new GwtMessage().getMessage("Just now"));
}

function updateConflictStatus(trackerState) {
	var conflictLastRun = $('sys_readonly.' + g_form.getTableName() + '.conflict_last_run');
	var ga = new GlideAjax("ChangeConflictAJAXProcessor");
	ga.addParam('sysparm_name', 'getConflictData');
	ga.addParam('sysparm_change_id', g_form.getUniqueValue());
	ga.getXML(function(message) {
		var conflictInfo = message.responseXML.documentElement.getAttribute("answer").evalJSON();

		// Do not update the form if the tracker is not finished
		if (trackerState === 0 || trackerState === 1)
			return;

		if (conflictInfo.conflictCount === 0) {
			if (conflictLastRun == '')
				g_form.setValue('conflict_status', 'Not Run');
			else {
				g_form.setValue('conflict_status', 'No Conflict');
				updateConflictLastRun(conflictInfo.timestamp);
			}
		} else {
			g_form.setValue('conflict_status', 'Conflict');
			updateConflictLastRun(conflictInfo.timestamp);
		}
	});
}

(function () {
	'use strict';

	var instances = {};

	/*
	 * Meant to be used by meeting dashboard widgets and directives.
	 * It provides managed access to meeting related data.
	 *
	 * Usage: var mtg = new meetingUtil(meetingId);
	 *
	 * APIs:-
	 *
	 * registerCurrentChangeWatcher(callback) - The callback will be invoked with change
	 * of the current agenda. The callback will be called whenever the current agenda changes.
	 *
	 * registerCurrentAgendaWatcher(callback) - The callback will be invoked with the current
	 * agenda. The callback will be called whenever the current agenda changes.
	 *
	 * getAgendaCount(callback) - The callback will be invoked periodically with the total agenda
	 * count, pending agenda count, and the count of agenda by which the meeting is running ahead
	 * in schedule. If meeting is just on schedule then the count will be zero. If it is running
	 * behind schedule, say, by 2 agenda items, then it will return -2.
	 *
	 * registerMeetingChangeWatcher(callback) - The callback will be called with meeting data object
	 * whenever meeting data is updated. The meeting data object will always contain the updated data.
	 */
	angular.module('sn.itsm.vcab.common')
	.factory('meetingUtil', function (vcabDataSource, amb, Tables, dataUtil, $q, $timeout, $log) {
		function ListenerCollection() {
			var list = [];
			this.add = function (cb) {
				list.push(cb);
			};
			this.remove = function (cb) {
				list.some(function (f, idx) {
					if (f === cb) {
						list.splice(idx, 1);
						return true;
					}
				});
			};
			this.notify = function () {
				var args = Array.prototype.slice.call(arguments);
				list.forEach(function (cb) {
					cb.apply(undefined, args);
				});
			};
		}

		function watch(table, filter, cb) {
			var ch = amb.getChannelRW(table, filter);
			ch.subscribe(cb);
			amb.connect();
		}

		function patchObjWithChanges(obj, msg) {// Where msg is the object returned by record watcher.
			if (msg.data.operation === 'update') {
				for (var i = 0; i < msg.data.changes.length; i++) {
					var attr = msg.data.changes[i];
					angular.extend(obj[attr], msg.data.record[attr]);
				}
			} else {
				$log.error('patchObjWithChanges: Unsupported operation ' + msg.data.operation);
			}
		}

		function MeetingUtil(meetingId) {
			if (instances[meetingId])
				return instances[meetingId];

			var currChangeWatchers = new ListenerCollection();
			var currAgendaWatchers = new ListenerCollection();
			var agendaCountWatchers = new ListenerCollection();
			var meetingWatchers = new ListenerCollection();

			var currentChange;
			var currentAgenda;

			var meetingData;
			var meetingTotalDuration; // In ms
			var runningCount;
			var lastRunningCount;
			var totalAgendaCount;
			var totalPendingAgendaCount;

			function onNewMeetingData(meeting, msg) {
				meeting.start.js = dataUtil.wsToJsDate(meeting.start);
				meeting.end.js = dataUtil.wsToJsDate(meeting.end);
				meetingTotalDuration = meeting.end.js.getTime() - meeting.start.js.getTime();
				refreshRunningStatus();
				meetingWatchers.notify(angular.extend({}, meeting), msg);
			}

			vcabDataSource.meeting.get(meetingId).then(function (meeting) {
				meetingData = meeting;
				onNewMeetingData(meetingData);

				watch(Tables.MEETING, 'sys_id=' + meetingId, function (msg) {
					if (recalTimer)
						$timeout.cancel(recalTimer);
					patchObjWithChanges(meetingData, msg);
					onNewMeetingData(meetingData, msg);
				});
			});

			var recalTimer;
			function recalRunningStatus(forceNotify) {
				var timePerAgenda = meetingTotalDuration / totalAgendaCount;
				var elapsedTime = dataUtil.dateNowAsPerUserProfile() - meetingData.start.js.getTime();
				var howManyShouldHaveBeenDone = Math.floor(elapsedTime / timePerAgenda);
				var howManyActuallyDone = totalAgendaCount - totalPendingAgendaCount - 1; // Removing the in-progress one
				runningCount = howManyActuallyDone - howManyShouldHaveBeenDone;
				if (runningCount !== lastRunningCount || forceNotify)
					agendaCountWatchers.notify(totalAgendaCount, totalPendingAgendaCount, runningCount);
				lastRunningCount = runningCount;

				recalTimer = $timeout(recalRunningStatus, 1000);
			}

			function refreshRunningStatus() {
				$q.all([
			        vcabDataSource.agenda.count('cab_meeting=' + meetingId),
			        vcabDataSource.agenda.count('state=pending^cab_meeting=' + meetingId)
		        ]).then(function (res) {
		        	totalAgendaCount = res[0];
		        	totalPendingAgendaCount = res[1];
		        	recalRunningStatus(true);
		        });
			}

			function getCurrentAgendaAndNotify() {
				vcabDataSource.agenda.query('state=in_progress^cab_meeting=' + meetingId).then(function (agendas) {
					if (agendas.length > 0) { // It is expected that only one agenda will be in in_progress
						refreshAgenda(undefined, agendas[0]);
					}
				});
			}

			function refreshChange (agendaId) {
