angular.module('sn.change_management.cab.calendar').directive('snCabMeetingCalendar', ['CAB', 'modelUtil', 'meetingCalendar', 'spUtil', 'getTemplateUrl', 'CalendarView', '$timeout', '$log', '$compile', '$document', 'filterFilter', '$window', '$location', 'isAccessibilityEnabled', 'calendarUtils', function(CAB, modelUtil, meetingCalendar, spUtil, getTemplateUrl, CalendarView, $timeout, $log, $compile, $document, filterFilter, $window, $location, isAccessibilityEnabled, calendarUtils) {
    return {
        restrict: 'E',
        scope: {
            timeformat: '<',
        },
        templateUrl: getTemplateUrl('cab_workbench_meeting_calendar.xml'),
        link: function($scope, el, attrs, Ctrl) {
            var SPACE = 32;
            var ENTER = 13;
            var ESCAPE = 27;
            var TAB = 9;
            var popoverTimerPromise, resizeTimerPromise;
            var userTzName;
            var isA11yEnabled = isAccessibilityEnabled();
            var popoverScopes = [];
            var eventbarMap = {};
            var eventIndex = 0;
            var activePopoverId = "";

            function destroyPopoverScopes() {
                for (var i = 0; i < popoverScopes.length; i++)
                    popoverScopes[i].$destroy();
                popoverScopes.length = 0;
                eventbarMap = {};
                eventIndex = 0;
            }

            function buildEventTemplate(meeting) {
                return "<span>" + meeting.name.value + "<br />" + meeting.manager.display_value + "</span>";
            }

            function meetingToEvent(meeting) {
                // Just incase start doesn't have a timezone, grab it here on each map.
                if (!userTzName && meeting.start.tz_name)
                    userTzName = meeting.start.tz_name;

                return {
                    id: meeting.sys_id.value,
                    start_date: new Date(meeting.start.display_value_ms),
                    end_date: new Date(meeting.end.display_value_ms),
                    text: buildEventTemplate(meeting),
                    meeting: meeting
                };
            }

            $scope.events = [];
            $scope.ready = false; // When this directive is rendered, it might be within ng-hide. Initing scheduler code then will render it collapsed.
            function renderPopoverEvent(container, ev, width, height, header_content, body_content, templates) {

                var $container = angular.element(container);
                $container.attr("tabindex", 0);
                eventIndex++;
                var eventKey = ev.id + "_" + eventIndex;
                var durationInMins = (ev.meeting.end.value_ms - ev.meeting.start.value_ms) / (1000 * 60);
                // If duration is less than 1 hr, its defaulting to 1 hr in global calendar class. 
                // So handling it here by making default as 15mins 
                // Setting default height to 12 if duration is less than 15 mins.
                var heightModfied = null;
                if (durationInMins <= 15) {
                    height = 12;
                    heightModfied = height;
                } else if (durationInMins < 60) {
                    //44 is the default height for 1hour
                    height = (durationInMins / 60) * 44;
                    heightModfied = height;
                }
                // We are removing padding only if duration is <30 to show the meeting name clearly within the event box.
                var removePadding = '';
                if (durationInMins < 30)
                    removePadding = "padding-top:0px;";
                var popoverScope = recompileEvent(container, eventKey, heightModfied);
                var html = "<div style='overflow: hidden; width: " + width + "px; height: " + height + "px;' >";
                html += "<div class='dhx_event_move dhx_header' style='width:" + width + "px'></div>";
                if ($scope.view != "day")
                    html += "<div class='dhx_event_move dhx_title' style='width:" + width + "px'>" + header_content + "</div>";

                html += "<div class='dhx_body' style='" + removePadding + "width:" + width + "px'>" + body_content + "</div>";
                html += "<div class='dhx_event_resize dhx_footer' style='width:" + width + "px'></div>";
                html += "</div>";
                popoverScope.ev = ev;
                angular.element(container).append(($compile(html))(popoverScope));
                return true;
            }

            $scope.popoverTemplate = "cab_meeting_cal_popover";
            $scope.view = 'month';

            var myPlacement = "auto right";
            $scope.popoverPlacement = function() {
                return myPlacement;
            };

            $scope.config = {
                date: new Date(),
                viewTimeFormat: $scope.timeformat,
                multiDay: true,
                allTimed: true,
                allowCreate: false,
                allowResize: false,
                allowMoving: false,
                readOnly: true,
                supportedModes: [CalendarView.DAY, CalendarView.WEEK, CalendarView.MONTH],
                mode: CalendarView.MONTH,
                useSelectMenuSpace: true,
                renderEvent: renderPopoverEvent,
                timeoutToDisplay: 60000,
                weekStartDay: calendarUtils.getNormalizedWeekStartDay(($window.g_first_day_of_week === undefined || $window.g_first_day_of_week === null || isNaN($window.g_first_day_of_week)) ? 2 : $window.g_first_day_of_week),
                templates: {
                    event_class: function(start, end, ev) {
                        return "cab_event_class";
                    },
                    // Month view time.  Slight hack as the date starts a div, the text ends it.
                    event_bar_date: function(start, end, ev, scheduler) {
                        var html = "<b>" + (scheduler.date.date_to_str($scope.timeformat))(start) + "</b> ";
                        return html;
                    },
                    event_bar_text: function(start, end, ev, scheduler) {
                        return ev.text;
                    },
                    // Week/Day view header
                    event_header: function(start, end, ev, scheduler) {
                        var dateFormat = scheduler.date.date_to_str($scope.timeformat);
                        if (scheduler.getState().mode == CalendarView.WEEK)
                            return "<b>" + dateFormat(start) + " - " + dateFormat(end) + "</b> ";
                        return "";
                    }
                }
            };

            function isReady() {
                if (el.height() === 0) { // Means it is not yet visible
                    $timeout(isReady, 300);
                    return;
                }
                $scope.ready = true;
            }

            isReady();

            var loadingCallsCounter = 0;
            $scope.enableLoading = function() {
                $scope.isLoading = true;
                loadingCallsCounter++;
            };

            $scope.disableLoading = function() {
                if (loadingCallsCounter === 0)
                    return;

                loadingCallsCounter--;
                if (loadingCallsCounter === 0)
                    $scope.isLoading = false;
            };

            $scope.onSchedulerResize = function(schedulerInstance) {

                for (var p in eventbarMap) {
                    if (eventbarMap[p]) {
                        activePopoverId = p;
                        break;
                    }
                }

                destroyPopoverScopes();
                updateEvents();
            };

            function bindGlobalEvents() {
                $document.on("click", handleDocumentClickEvent);
                $document.on("keyup", handleEscape);
                $(".sp-scroll").on("scroll", function() {
                    $scope.$digest();
                });
            }

            function unbindGlobalEvents() {
                $document.off("click", handleDocumentClickEvent);
                $document.off("keyup", handleEscape);
                $(".sp-scroll").off("scroll");
            }

            function clearPopovers(shiftFocus) {
                unbindGlobalEvents();
                var eventKey = "";
                for (var p in eventbarMap) {
                    if (eventbarMap[p])
                        eventKey = p;
                    eventbarMap[p] = false;
                }

                if (!shiftFocus)
                    return;
                if (eventKey.length == 0)
                    return;

                var el = angular.element($document).find("#" + eventKey);

                if (el.length == 0)
                    return;

                el.focus();
            }

            function handleEscape($event) {
                if ($event.keyCode != ESCAPE)
                    return;
                var shiftFocus = angular.element($event.target).closest(".popover").length != 0;
                $scope.$apply(function() {
                    clearPopovers(shiftFocus);
                });
            }

            function handleDocumentClickEvent($event) {
                var $el = angular.element($event.target);
                // return if clicked on popover dialog.
                if ($el.closest(".popover").length != 0)
                    return;
                $scope.$apply(function() {
                    clearPopovers(false);
                });
            }

            $scope.$on("$destroy", function() {
                clearPopovers();
            });

            function togglePopover(eventKey) {
                var isOpen = !eventbarMap[eventKey];
                for (var p in eventbarMap)
                    eventbarMap[p] = false;

                unbindGlobalEvents();
                eventbarMap[eventKey] = isOpen;
                if (!isOpen)
                    return;
                if (popoverTimerPromise)
                    $timeout.cancel(popoverTimerPromise);
                popoverTimerPromise = null;

                popoverTimerPromise = $timeout(function() {
                    bindGlobalEvents();
                    if (!isA11yEnabled)
                        return;
                    var elFocus = angular.element($document).find("#cab_event_dialog_" + eventKey);
                    if (elFocus.length >= 1)
                        elFocus.focus();
                }, 30, false);
            }

            $scope.isPopoverOpen = function(eventKey) {
                if (typeof eventbarMap[eventKey] == 'undefined')
                    eventbarMap[eventKey] = false;
                return eventbarMap[eventKey];
            };

            $scope.onPopoverEventClick = function($event, eventKey) {
                togglePopover(eventKey);
            };

            function updateEvents() {
                if ($scope.view != CalendarView.MONTH)
                    return;
                if (resizeTimerPromise)
                    $timeout.cancel(resizeTimerPromise);
                resizeTimerPromise = undefined;
                resizeTimerPromise = $timeout(function() {
                    var evEl = $document.find("div[event_id]");
                    for (var i = 0; i < evEl.length; i++)
                        recompileEvent(evEl[i], evEl[i].getAttribute("event_id") + "_" + i, null);
                }, 100, false);
            }

            function recompileEvent(el, eventKey, height) {

                var $container = angular.element(el);
                $container.attr("id", eventKey);
                $container.attr("uib-popover-template", "popoverTemplate");
                $container.attr("popover-placement", "{{popoverPlacement()}}");
                $container.attr("popover-trigger", "none");
                $container.attr("popover-append-to-body", "true");
                $container.attr("popover-is-open", "isPopoverOpen('" + eventKey + "')");
                $container.attr("ng-click", "onPopoverEventClick($event, '" + eventKey + "')");
                $container.attr("data-popover-index", eventKey);
                // Here we are modifying the height of the div (its coming default as 40 if duration is less than 1 hr)
                if (height != null) {
                    var len = $container.attr("style").length;
                    $container.attr("style", $container.attr("style").substring(0, len - 5) + height + "px");
                }
                var popoverScope = $scope.$new();
                popoverScope.tabindex = $container.attr("tabindex");
                popoverScope.eventIndex = eventKey;
                popoverScope.cab_a11y = isA11yEnabled ? "cab_a11y" : "";
                popoverScope.ev = filterFilter($scope.events, {
                    id: $container.attr("event_id")
                })[0];
                if (activePopoverId == eventKey) {
                    eventbarMap[activePopoverId] = true;
                    activePopoverId = '';
                } else
                    eventbarMap[eventKey] = false;
                popoverScopes.push(popoverScope);
                $compile($container)(popoverScope);
                return popoverScope;
            }

            $scope.onEventClick = function(eventId) {
                $window.location.href = spUtil.getHost() + $location.path() + '?id=' + CAB.WORKBENCH + '&sys_id=' + eventId;
            };
            $scope.onEventKeydown = function(event, eventId, elementId) {
                if (event.which == ENTER) {
                    $window.location.href = spUtil.getHost() + $location.path() + '?id=' + CAB.WORKBENCH + '&sys_id=' + eventId;
                } else if (event.which == TAB) {
                    togglePopover(eventId);
                    angular.element('#' + elementId).focus();
                    event.preventDefault();
                }
            };

            $scope.onViewChange = function(view) {
                $scope.view = view;
                // Deal with placement of the popover in different views
                if (view == CalendarView.DAY)
                    myPlacement = "auto top";
                else
                    myPlacement = "auto right";

                destroyPopoverScopes();
            };

            $scope.onDateRangeChange = function() {
                destroyPopoverScopes();
            };

            $scope.fetchEvents = function(startDate, endDate) {
                // Start Date and endDate are in the forced users's timezone.  Need to adjust accordingly.
                var startMs = startDate.getTime() - modelUtil.getTzToLocalMSDiff(startDate.getTime(), userTzName);
                var endMs = endDate.getTime() - modelUtil.getTzToLocalMSDiff(endDate.getTime(), userTzName);

                meetingCalendar.getMeetingsBetween(startMs, endMs).then(
                    function(meetings) {
                        $scope.events = meetings.map(function(meeting) {
                            return meetingToEvent(meeting);
                        });
                        //Funky. Should be bound to the render of the month view events.
                        // Also destruction of popover scopes should be bound to the destruction of the event.
                        updateEvents();
                    });
            };

            el.on('keydown', '.cab_event_class', function(event) {
                if (event.which == ENTER) {
                    angular.element(event.target).click();
                }
            });

        }
    };
}]);

var AssessmentClientUtils = {
	populateDefaultFilter: function() {
		var id = g_form.getUniqueValue();
		var table = g_form.getValue('table');
		var condition = getFilter('asmt_metric_type.condition');
		var filterTable = g_form.getValue('filter_table');
		var groupField = g_form.getValue('display_field');
		var filterCondition = g_form.getValue('filter_condition');
		var showAllGroups = this._getDisplayAll();
		var currentDefault = g_form.getValue('default_filter').toString();

		var onResponse = function(serverResponse) {
			var filters = serverResponse.responseXML.getElementsByTagName('group');
			var numFilters = filters.length;

			g_form.clearOptions('default_filter');
			g_form.addOption('default_filter', '-- None --', getMessage('-- None --'));
			for(var i = 0; i < numFilters; i++) {
				var display = filters[i].getAttribute('display');
				var id = filters[i].getAttribute('value');
				g_form.addOption('default_filter', id, display);
				if(currentDefault == id)
					g_form.setValue('default_filter', id);
			}
		};

		var ga = new GlideAjax('AssessmentUtilsAJAX');
		ga.addParam('sysparm_id', id);
		ga.addParam('sysparm_name', 'getFilters');
		ga.addParam('sysparm_base_table', table);
		ga.addParam('sysparm_condition', condition);
		ga.addParam('sysparm_filter_table', filterTable);
		ga.addParam('sysparm_group_field', groupField);
		ga.addParam('sysparm_filter_condition', filterCondition);
		ga.addParam('sysparm_show_all_groups', showAllGroups);
		ga.getXML(onResponse);
	},

	_getDisplayAll: function() {
		// If the field is on the form (even if it is hidden), 
		// return the value of the field
		var displayAllField = $('asmt_metric_type.display_all_filters');
		if (displayAllField)
			return g_form.getValue('display_all_filters');
		// If the field value is on the scratchpad, return that value
		else if (g_scratchpad._loadDisplayAll !== undefined)
			return g_scratchpad._loadDisplayAll;
		// Make an AJAX to get the value of the field
		else {
			var currentSysId = g_form.getUniqueValue().toString();
			var gr = new GlideRecord('asmt_metric_type');
			gr.get(currentSysId);
			return gr.display_all_filters;
		}
	}
};

angular.module("sn.app_common.aisa.action", ["sn.app_common", "sn.app_common.aisa.utils"]);
angular.module("sn.app_common.aisa.action").service("aisaAction", ["$http", "$window", "$location", "$log", "aisaUtils", "aisaActionModalHelper", "aisaFeedback", "i18n", function($http, $window, $location, $log, aisaUtils, aisaActionModalHelper, aisaFeedback, i18n) {
	var aisaAction = this;

	aisaAction.ACTION_ENDPOINT_BASE = '/api/now/aisa_action';
	aisaAction.ACTION_TYPE = {
		'OPEN_LINK_NEW_TAB': 'OPEN_LINK_NEW_TAB',
		'OPEN_LINK_CURRENT_TAB': 'OPEN_LINK_CURRENT_TAB',
		'OPEN_MODAL': 'OPEN_MODAL',
		'REDIRECT': 'REDIRECT',
		'SEARCH_RESULT_EVENT_SIGNAL': 'SEARCH_RESULT_EVENT_SIGNAL',
	};
	aisaAction.ACTIONS = {
		'SOLVES_ISSUE': 'Solves my issue'
	};

	aisaAction.execAction = function(action, resultRecord, commonFeedbackData, isModalSubmit, modalData, domain, rpSysId) {
		var table = resultRecord.table;
		var sysId = resultRecord.sysId;

		var className = aisaUtils.getResultField(resultRecord, "sys_class_name").value;
		if (!className)
			className = table;

		var restPath = action.restPath;
		if (!restPath.startsWith('/'))
			restPath = '/' + restPath;

		var actionRequest = {
			method: 'POST',
			url: aisaAction.ACTION_ENDPOINT_BASE + restPath,
			headers: {
				'Content-Type': "application/json; charset=UTF-8"
			},
			data: {
				table: table,
				sysId: sysId,
				className: className,
				commonFeedbackData: commonFeedbackData,
				isModalSubmit: isModalSubmit,
				modalData: modalData
			}
		};

		$http(actionRequest).then(
			function(response) {
				var result = response.data.result;
				var actionType = result.actionType;
				var forceRedirect = result.forceRedirect;

				if (aisaAction.ACTION_TYPE.OPEN_LINK_NEW_TAB === actionType) {
					aisaFeedback.sendSearchCompleteAppSeeEvent(resultRecord, rpSysId, result.linkValue, action.actionId, domain);
					$window.open(result.linkValue, '_blank');
				} else if (aisaAction.ACTION_TYPE.OPEN_LINK_CURRENT_TAB === actionType) {
					aisaFeedback.sendSearchCompleteAppSeeEvent(resultRecord, rpSysId, result.linkValue, action.actionId, domain);
					if (forceRedirect) {
						g_dirty_form_warning_enabled = false;
						// using open will do a refresh to reset g_dirty_form_warning_enabled
						$window.open(result.linkValue, '_self');
					} else
						$location.search(result.linkValue);
				} else if (aisaAction.ACTION_TYPE.OPEN_MODAL === actionType) {
					var actionModal = {};
					actionModal.result = resultRecord;
					actionModal.action = action;
					actionModal.title = result.modalTitle || action.actionLabel;
					actionModal.bodyTemplate = result.modalBodyTemplate;
					actionModal.submitText = result.modalSubmitText || i18n.getMessage('Submit');
					actionModal.cancelText = result.modalCancelText || i18n.getMessage('Cancel');
					// modalData is for custom usage of modal
					actionModal.modalData = result.modalData;
					actionModal.modalSubmit = function() {
						aisaAction.execAction(this.action, this.result, commonFeedbackData, true, this.modalData, domain, rpSysId);
					};
					aisaActionModalHelper.showActionModal(actionModal);
				} else if (aisaAction.ACTION_TYPE.SEARCH_RESULT_EVENT_SIGNAL === actionType) {
					var searchTerm = commonFeedbackData.queryTerm;
					var rpConfig = {searchApp: commonFeedbackData.searchContextConfigId};
					var signalType = result.signalType;
					var signalValue = result.signalValue;
					aisaFeedback.searchResultEvent(searchTerm, rpConfig, resultRecord, signalType, signalValue);
					aisaFeedback.sendActionClickAppSeeEvent(resultRecord, rpSysId, {
						actionId: action.actionId,
						actionValue: signalValue
					}, domain);
				}
			},
			function(response) {

			}
		);
	};
}]);

angular.module("sn.app_common.aisa.action").factory('aisaActionModalHelper', ["$uibModal", "aisaUtils", function ($uibModal, aisaUtils) {
	"use strict";
	return {
