var PwdWfManager = Class.create();

PwdWfManager.prototype = {
  wfResult : {bPass : false, msg : ""},
  requestId : "",
  processId : "",  
  // Error Message to display in case of workflow errors
  errorMsg : "",
  startTime : "",
  wfCheckDoneFrequency : 500,
  wfTimeOut : 300000,
  wfContextSysId : "",

  initialize : function (_wfCheckDoneFrequency, _wfTimeOut) {
    this.wfCheckDoneFrequency = _wfCheckDoneFrequency;
    this.wfTimeOut = _wfTimeOut;
  },

  startWfViaAjax : function(processId, requestId, password) {
    this.requestId = requestId;
    this.processId = processId;
    this.startTime = new Date().getTime();

    var ga = new GlideAjax('PwdAjaxWFRequestProcessor');

    // These 4 params are required.
    ga.addParam('sysparm_name', 'startWorkflow');
    ga.addParam('sysparam_request_id', requestId);
    if (password != undefined) {
      ga.addParam('sysparam_new_password', password);
    }
    ga.addParam('sysparam_pwd_csrf_token', findCSRFElement().value);
    ga.getXML(this.callBackForStartWf.bind(this));
  },

  callBackForStartWf : function(response, args) {
    //Let's handle the security.
    handleSecurityFrom(response);

    // reponse message. this always exists.
    var res =  response.responseXML.getElementsByTagName("response");
    var status = res[0].getAttribute("status");
    var message = res[0].getAttribute("message");
    this.wfContextSysId = res[0].getAttribute("value");
    if (status.match(/failure/i)) {
      //this._onError("An error occured in PwdWfManager UI Script callBackForStartWf(): " + message);
		this._onError("TIMEOUT");
      return;
    }

    this.setTimeoutForRefresh();
  },

  setTimeoutForRefresh : function() {
    window.setTimeout(this.checkWFProgress.bind(this), this.wfCheckDoneFrequency);
  },

  checkWFProgress : function() {
    var currTime = new Date().getTime();
    if (currTime - this.startTime > this.wfTimeOut) {
      this._onError("TIMEOUT");
      return;
    }

    var ga = new GlideAjax('PwdAjaxWFRequestProcessor');
    ga.addParam('sysparm_name', 'getWorkflowStatus');
    ga.addParam('sysparam_wf_context_sys_id', this.wfContextSysId);
    // csrf token
    ga.addParam('sysparam_pwd_csrf_token', findCSRFElement().value)
    ga.getXMLWait();

    handleSecurityFrom(ga.requestObject);
    var state = ga.getAnswer();

    if (!state.match(/finished/i)) {
      this.setTimeoutForRefresh();
      return;
    } else if (state.match(/failure/i)) {
      this._onError("An error occured in PwdWfManager UI Script : checkWFProgress()");
      return;
    }

	var gaResult = new GlideAjax('PwdAjaxWFRequestProcessor');
    gaResult.addParam('sysparm_name', 'getWorkflowResult');
    gaResult.addParam('sysparam_wf_context_sys_id', this.wfContextSysId);

    // csrf token
    gaResult.addParam('sysparam_pwd_csrf_token', findCSRFElement().value);
    gaResult.getXML(this.callBackForGetWfContext.bind(this));
  },


  callBackForGetWfContext : function(response) {
    handleSecurityFrom(response);

    this._alert("client: callBackForGetWfContext triggered!");

    var history = response.responseXML.getElementsByTagName('history');
    var names = response.responseXML.getElementsByTagName('activity');

    var bAllActivitiesSucceeded = true;

    // An array of obj representing the state of an activity
    var allActivitiesObj = [];


    // Grab all the activities in the workflow and build an array of objects
    for (var ii=0; ii<history.length; ii++) {
      var tmp = {};
      tmp.name = names[ii].getAttribute("name");
      tmp.index = history[ii].getAttribute("activity_index");

      tmp.result = history[ii].getAttribute("activity_result");
      if (tmp.result == undefined) {
        tmp.result = "";
      }

      tmp.faultDescription = history[ii].getAttribute("fault_description");
      if (tmp.faultDescription == undefined) {
        tmp.faultDescription = "";
      }

      allActivitiesObj.push(tmp);

      this._alert("Adding activity: " + "index=" + tmp.index + " : name=" + tmp.name + " : result=" + tmp.result + " : faultDescription=" + tmp.faultDescription);
    }

    // Iterate through activity history list to find the first error
    allActivitiesObj.sort(function(a, b) { return b.index - a.index; });
    var failingActivityObj = {};
    for (var jj in allActivitiesObj) {
      var eachObj = allActivitiesObj[jj];

      if (eachObj.result == undefined)
        continue;

      if (eachObj.result.match(/failure/i)) {
        bAllActivitiesSucceeded = false;
        this._alert("Found an error in activity history -> " + eachObj.name);

        // Check if faultDescription is an json object. If so, this was created via SNC.PwdWorkflowManager().creatError().
        try {
          failingActivityObj = eval("(" + eachObj.faultDescription + ")");
          this._alert("Fault description created via new SNC.PwdWorkflowManager().getErrorJSONString(...): \n* wfName=" + failingActivityObj.wfName + "\n* activityName=" + failingActivityObj.activityName + " \n* message=" + failingActivityObj.message + " \n* isFatal=" + failingActivityObj.isFatal);
        } catch (e) {
          //The faultDescription was created manually, and not via new SNC.PwdWorkflowManager().getErrorJSONString(...)
          this._alert("Fault description created manually");
          failingActivityObj.message = eachObj.faultDescription;
          failingActivityObj.isFatal = true;
        }
        break;
      }
    }

    try {
      var res =  response.responseXML.getElementsByTagName("response");
    } catch (error) {
      alert("PwdWfManager caught exception! " + error.message);
    }
    var wfGeneralStatus = res[0].getAttribute("status");
    var message = res[0].getAttribute("message");
    var value = res[0].getAttribute("value");

    if (!wfGeneralStatus.match(/success/i) || (!bAllActivitiesSucceeded)) {
      //Check failure via by workflow result
      this.wfResult.bPass = false;
      this.wfResult.msg = "Password reset request failed.";
    } else { //pass
      this.wfResult.bPass = true;
    }

    this._alert ("Final: WFContext results:\n bPass=" + this.wfResult.bPass + " \n msg=" + this.wfResult.msg);

    if (this.wfResult.bPass) {
      this._postProcessSuccess();
    } else {
      if (failingActivityObj) {
        if (!failingActivityObj.isFatal) {
          var msg = "non fatal failure";
		  this._alert(msg);

          submitWithOKAndSysParam('pwd_new.do','sysparm_page_text', msg);

        } else {
          this._postProcessError(this.wfResult.msg);
        }
      } else {
        this._postProcessError(this.wfResult.msg);
      }
    }
  },

  _postProcessSuccess : function() {
    var ga = this._createPostProcessAjax("true");
    ga.getXML(this._onSuccess.bind(this));
  },

  _postProcessError : function(msg) {
    this.errorMsg = msg;
    var ga = this._createPostProcessAjax("false");
    // Need to use the callback because of the parameter errorMsg
    ga.getXML(this.callBackForPostProcessOnError.bind(this));
  },

  callBackForPostProcessOnError : function(response) {
    this._onError(this.errorMsg);
  },

  _createPostProcessAjax : function(isSuccess) {
    var ga = new GlideAjax('PwdAjaxWFRequestProcessor');
    ga.addParam('sysparm_name', 'runPostProcessor');
    ga.addParam('sysparam_request_id', this.requestId);
    ga.addParam('sysparam_process_id', this.processId);
    ga.addParam('sysparam_workflow_status', isSuccess);
    ga.addParam('sysparam_pwd_csrf_token', findCSRFElement().value);
    return ga;
  },

  _alert : function(msg) {
    var debug = false;
    if (debug) {
      alert(msg);
    }
  },
  _onError:function(errorReason) {
    submitWithBlock('pwd_error.do',errorReason);
  },
  _onSuccess:function(response) {
    handleSecurityFrom(response);
    submitWithOKAndSysParam('pwd_success.do','sysparm_success','SUCCESS');
  },

  type : "PwdWfManager"
};

/**
 * Onload function for pwd_enroll_question_ui macro.  
 * @param currForm
 */
function questions_and_answers_on_load(formId) {
    var currForm = document.getElementById(formId);

    if(currForm=='undefined')
      return;

    var mode = currForm.elements['mode'].value;

 	// If mode is create, all the selects will have 'Select a question'
  	// so disable the answer fields
    if (mode == 'create') {
        var allSelectElements = getAllSelectElements(currForm);
        for (var index = 0; index < allSelectElements.length; index++) {
            var currForm = allSelectElements[index].form;
            var answerElm = currForm.elements['answer_' + (index+1)];
            answerElm.disabled = true;
        }
        return;
     }

    // get the current verification Id.
    var verificationId = currForm.elements['verification_id'].value;
    // Get the stored questions .
    var storedQuestions = eval('getAllStoredQuestions_'+verificationId+'()');
    var storedAnswers = eval('getAllStoredAnswers_'+verificationId+'()');
    var allSelectElements = getAllSelectElements(currForm);

    // let's decide the size of the selects.
    var selectSize = storedQuestions.length;

    if (allSelectElements.length > storedQuestions.length) {

        // if # of select elements is > what are stored, 
        // the required # of questions has changed.

        displayEnrollAgainMessage();


    } else if (allSelectElements.length < storedQuestions.length) {

        // the size of questions got smaller for some reason.
        displayEnrollAgainMessage();
        selectSize = allSelectElements.length;
    }

    var allQuestions = eval('getAllQuestions_'+verificationId+'()');
    var allStoredAnswerSysIdElements = getAllStoredAnswerSysIdElementsFrom(currForm);
    var storedAnswerSysIds = eval('getAllStoredAnswerSysIds_'+verificationId+'()');

    for (var i = 0; i < selectSize; i++) {

        var storedQuestion = storedQuestions[i];

        // Get the stored answer Id
        var storedAnswersysId = storedAnswerSysIds[i];

        // assign the stored sys id to each element. 
        var sysIdElm = allStoredAnswerSysIdElements[i];
        sysIdElm.value = storedAnswersysId;

        //Let's find the stored question from the all questions.
        var index = getIndex(allQuestions,storedQuestion);

        // should not happen. can't find the stored question.
        if (index == -1) continue;

        // Once found, let's set selected index.
        var currElm = allSelectElements[i];
        currElm.options.selectedIndex = index+1;

        // update  the answer value.
        var answer = storedAnswers[i]; 
        currForm.elements['answer_'+(i+1)].value=answer;

    };
    // Let's remove selected ones from the main question list. 
    for (var i = 0; i< allSelectElements.length; i++) {
    var currElm = allSelectElements[i];
    _changeList(i,currElm,false);
  }

  // Load submit event handler.
  //setSubmitEventHanlder();
}

function displayEnrollAgainMessage(){
  displayErrorMessage(getMessage("The number of questions required for enrollment has changed. Enroll again."));  
}
/**
 * Removes the selected ones from the main question list.
 */
function changeList(index, currElm) {
	var currForm = currElm.form;
    var answerElm = currForm.elements['answer_' + index];
    // If 'Select a question' option is selected, disable the answer field
    if (currElm.value == 'N/A' || currElm.value == '') {
        answerElm.value = '';
        answerElm.disabled = true;
    } else {
        answerElm.disabled = false;
        _changeList(index, currElm, true);
    }
 }
/**
* Builds the question list with removing the already selected ones from the main question list.
*/
function _changeList(index, currElm, clearNotStoredAnswer) {
    // current form.
    var currForm = currElm.form;

  // verification Id
    var verificationId = currForm.elements['verification_id'].value;

  //Removes the value if exists.
    if (clearNotStoredAnswer) {

        var storedQuestions = eval('getAllStoredQuestions_'+verificationId+'()');
        var currentQuestion = currElm.value; 

        var metIndex = getIndex(storedQuestions,currentQuestion); 
        var answerElm = currForm.elements['answer_'+index];

        //If the current question is the stored question, show the stored answer.
        //If the not the stored answer, clear the answer.
        if (metIndex != -1) {
            var storedAnswers = eval('getAllStoredAnswers_'+verificationId+'()');
            answerElm.value = storedAnswers[metIndex];
        } else {
            answerElm.value='';
        }
    }

    // get all surrounding elements except for the current.
    var otherSelectElms = getOtherSelectElements(currElm);

    // traverse other select elements.
    for (var i = 0; i != otherSelectElms.length; i++) {

        var anotherElm  = otherSelectElms[i];
        // if the question is already selected, then throw an error. 
        var anotherQuestion = anotherElm.value;

        if (anotherQuestion != "N/A" && anotherQuestion == currElm.value) {
            displayErrorMessage(getMessage("The same question was already selected. Select a different question."));
            currElm.options.selectedIndex = 0;
            return;
        }
        // If not the same question, then let's rebuild other select lists. 
        rebuildQuestionList(anotherElm,verificationId);
    }
}
/**
 * Mark  the value of the passed element has changed.
 */
function markChanged(index,currElm) {

    var currForm = currElm.form;
    var verificationId = currForm.elements['verification_id'].value;
    var questionElm = currForm.elements['question_'+index]; 
    var question = questionElm.value;
    var storedQuestions = eval('getAllStoredQuestions_'+verificationId+'()');
    var metIndex = getIndex(storedQuestions,question);

    // this is a new question, then the status "true" and return.
    if (metIndex == -1) {
        currForm.elements['changed_'+index].value='true';
        return;
    }

    var storedAnswers = eval('getAllStoredAnswers_'+verificationId+'()');
    var storedAnswer = storedAnswers[metIndex]; 
    var answer = currElm.value;

    // let's validate one more....
    // if every character is "*" which is masked value, do not take it..
    var otherCharsFound = false;

    for (var i = 0; i != answer.length; i++) {

        var ch = answer.charAt(i); 
        if (ch!='*') {
            otherCharsFound = true;
            break;
        }
    }

    if (otherCharsFound && answer != storedAnswer) {
        currForm.elements['changed_'+index].value=true;
    }
} 
/**
 * Rebuilds the question list. While rebuilding, removes the questions selected by other select elements.
 */
 function rebuildQuestionList(currentElement, verificationId) {


  // get other select elements that exxcludes the current one.
  var otherSelectElements = getOtherSelectElements(currentElement);

     // Get all available questions. 
     var allQuestions = eval('getAllQuestions_'+verificationId+'()');

     // Get the selected questions for other select elements.
     var otherSelectedQuestions = getSelectedQuestions(otherSelectElements);

     // current question.
     var selectedQuestion = currentElement.value;

     // shrink the list to the first option. the link will be rebuit now.
     currentElement.options.length=1;

     // traverse all questions and remove the selected questions by other select elements.
     var index=0;

     for (var i = 0; i < allQuestions.length; i++) {

         var question = allQuestions[i];

         // if the question is  selected question, then remove the option from the list.
         if(getIndex(otherSelectedQuestions,question) != -1)
            continue;

         index++;
         currentElement.options[index]=new Option(question, question);

         if(selectedQuestion!="N/A" && question==selectedQuestion)
          currentElement.options.selectedIndex = index;

     }
 }
 /*
 * Returns the elements that have the name "stored_sys_id". 
 * The returned elements contain the stored answer sys Id.
 */
 function getAllStoredAnswerSysIdElementsFrom(currForm) {
     var arr = [];
     for(var i = 0; i != currForm.elements.length; i++) {
         var elm= currForm.elements[i];
         var name = elm.name;

         if(name.indexOf("stored_sys_id_") == -1) continue;

         arr.push(elm);
     }
     return arr;
 }
 /*
  * Find all select elements except for the passsed one from the form.
  */
  function getOtherSelectElements(currElm) {

    var currForm = currElm.form;
      var arr = [];
      for(var i = 0; i != currForm.elements.length; i++) {
          var elm= currForm.elements[i];
          var name = elm.name;

          if(name.indexOf("question_") == -1) continue;
          if(name.indexOf(currElm.name) != -1 ) continue;

          arr.push(elm);
      }
      return arr;
  }
  /**
   * Returns the index of the first occurrence from the array.
   * @param arr
   * @param val
   * @returns {Number}
   */
  function getIndex(arr, val) {
    for(var i = 0; i != arr.length; i++ ) {
        if(arr[i] == val) return i;
    }
    return -1;
  }
  /**
  * Returns an array of questions. The return array excludes the currently selected question. 
  */
  function getSelectedQuestions(elements) {
    var arr = [];
    for(var i = 0; i != elements.length ;i++) {
          // push other selected question into the array. 
          var question = elements[i].value;
          if(question == "N/A") continue;
          arr.push(question);
      }
      return arr;
    } 
  /**
   *Tests if the passed question has the same occrence 
   */
   function getStoredQuestionIndex(question, verificationId) {

        var storedQuestions = eval('getAllStoredQuestions_'+verificationId+'()');
        return getIndex(storedQuestions, question);
   } 
   /**
   * Validate the form before submit.
   */
   function questions_and_answers_on_submit(formId) {

        var currForm = document.getElementById(formId);

        if(currForm=='undefined')
          return false;

        var selects = getAllSelectElements(currForm);
        var answers = getAllAnswerElements(currForm);

        //We need to traverse all the answers and call markChanged method 
        //because some browsers (i.e. safari on ipad) have different behaviors for certain events.
        //markChanged function marks the answer dirty if the answer is changed by the user. 
        for(var i=0; i<answers.length; i++){
            	var answer = answers[i];

	        //make sure i+1 is getting passed. It's a question indenx starting with 1.
        	markChanged(i+1,answer);
        }

        for(var i = 0; i< selects.length ; i++) {
            var select = selects[i];
            if(select.value == 'N/A') {
                displayErrorMessage(getMessage("You need to choose " + selects.length + " questions"));
                return false;
            }
            var answer = answers[i];
            if(answer.value.trim().length == 0) {
                displayErrorMessage(getMessage("All questions should be answered"));
                answer.focus();
                return false;
            }
        }
        return true;
    }
    /*
    * Find all select elements from the form.
    */
    function getAllSelectElements(currForm) {
        var arr = [];
        var elements = currForm.elements;

        for(var i = 0; i < elements.length ; i++) {
            var element= elements[i];
            var name = element.name;

            if(name.indexOf("question_") == -1) continue;
            arr.push(element);
        }
        return arr;
    }
    /*
