        for (var i = 0; i < items.length; i++) {
            var obj = items[i];
            var listItem = $j('<div>').addClass('list-item').attr('tabindex', '0').data({
                id: obj.id,
                label: obj.label
            }).appendTo(columnDiv);
            addEditImg(listItem);

