function checkStartDate() {
	_checkDates('startDate');
}

function checkEndDate() {
	_checkDates('endDate');
}

function _checkDates(dateField) {
	if (!dateField || !['startDate', 'endDate'].includes(dateField)) {
		console.error('Invalid _checkDates argument dateField: ' + dateField);
		return;
	}

	var noLaterThanToday = 'Please select a date no later than today';
	var currentDate = new Date();
	var datesInSystemFormat = convertDatesToSystemFormat(gel('startDate').value, gel('endDate').value);
	var startDate = new Date(datesInSystemFormat[0] + 'T00:00');
	var endDate = new Date(datesInSystemFormat[1] + 'T00:00');
	var dateRange = gel('date_range').value;

	if (dateField == 'startDate') {
		if (endDate < startDate)
			gel('endDate').value = gel('startDate').value;

		if (currentDate < startDate) {
			alert(new GwtMessage().getMessage(noLaterThanToday));
			gel('startDate').value = new Date().toJSON().slice(0,10);
			gel('endDate').value = new Date().toJSON().slice(0,10);
		}
	} else {
		if (endDate < startDate) {
			alert(new GwtMessage().getMessage("Please select a date after start date"));
			gel('endDate').value = gel('startDate').value;
		}

		if (currentDate < endDate) {
			alert(new GwtMessage().getMessage(noLaterThanToday));
			gel('endDate').value = gel('startDate').value;
		}
	}

	if (OutOfDateRange(endDate, startDate, dateRange)) {
		alert(new GwtMessage().getMessage("Please select a date within {0} days", dateRange));
		gel('endDate').value = gel('startDate').value;
	}

	gel('date').value=gel(dateField).value;
}

function checkDate() {
	var currentDate = new Date();
	var dateInSystemFormat = convertDatesToSystemFormat(gel('date').value);
	var date = new Date(dateInSystemFormat[0]);
	var singleDate = gel('single_date').value;
	if (OutOfDateRange(currentDate, date, singleDate)) {
		alert(new GwtMessage().getMessage("Please select a date within {0} days", singleDate));
		gel('date').value = new Date().toJSON().slice(0,10);
	}
	if (currentDate < date) {
		alert(new GwtMessage().getMessage("Please select a date no later than today"));
		gel('date').value = new Date().toJSON().slice(0,10);
	}
	gel('startDate').value=gel('date').value;
	gel('endDate').value=gel('date').value;
}

//check whether the select date is within the date range that defined in GlideProperty
function OutOfDateRange(currentDate, date, dateRange) {
	return currentDate - date > (dateRange * 24 * 60 * 60 * 1000);
}

function scheduleCreation() {
	var select = getNotification();
	if (select === 'email') {
		var email = gel('emailAddress').value;
		if (!email) {
			alert(new GwtMessage().getMessage("Please specify an email address"));
			return false;
		}
	} else
		gel('emailAddress').value = '';

	if (downloadingInProcess())
		alert(new GwtMessage().getMessage("Another similar download is already in progress, please try later."));
	else
		schedule();

	return false;
}

function getNotification() {
	var notification = document.getElementsByName('notify');
	for (var i=0; i < notification.length; i++)
		if (notification[i].checked)
			return notification[i].value;

}

function downloadingInProcess() {
	var gr = new GlideRecord('sys_execution_tracker');
	gr.addQuery('name', 'Uploading Logs from Selected Nodes');
	gr.orderByDesc('sys_created_on');
	gr.setLimit(1);
	gr.query();
	if (!gr.next())
		return false;

	return gr.getValue('state') === '1';
}

function schedule() {
	var ga = new GlideAjax('ScheduleNodesLogsDownload');
	ga.addParam('sysparm_name','schedule');
	ga.addParam('sysparm_selected_sys_ids', getSelectedNode());
	ga.addParam('sysparm_email', gel('emailAddress').value);
	ga.addParam('sysparm_protocol', window.location.protocol);
	ga.addParam('sysparm_host', window.location.host);

	var dateRange = getDateRange(gel('startDate').value, gel('endDate').value);
	ga.addParam('sysparm_date_range', dateRange);

	if (gel('emailAddress').value === '')
		ga.getXML(redirect);

	else {
		ga.getXML();
		alert(new GwtMessage().getMessage(formatMessage('An email will be sent to {0} upon completion.', gel('emailAddress').value)));
		GlideModal.get().destroy();
	}
}

function getDateRange(startDate, endDate){
	var datesInSystemFormat = convertDatesToSystemFormat(startDate, endDate);
	startDate = datesInSystemFormat[0];
	endDate = datesInSystemFormat[1];
	var dateArray = [];
	var currentDate = startDate;
	while (currentDate <= endDate) {
		dateArray.push(currentDate);
		var oneDayAfter = new Date(new Date(currentDate).getTime() + 24*60*60*1000);
		currentDate = oneDayAfter.toJSON().slice(0,10);
	}
	return dateArray.join(';');
}

function convertDatesToSystemFormat(startDate, endDate) {
	var todayDate = new Date().toJSON().slice(0,10);
	var datesArray = [];
	// If startDate or endDate is today's date then there is no need to convert them into system's format.
	if(startDate == todayDate && endDate == null){
		datesArray.push(startDate);
		return datesArray;
	}
	else if (startDate != todayDate && endDate == null){
		var res = makeAjaxCallToConvertDates(startDate);
		datesArray.push(res.start_date);
		return datesArray;
	}
	else if (startDate == todayDate && endDate == todayDate){
		datesArray.push(startDate);
		datesArray.push(endDate);
		return datesArray;
	}
	else if (startDate != todayDate && endDate == todayDate){
		var res = makeAjaxCallToConvertDates(startDate);
		datesArray.push(res.start_date);
		datesArray.push(endDate);
		return datesArray;
	}
	else if (startDate != null && endDate != null){
		var res = makeAjaxCallToConvertDates(startDate, endDate);
		datesArray.push(res.start_date);
		datesArray.push(res.end_date);
		return datesArray;
	}
}

function makeAjaxCallToConvertDates(startDate, endDate){
	var ga = new GlideAjax('DownloadLogFromNodesUtil');
	ga.addParam('sysparm_name', 'convertDatesToSystemFormat');
	ga.addParam('sysparm_start_date', startDate);
	ga.addParam('sysparm_end_date', endDate);
	ga.getXMLWait();
	return JSON.parse(ga.getAnswer());
}

function redirect(response) {
	var tableSysID = response.responseXML.documentElement.getAttribute("answer");
	if (tableSysID === undefined)
		return;

	window.location.href = window.location.protocol + "//" + window.location.host + '/node_log_download_info.do?sys_id=' + tableSysID;
}

function SkippedRecordRuleEngineUtils() {

 // for List view
    function runSelectedActiveRulesOnLastUpgrade() {
		var activeRuleIds = getListOfSelectedActiveRules();
		var title = getMessage('Are you sure you want to run rule(s)?');
		var param = new GwtMessage().getMessage('RunRules');
		showConfirmDialogAndTriggerRule(title, param, activeRuleIds, null, true);
    }

// for Form view
   function runRuleOnLastUpgrade(ruleId) {
		var title = getMessage('Are you sure you want to run rule?');
		var param = new GwtMessage().getMessage('RunNow');
		showConfirmDialogAndTriggerRule(title, param, ruleId, null, true);
    }

// for system upgrade
     function runActiveRulesOnGivenUpgradeHistoryId(upgradeHistorySysId) {
		var title = getMessage('Are you sure you want to run rule(s)?');
		var param = new GwtMessage().getMessage('RunRules');
		showConfirmDialogAndTriggerRule(title, param, null, upgradeHistorySysId, false);
    }

    function showConfirmDialogAndTriggerRule(title, param, ruleIds, upgradeHistorySysId, showUpgradeLink) {
      var gm = new GlideModal('skipped_record_run_rules_confirm_modal');
		gm.setTitle(title);
		gm.setPreference('sysparm_message', param);
		gm.setPreference('sysparm_showlink', showUpgradeLink);

		gm.setWidth(450);
		gm.on('beforeclose', function() {
        if ($j('#confirmModalAction').val() == 'execute') {
                triggerRule(ruleIds, upgradeHistorySysId);
			}
		});
		gm.render();
    }


    function triggerRule(ruleIds, upgradeHistorySysId) {
        var ga = new GlideAjax('SkippedRecordRuleUtil');
		ga.addParam('sysparm_name', 'triggerRules');
		if(ruleIds != null)
			ga.addParam('sysparm_sysIds', ruleIds);

		if(upgradeHistorySysId != null)
			ga.addParam('sysparm_upgradeHistorySysId', upgradeHistorySysId);
		ga.getXML(handleTriggerRulesResponse);
    }


	function getListOfSelectedActiveRules() {
		var selSysIds = g_list.getChecked();
		var sysIdList;
		if(selSysIds && selSysIds.length > 0) {
			sysIdList = selSysIds.split(',');
		}
		var activeRuleSysIds = [];
		var gr = new GlideRecord('sys_upgrade_skipped_record_rule');
		if(sysIdList && sysIdList.length > 0)
			gr.addQuery('sys_id','IN',sysIdList);
		gr.addQuery('active', true);	
		gr.query();

		while (gr.next()) {
			activeRuleSysIds.push(gr.getValue("sys_id"));
		}
		return activeRuleSysIds;
    }

	function handleTriggerRulesResponse(response) {
		var result = JSON.parse(response.responseXML.documentElement.getAttribute('answer'));
		if (result && result.errorMessage) {
			showErrorMessage(result.errorMessage);
			return;
		}

		if(result && result.progressId)
			renderProgressViewer(new GwtMessage().getMessage('Rules Execution Progress'), result.progressId);
	}

	function showErrorMessage(errorMessage) {
		var glideForm = typeof g_form === 'undefined' ? new GlideForm() : g_form; // support for list and form
		glideForm.addErrorMessage(errorMessage);
	}


	function renderProgressViewer(title, progressId) {
		var dialogClass = window.GlideModal ? GlideModal : GlideDialogWindow;
		var modal = new dialogClass('hierarchical_progress_viewer', false, '40em', '10.5em');

		modal.setTitle(title);
		modal.setPreference('sysparm_renderer_execution_id', progressId);
		modal.setPreference('sysparm_button_close', new GwtMessage().getMessage('Close'));
		modal.setPreference('sysparm_renderer_expanded_levels', '0');

		modal.on('bodyrendered', function() {
			new UpgradePlanProgressViewerUtil().disableModalCloseButton();
		});

		modal.on('executionComplete', function() {
			new UpgradePlanProgressViewerUtil().updateModalButtons(modal);
		});

		modal.render();
	}

    return {
        runRuleOnLastUpgrade: runRuleOnLastUpgrade,
        runActiveRulesOnGivenUpgradeHistoryId: runActiveRulesOnGivenUpgradeHistoryId,
        runSelectedActiveRulesOnLastUpgrade: runSelectedActiveRulesOnLastUpgrade,
        type: 'SkippedRecordRuleEngineUtils'
    };
}

angular.module("sn.change_management.cab.watcher", ['ng.amb'])
.service("cabWatcher",["$rootScope", "amb", "$timeout", function($rootScope, amb, $timeout) {
	var cabWatcher = this;  //Convenience

	var ambChannel = null;
	var mId = null;
	var cbFunc = [];


	function subscribeCallback(callbackFunc) {
		if (callbackFunc && typeof callbackFunc === "function")
			cbFunc.push(callbackFunc);
	}

	function unsubscribeCallback(callbackFunc) {
		var funcIdx = cbFunc.indexOf(callbackFunc);

		if (funcIdx !== -1)
			cbFunc.splice(funcIdx,1);
	}	

	function executeCallbacks(response) {
		cbFunc.forEach(function(callbackFunc) {
			callbackFunc(response);
		});
	}

	function initChannel(meetingSysId) {
		ambChannel = amb.getChannel("/cab/" + meetingSysId);
		ambChannel.subscribe(executeCallbacks);
		amb.connect();

		subscribeCallback(function (message) {
			$rootScope.$broadcast("cab.state.change", message.data);
		});

		return meetingSysId;
	}

	function clearChannel() {
		mId = null;
		ambChannel.unsubscribe();
		ambChannel = null;
	}

	cabWatcher.watch = function(meetingSysId) {
		if (!meetingSysId)
			return null;

		if (mId !== null && meetingSysId !== mId)
			clearChannel();

		if (mId === null)
			mId = initChannel(meetingSysId);

		return {
			"subscribe": subscribeCallback,
			"unsubscribe": unsubscribeCallback
		};
	};
}]);

var lookup = [];
var revLookup = [];
var Arr = typeof Uint8Array !== 'undefined' ? Uint8Array : Array;

var code = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/';
for (var i = 0, len = code.length; i < len; ++i) {
    lookup[i] = code[i];
    revLookup[code.charCodeAt(i)] = i;
}

// Support decoding URL-safe base64 strings, as Node.js does.
// See: https://en.wikipedia.org/wiki/Base64#URL_applications
revLookup['-'.charCodeAt(0)] = 62;
revLookup['_'.charCodeAt(0)] = 63;

function getLens(b64) {
    var len = b64.length;

    if (len % 4 > 0) {
        throw new Error('Invalid string. Length must be a multiple of 4');
    }

    // Trim off extra bytes after placeholder bytes are found
    // See: https://github.com/beatgammit/base64-js/issues/42
    var validLen = b64.indexOf('=');
    if (validLen === -1)
        validLen = len;

    var placeHoldersLen = validLen === len ? 0 : 4 - (validLen % 4);

    return [validLen, placeHoldersLen];
}

// base64 is 4/3 + up to two characters of the original data
function byteLength(b64) {
    var lens = getLens(b64);
    var validLen = lens[0];
    var placeHoldersLen = lens[1];
    return ((validLen + placeHoldersLen) * 3 / 4) - placeHoldersLen;
}

function _byteLength(b64, validLen, placeHoldersLen) {
    return ((validLen + placeHoldersLen) * 3 / 4) - placeHoldersLen;
}

function toByteArray(b64) {
    var tmp;
    var lens = getLens(b64);
    var validLen = lens[0];
    var placeHoldersLen = lens[1];

    var arr = new Arr(_byteLength(b64, validLen, placeHoldersLen));

    var curByte = 0;

    // if there are placeholders, only get up to the last complete 4 chars
    var len = placeHoldersLen > 0 ? validLen - 4 : validLen;

    var i;
    for (i = 0; i < len; i += 4) {
        tmp =
            (revLookup[b64.charCodeAt(i)] << 18) |
            (revLookup[b64.charCodeAt(i + 1)] << 12) |
            (revLookup[b64.charCodeAt(i + 2)] << 6) |
            revLookup[b64.charCodeAt(i + 3)];
        arr[curByte++] = (tmp >> 16) & 0xFF;
        arr[curByte++] = (tmp >> 8) & 0xFF;
        arr[curByte++] = tmp & 0xFF;
    }

    if (placeHoldersLen === 2) {
        tmp = (revLookup[b64.charCodeAt(i)] << 2) | (revLookup[b64.charCodeAt(i + 1)] >> 4);
        arr[curByte++] = tmp & 0xFF;
    }

    if (placeHoldersLen === 1) {
        tmp =
            (revLookup[b64.charCodeAt(i)] << 10) |
            (revLookup[b64.charCodeAt(i + 1)] << 4) |
            (revLookup[b64.charCodeAt(i + 2)] >> 2);
        arr[curByte++] = (tmp >> 8) & 0xFF;
        arr[curByte++] = tmp & 0xFF;
    }

    return arr;
}

function tripletToBase64(num) {
    return lookup[num >> 18 & 0x3F] +
        lookup[num >> 12 & 0x3F] +
        lookup[num >> 6 & 0x3F] +
        lookup[num & 0x3F];
}

function encodeChunk(uint8, start, end) {
    var tmp;
    var output = [];
    for (var i = start; i < end; i += 3) {
        tmp =
            ((uint8[i] << 16) & 0xFF0000) +
            ((uint8[i + 1] << 8) & 0xFF00) +
            (uint8[i + 2] & 0xFF);
        output.push(tripletToBase64(tmp));
    }
    return output.join('');
}

function fromByteArray(uint8) {

    var tmp;
    var len = uint8.length;
    var extraBytes = len % 3; // if we have 1 byte left, pad 2 bytes
    var parts = [];
    var maxChunkLength = 16383; // must be multiple of 3

    // go through the array every three bytes, we'll deal with trailing stuff later
    for (var i = 0, len2 = len - extraBytes; i < len2; i += maxChunkLength) {
        parts.push(encodeChunk(uint8, i, (i + maxChunkLength) > len2 ? len2 : (i + maxChunkLength)));
    }

    // pad the end with zeros, but make sure to not forget the extra bytes
    if (extraBytes === 1) {
        tmp = uint8[len - 1];
        parts.push(
            lookup[tmp >> 2] +
            lookup[(tmp << 4) & 0x3F] +
            '=='
        );
    } else if (extraBytes === 2) {
        tmp = (uint8[len - 2] << 8) + uint8[len - 1];
        parts.push(
            lookup[tmp >> 10] +
            lookup[(tmp >> 4) & 0x3F] +
            lookup[(tmp << 2) & 0x3F] +
            '='
        );
    }

    return parts.join('');
}

function decode(code) {
    code = code.replace(/-/g, '+').replace(/_/g, '/') + '===='.substring(0, (4 - (code.length % 4)) % 4);
    return toByteArray(code);
}

function encode(code) {
    code = fromByteArray(ensureUint8Array(code));
    return code.replace(/\+/g, '-').replace(/\//g, '_').replace(/=/g, '');
}

function ensureUint8Array(arg) {
    if (arg instanceof ArrayBuffer) {
        return new Uint8Array(arg);
    } else {
        return arg;
    }
}

function decodeExcludeCredentials(cred) {
    cred.id = decode(cred.id);
    delete cred.transports;
}

function registerAutheticator(response) {
    var options = response.responseXML.documentElement.getAttribute('answer');
	var valid = !!options;

	if(!valid){
		jslog("Error while generating registration request.");
		close();
		return false;
	}

    options = JSON.parse(options);
    options.user.id = decode(options.user.id);
    options.challenge = decode(options.challenge.value);

    delete options.user.icon;
    delete options.rp.icon;

    if (options.excludeCredentials) {
        options.excludeCredentials.forEach(decodeExcludeCredentials);
    }

    navigator.credentials.create({
        publicKey: options
    }).then(function(response) {
        var res = {};
        res.id = response.id;
        res.rawId = encode(response.rawId);
        res.type = response.type;
        res.response = {};
        res.response.clientDataJSON = encode(response.response.clientDataJSON);
        res.response.attestationObject = encode(response.response.attestationObject);
        res.clientExtensionResults = response.getClientExtensionResults();

        if (response.response.getTransports)
            res.response.transports = JSON.stringify(response.response.getTransports());

        validateRegistrationResponse(res);
    }).catch(function(error) {
        alert(error);
    });
}

function doesClientSupportWebAuthn() {
    if (!window.PublicKeyCredential) {
        return false;
    }
    return true;
}

function authenticate(response) {
    var options = response.responseXML.documentElement.getAttribute('answer');
    options = JSON.parse(options);
    options.challenge = decode(options.challenge.value);
    options.allowCredentials.forEach(function(item, index) {
        item.id = decode(item.id);
        delete item.transports;
    });

    delete options.extensions;

    navigator.credentials.get({
        publicKey: options
    }).then(function(response) {
        var res = {};
        res.id = response.id;
        res.rawId = encode(response.rawId);
        res.type = response.type;
        res.response = {};
        res.response.clientDataJSON = encode(response.response.clientDataJSON);
        res.response.authenticatorData = encode(response.response.authenticatorData);
        res.response.signature = encode(response.response.signature);
        res.clientExtensionResults = response.getClientExtensionResults();
        gel('sys_web_authentication_response').value = JSON.stringify(res);
        validateAuthenticationResponse(res);
    }).catch(function(error) {
        jslog("Error while authenticating: " + error);
    });
}

function setViewportForMobile() {
	var CONTENT = 'width=device-width, initial-scale=1.0, minimum-scale=1.0';

	var viewportElem = $j("meta[name='viewport']");	
	if (viewportElem.length)
		viewportElem.attr("content", CONTENT);
	else
		$j('head').append('<meta name="viewport" content="' + CONTENT + '">');
}

angular.module("sn.change_request.next_available_time", ["ui.bootstrap"])
	.constant("NEXT_AVAILABLE_TIME", {
		"URL": "/api/now/change_request_calendar/change/nextavailabletime/",
		"SYSPARM_CK": "?sysparm_ck=",
		"KEYS": {
			"TABKEY": 9,
			"ENTER": 13,
			"ESCAPE": 27,
			"SPACE": 32,
			"LEFT": 37,
			"UP": 38,
			"RIGHT": 39,
			"DOWN": 40
		},
		"VIEW_MODE": {
			"DAY": 0,
			"MONTH": 1,
			"YEAR": 2
		},
		"FORM": {
			"MODAL": "chg_next_available_time"
		}
	})
	.config(function($locationProvider) {
		$locationProvider.html5Mode({
			enabled: true,
			requireBase: false
		});
	})
	.service("snCalendarNextAvailableTimeService", function(i18n, $http, $q, $rootScope, NEXT_AVAILABLE_TIME, $window) {
		var snCalendarNextAvailableTimeService = this;

		snCalendarNextAvailableTimeService.getSlots = function(chgId, range) {
			chgId = sanitizeSysId(chgId);
			if (!chgId)
				return null;

			var deferred = $q.defer();
			var url = NEXT_AVAILABLE_TIME.URL + chgId + NEXT_AVAILABLE_TIME.SYSPARM_CK + $window.g_ck;
			$http.post(url, range).then(function(response) {
				deferred.resolve(response.data.result);
			}, function(response) {
				deferred.reject(response);
			});
			return deferred.promise;
		};
		snCalendarNextAvailableTimeService.submit = function(chgId, data) {
			chgId = sanitizeSysId(chgId);
			if (!chgId)
				return null;

			var deferred = $q.defer();
			var url = NEXT_AVAILABLE_TIME.URL + chgId + NEXT_AVAILABLE_TIME.SYSPARM_CK + $window.g_ck;
			$http.put(url, data).then(function(response) {
				deferred.resolve(response.data.result);
			}, function(response) {
				deferred.reject(response);
			});
			return deferred.promise;
		};
		snCalendarNextAvailableTimeService.abort = function(promise) {
			$q.resolve(promise);
		};
		snCalendarNextAvailableTimeService.broadcast = function(type, msg) {
			$rootScope.$broadcast("sn.cnat." + type, msg);
		};
		snCalendarNextAvailableTimeService.translate = function() {
			var locale = {
				"days": {
					previous: i18n.getMessage("Previous month"),
					next: i18n.getMessage("Next month")
				},
				"months": {
					next: i18n.getMessage("Next year"),
					previous: i18n.getMessage("Previous year"),
				},
				"years": {
					previous: i18n.getMessage("Previous decade"),
					next: i18n.getMessage("Next decade")
				},
				"today": i18n.getMessage("Today")
			};
			return locale;
		};
		snCalendarNextAvailableTimeService.normalizeKeyCode = function(evt) {
			evt.charCode = evt.charCode === 0 ? evt.keyCode : evt.charCode;
			return evt;
		};
		snCalendarNextAvailableTimeService.locale = function() {
			var lang = angular.element("html").attr("lang");
			var momentLocaleMapper = {
				'pb': 'pt-br', //Portuguese-Brazil
				'fq': 'fr-ca', //French Canadian
				'fc': 'fr-ca', //French Canadian
				'zt': 'zh-tw', //Chinese Traditional
				'zh': 'zh-cn' //Chinese Simplified
			};
			return momentLocaleMapper[lang] ? momentLocaleMapper[lang] : lang;
		};
	})
	.directive("snChangeDatePicker", function(i18n, getTemplateUrl, $timeout, NEXT_AVAILABLE_TIME, snCalendarNextAvailableTimeService) {
		return {
			replace: true,
			restrict: "E",
			templateUrl: getTemplateUrl("sn_next_available_time_date_picker.xml"),
			link: function($scope, element, $attrs) {
				function handler(evt) {
					evt = snCalendarNextAvailableTimeService.normalizeKeyCode(evt);
					var index;
					var next;
					var minicalObj = angular.element(element).datetimepicker().data("DateTimePicker");
					var focused = angular.element(document.activeElement);
					var list = angular.element(".datepicker > div:visible").find(".prev:visible, .next:visible, [tabindex='0']");
					if (typeof index === "undefined")
						index = list.index(focused);
					switch (evt.charCode) {
						case NEXT_AVAILABLE_TIME.KEYS.TABKEY:
						case NEXT_AVAILABLE_TIME.KEYS.LEFT:
						case NEXT_AVAILABLE_TIME.KEYS.RIGHT:
							var nav = focused.parents(".bootstrap-datetimepicker-widget");
							if (nav.length) {
								evt.preventDefault();
								evt.stopPropagation();
								if ((evt.charCode === NEXT_AVAILABLE_TIME.KEYS.TABKEY && !evt.shiftKey) || evt.charCode === NEXT_AVAILABLE_TIME.KEYS.RIGHT)
									index = (index === list.length - 1) ? 0 : index + 1;
								else if ((evt.charCode === NEXT_AVAILABLE_TIME.KEYS.TABKEY && evt.shiftKey) || evt.charCode === NEXT_AVAILABLE_TIME.KEYS.LEFT)
									index = (index === 0) ? list.length - 1 : index - 1;
								next = list.get(index);
							}
							break;
						case NEXT_AVAILABLE_TIME.KEYS.UP:
							if (focused.is("th")) {
								next = list.get(list.length - 1);
								break;
							}
							var noOfcols = (minicalObj.viewMode === NEXT_AVAILABLE_TIME.VIEW_MODE.DAY) ? 7 : 4;
							var noOfHeaderElements = 3;
							if (index === noOfHeaderElements)
								index = 0;
							else if (index > noOfHeaderElements && index < noOfHeaderElements + noOfcols - 1)
								index = 1;
							else
								index -= noOfcols;
							next = list.get(index);
							break;
						case NEXT_AVAILABLE_TIME.KEYS.DOWN:
							if (focused.is("th")) {
								next = list.get(3);
								break;
							}
							switch (minicalObj.viewMode) {
								case NEXT_AVAILABLE_TIME.VIEW_MODE.DAY:
									index = (index + 7 > list.length - 1) ? 0 : index + 7;
									break;
								case NEXT_AVAILABLE_TIME.VIEW_MODE.MONTH:
								case NEXT_AVAILABLE_TIME.VIEW_MODE.YEAR:
									index = (index + 4 > list.length - 1) ? 0 : index + 4;
									break;
							}
							next = list.get(index);
							break;
						case NEXT_AVAILABLE_TIME.KEYS.ENTER:
							focused.click();
							next = focused;
							break;
						case NEXT_AVAILABLE_TIME.KEYS.ESCAPE:
							evt.preventDefault();
							evt.stopPropagation();
							minicalObj.hide();
							return;
					}
					if (next)
						next.focus();
				}
				angular.element(element).datetimepicker({
					language: snCalendarNextAvailableTimeService.locale(),
					format: "L",
					viewMode: 'days',
					pickTime: false,
					icons: {
						previous: 'icon-arrow-left',
						next: 'icon-arrow-right'
					},
					defaultDate: $scope.getCurrentDate(),
					translation: snCalendarNextAvailableTimeService.translate()
				}).on("dp.show", function(event) {
					$timeout(function() {
						angular.element(event.currentTarget).find("button").attr("aria-expanded", true);
						var minical = angular.element(".bootstrap-datetimepicker-widget");
						minical.attr("aria-expanded", true);
						minical.off("keydown"); // make sure the keydown event is not attached
						minical.on("keydown", handler);
						minical.find("table").attr("role", "presentation");
						angular.element(".datepicker div:visible th.prev").focus();
					});
				}).on('dp.change', function(event) {
					if (!event.oldDate.isValid()) return;
					$scope.setCurrentDate(event.date);
				}).on("dp.hide", function(event) {
					if (angular.element(event.currentTarget).find("button").attr("aria-expanded") === "false")
						return;
					angular.element(event.currentTarget).find("button").attr("aria-expanded", false);
					var minical = angular.element(".bootstrap-datetimepicker-widget");
					minical.attr("aria-expanded", false);
					minical.off("keydown");
					angular.element("#change-date-picker").focus();
				}).on("sn.changeview", function(event) {
					if (angular.element(event.currentTarget).find("button").attr("aria-expanded") === "false")
						return;
					angular.element(".datepicker-" + event.view + " .prev").focus();
				});
			},
			controller: function($scope) {
				$scope.$on('modal.closing', function(event) {
					angular.element('#datetimepicker1').data("DateTimePicker").destroy();
				});
				$timeout(function() {
					angular.element("#change-date-picker").focus();
				});
			}
		};
	})
	.directive("snFormNextAvailableTime", function(i18n, getTemplateUrl, $window, $timeout, NEXT_AVAILABLE_TIME, snCalendarNextAvailableTimeService) {
		"use strict";
		return {
			replace: true,
			restrict: "E",
			scope: {
				title: "@"
			},
			templateUrl: function(tElement, tAttrs) {
				return getTemplateUrl("sn_next_available_time_window.xml");
			},
			link: function($scope, $element, $attrs) {
				$scope.form = true;

				angular.element($window.document).on("keydown", function(event) {
					event = snCalendarNextAvailableTimeService.normalizeKeyCode(event);
					if (NEXT_AVAILABLE_TIME.KEYS.ESCAPE === event.charCode && $scope.form && $window && $window.parent && $window.parent.GlideModal)
						setTimeout(function() {
							new $window.parent.GlideModal().get(NEXT_AVAILABLE_TIME.FORM.MODAL).destroy();
						}, 0);
				});
