			angular.forEach(f.variableParams, function(obj, key) {
				if(obj.value.length > 0)
					f.variableParams[key].value = [];
			});

			angular.forEach(f.queryParams, function(obj, key) {
				if(obj.value != "")
					f.queryParams[key].value = "";
