					}else{
						facetData.forEach(function(k) {
							var fd = angular.copy(k);
							if($.isArray(value)){
								value.forEach(function(v){
									if(fd.id == v){
										if(v == "NULL_VALUE")
											fd.label = f.variableParams[key].title +" "+fd.label;
										fd.name = key;
										fd.type = f.faceTypeMap[key];
										f.selectionStack.push(key);
										f.breadcrumbFilters.push(fd);
									}
								});

