			var navBarHeight = $('.snc-article-header-toolbar').height(),
				topMargin;
			if(navBarHeight < 40){
				topMargin = 0;
			}if(navBarHeight < 80){
				topMargin = navBarHeight * (2/3);
			}else{
				topMargin = navBarHeight * (130/150);				  
			}
			$('.kb-view-content-wrapper').css('margin-top', topMargin + 'px');
		}
	})();	

});

angular.module("sn.app_common.aisa.search", ["sn.app_common"]);
angular.module("sn.app_common.aisa.search").service("aisaSearch", ["$http", "$q", "$log", function($http, $q, $log) {
	var aisaSearch = this;

	this.AISA_SEARCH_ENDPOINT = "/api/now/aisa/search";

	// Definition of the client side SearchRequest
	var SearchRequest = function(requestData) {
		var searchRequest = {
			method: 'POST',
			url: aisaSearch.AISA_SEARCH_ENDPOINT,
			headers: {
				'Content-Type': "application/json; charset=UTF-8"
			},
			data: {
					"searchContextConfigId":requestData.searchApp,
					"requestedFields": requestData.requestedFields,
					"searchTerm": requestData.searchTerm,
					"rpSysId": requestData.rpSysId
			}
		};
		// Deep copy of request.  Stops dot walking inadvertantly modifying different requests.
		angular.copy(searchRequest, this);

		this.submit = function() {
			return aisaSearch.submit(this);
		};
	};

	// Definition of the client side SearchResponse
	var SearchResponse = function(responseData) {
		var searchAPIResponse = {};
		try {
			searchAPIResponse = responseData.data.result;
			searchAPIResponse.status = responseData.status;
		} catch (e) {
			searchAPIResponse = {
				status: 500,
				error: "Server Internal Error"
			};
		} finally {
			angular.copy(searchAPIResponse, this);
		}
	};

	aisaSearch.newSearchRequest = function(requestData) {
		return new SearchRequest(requestData);
	};

	aisaSearch.submit = function(searchRequest) {
		var deferred = $q.defer();
		$http(searchRequest).then(
			function(response) {
				deferred.resolve(new SearchResponse(response));
			},
			function(response) {
				deferred.reject(response);
			}
		);
		return deferred.promise;
	};
}]);

function validateStartEndDate(startDateField, endDateField, processErrorMsg){
	var startDate = g_form.getValue(startDateField);
	var endDate = g_form.getValue(endDateField);

	var format = g_user_date_format;

	if (startDate === "" || endDate === "")
		return true;

	// format the date using getDateFromFormat
	var startDateFormat = getDateFromFormat(startDate, format);
	var endDateFormat = getDateFromFormat(endDate, format);

	if (startDateFormat < endDateFormat)
		return true;

	// 0 from "getDateFormat" means an invalid date string was passed to it
	if (startDateFormat === 0 || endDateFormat === 0){
		processErrorMsg(new GwtMessage().getMessage("{0} is invalid", g_form.getLabelOf(startDate === 0? startDateField : endDateField)));
		return false;
	}

	if (startDateFormat > endDateFormat){
		processErrorMsg(new GwtMessage().getMessage("{0} must be after {1}", g_form.getLabelOf(endDateField), g_form.getLabelOf(startDateField)));
		return false;
	}

	return true;
}

