.directive("snColorPickerPalletColor", function() {
	return {
		restrict: "E",
		require: "^snColorPickerPallet",
		replace: true,
		template: '<button class="cp-control pallet-color default-focus-outline" ng-style="{\'background-color\': palletColor}" ng-click="selectColor()" ng-class="{\'selected\': selected()}" role="radio" aria-label="{{palletColor}}" aria-checked="{{selected()}}"><span class="icon-check dead-centre selected-color" ng-show="selected()"></span></buton>',
		link: function(scope) {

			scope.selected = function() {
				if (!scope.state.selectedColor || !scope.palletColor)
					return false;

				return scope.state.selectedColor.toLowerCase() === scope.palletColor.toLowerCase();
			};

			scope.selectColor = function() {
				scope.color.setColor(scope.palletColor);
				if (scope.hsl) {
					var hsl = scope.color.getHSL();
					["h","s","l","a"].forEach(function(key) {
						scope.hsl[key] = hsl[key];
					});
				}
			};

			scope.$watchGroup(["color.r","color.g","color.b", "color.a"], function() {
				scope.state.selectedColor = scope.color.rgbHex();
			});
		}
	};
})
.directive("snColorPickerPalletToggle", ["VIEW", function(VIEW) {
	return {
		restrict: "E",
		replace: true,
		template: '<button class="cp-control pallet-toggle" ng-class="{\'to-pallet\': state.view==\'hsl\', \'to-hsl\': state.view==\'pallet\'}"></button>',
		link: function(scope, element) {

			function toggleView() {
				if (scope.state.view == VIEW.HSL)
					scope.action.palletView();
				else
					scope.action.hslView();
			}

			element.on("click", function(event) {
				event.stopPropagation();
				toggleView();
				scope.$apply();
			});

			scope.$watch("state.selectedColor", function() {
				element.css("background-color", scope.color.rgbBaseHue());
			});
		}
	};
}]);

angular.module("sn.change_management.cab.calendar")
	.service("meetingCalendar", ['CAB', 'modelUtil', '$http', '$q', '$log', function(CAB, modelUtil, $http, $q, $log) {
		var meetingCalendar = this;

		meetingCalendar.getMeetingsBetween = function(startDateMs, endDateMs) {
			var deferred = $q.defer();
			$http.get(CAB.SERVICE.MEETING_CALENDAR + startDateMs + "/" + endDateMs ).then(
				function(response){
					response.data.result.forEach(function(meeting){
						modelUtil.addValueMSToAll(meeting);
					});

					return deferred.resolve(response.data.result);
				},
				function(response){
					modelUtil.failNicely(response);
					return deferred.reject(response);
				}
			);

			return deferred.promise;
		};
	}]);

var app = angular.module("sn.chg_model_ui.landing", ["sn.common",
	"sn.common.glide",
	"sn.app_common",
	"ui.bootstrap",
	"ngAnimate",
	"sn.app_itsm.now.filter",
	"sn.app_itsm.now.accessibility",
	"sn.app_itsm.now.iscroll",
	"sn.chg_model_ui.tooltip_overflow",
	"sn.chg_model_ui.landing_splash_screens",
	"sn.chg_model_ui.notification"
]);
app.controller("ChgModelLandingController", ["$scope", "$window", "userPreferences", "getTemplateUrl", "i18n", "$timeout", "$filter", "snConnectService", "chgModelService", "TextSearchService", "HighlightService", "ChgModelConstants",
	function($scope, $window, userPreferences, getTemplateUrl, i18n, $timeout, $filter, snConnectService, chgModelService, TextSearchService, HighlightService, ChgModelConstants) {

		var chgModelLandingCtrl = this;
		var userPreferenceScheduleFilter = "sn_chg_model_ui.landing_page.chg_model_filter";
		var debounceTimeout;

		chgModelLandingCtrl.isRTL = angular.element("html").hasClass("rtl");
		chgModelLandingCtrl.showChgModels = false;
		chgModelLandingCtrl.textSearchReadOnly = true;
		chgModelLandingCtrl.textSearch = "";
		chgModelLandingCtrl.scheduleOffset = 0;
		chgModelLandingCtrl.limitReached = false;
		chgModelLandingCtrl.showChgModelPinnedMsg = false;
		chgModelLandingCtrl.loading = true;
		chgModelLandingCtrl.searching = false;
		chgModelLandingCtrl.cardClicked = false;
		chgModelLandingCtrl.canCreateSchedule = ($window.NOW.chg_model_landing.canCreate === "true");

		chgModelLandingCtrl.filter = $window.NOW.chg_model_landing.chg_model_filter || "pinned";
		chgModelLandingCtrl.userID = $window.NOW.user_id;
		chgModelLandingCtrl.limit = parseInt($window.NOW.chg_model_landing.chg_model_limit || "40");
		chgModelLandingCtrl.maxChgModels = parseInt($window.NOW.chg_model_landing.max_chg_models || "300");
		chgModelLandingCtrl.notificationTimeout = parseInt($window.NOW.chg_model_landing.notification_timeout || "4000");
		chgModelLandingCtrl.isStdChgCatalogActive = ($window.NOW.chg_model_landing.isStdChgCatalogActive === "true");
		chgModelLandingCtrl.isAccessibilityEnabled = ($window.NOW.chg_model_landing.isAccessibilityEnabled === "true");
		chgModelLandingCtrl.showPinnedNotification = ($window.NOW.chg_model_landing.pinned_notification === "true");

		$scope.$on("textSearch", function(event, textSearch) {
			chgModelLandingCtrl.textSearch = textSearch;
			chgModelLandingCtrl.searching = true;
			if (debounceTimeout)
				$timeout.cancel(debounceTimeout);
			debounceTimeout = $timeout(function() {
				_reset();
			}, 1000);
		});

		$scope.$watch("selectedSort", function(newValue, oldValue) {
			if (!newValue || !oldValue)
				return;

			_reset();
		});

		function _isFiltered() {
			return chgModelLandingCtrl.textSearch.trim() !== "";
		}

		$scope.isLoading = function() {
			return chgModelLandingCtrl.loading;
		};

		chgModelLandingCtrl.isSearching = function() {
			return chgModelLandingCtrl.searching;
		};

		function _showPinnedNotification(chgModel) {
			if (!chgModel || !chgModel.sys_id || !chgModel.name || !chgModelLandingCtrl.showPinnedNotification)
				return;

			var msg = chgModelLandingCtrl.isPinned(chgModel.sys_id.value) ? chgModelLandingCtrl.form.addTo : chgModelLandingCtrl.form.removeFrom;

			chgModelLandingCtrl.form.schedulePinnedMsg = i18n.format(msg, {
				name: chgModel.name.display_value,
				yourPinned: chgModelLandingCtrl.form.pinned.title
			});

			chgModelLandingCtrl.showChgModelPinnedMsg = true;

			$timeout(function() {
				chgModelLandingCtrl.showChgModelPinnedMsg = false;
			}, chgModelLandingCtrl.notificationTimeout);

		}

		function _splashScreenVisibility() {
			chgModelLandingCtrl.showSplashMsg = chgModelService.showSplashMsg(chgModelLandingCtrl.filter) && !_isFiltered();
			chgModelLandingCtrl.textSearchReadOnly = chgModelLandingCtrl.showSplashMsg;
			chgModelLandingCtrl.showChgModels = !chgModelLandingCtrl.showSplashMsg;
		}

		chgModelLandingCtrl.getTabs = function() {
			var tabs = ["pinned", "all"];
			if (chgModelLandingCtrl.isStdChgCatalogActive) {
				tabs.push("preapproved");
				tabs.push("change_models");
			}
			return tabs.join(" ");
		};

		chgModelLandingCtrl.getTitle = function() {
			return chgModelLandingCtrl.form[chgModelLandingCtrl.filter].splash_title || chgModelLandingCtrl.form[chgModelLandingCtrl.filter].title || "";
		};

		chgModelLandingCtrl.getDescription = function() {
			return chgModelLandingCtrl.form[chgModelLandingCtrl.filter].description || "";
		};

		chgModelLandingCtrl.highlight = function(text, search) {
			return HighlightService.highlight(text, search, "change-model__highlight");
		};

		chgModelLandingCtrl.clearFilters = function() {
			TextSearchService.clearFilters();
		};

		chgModelLandingCtrl.showClearFilters = function() {
			return _isFiltered();
		};

		chgModelLandingCtrl.foundChangeModels = function() {
			var filteredChgModels = TextSearchService.getFilteredArray(chgModelLandingCtrl.chgModelArray, chgModelLandingCtrl.textSearch);
			return chgModelLandingCtrl.initialLoad || (_isFiltered() && filteredChgModels && filteredChgModels.length > 0);
		};

		chgModelLandingCtrl.showNoResultsFound = function() {
			if (!_isFiltered())
				return false;

			return !chgModelLandingCtrl.foundChangeModels() && !chgModelLandingCtrl.isSearching() && !$scope.isLoading() && !chgModelLandingCtrl.showSplashMsg;
		};

		chgModelLandingCtrl.isPinned = function(sysId) {
			if (!chgModelService.changeModels.hasOwnProperty("__struct"))
				return false;

			return chgModelService.changeModels.__struct["pinned"].sysIds.indexOf(sysId) !== -1;
		};

		chgModelLandingCtrl.isDisabled = function() {
			if (!chgModelService.changeModels || !chgModelService.changeModels.__struct || $scope.isLoading())
				return true;

			return chgModelService.changeModels.__struct[chgModelLandingCtrl.filter].sysIds.length === 0 && !_isFiltered();
		};

		chgModelLandingCtrl.setCardClicked = function() {
			chgModelLandingCtrl.cardClicked = true;
		};

		chgModelLandingCtrl.isCardClicked = function() {
			return chgModelLandingCtrl.cardClicked;
		};

		chgModelLandingCtrl.togglePinned = function($event, chgModel) {
			$event.stopPropagation();
			$event.preventDefault();

			if (!chgModel || !chgModel.sys_id)
				return;

			chgModelService.togglePinned(chgModel.sys_id.value);
			_showPinnedNotification(chgModel);
			_splashScreenVisibility();
		};

		chgModelLandingCtrl.immediateCheck = function() {
			if (!chgModelService.changeModels.hasOwnProperty("__struct"))
				return false;

			return chgModelService.hasMoreChangeModels(chgModelLandingCtrl.filter);
		};

		chgModelLandingCtrl.getOrderBy = function() {
			if (!$scope.selectedSort)
				return "name";

			return $scope.selectedSort;
		};

		chgModelLandingCtrl.getPinA11yLabel = function(name, sysId) {
			return chgModelLandingCtrl.isPinned(sysId) ?
				i18n.format(chgModelLandingCtrl.form.unpinChangeModelA11y, {
					name: name
				}) :
				i18n.format(chgModelLandingCtrl.form.pinChangeModelA11y, {
					name: name
				});
		};

		chgModelLandingCtrl.filterBy = function(filter) {
			if (chgModelLandingCtrl.filter === filter)
				return;

			chgModelLandingCtrl.filter = filter;
			$window.NOW.chg_model_landing.chg_model_filter = filter;
			_splashScreenVisibility();
			chgModelService.hasReachedLimit(filter);
			userPreferences.setPreference(userPreferenceScheduleFilter, filter);
			chgModelLandingCtrl.getChangeModels(chgModelLandingCtrl.textSearch);
		};

		chgModelLandingCtrl.getChgModelArray = function() {
			var arr = [];

			if (!chgModelLandingCtrl.filter)
				return arr;

			if (chgModelService.changeModels.hasOwnProperty("__struct") && !chgModelLandingCtrl.searching) {
				var arraySysIds = chgModelService.changeModels.__struct[chgModelLandingCtrl.filter].sysIds;

				for (var i = 0; i < arraySysIds.length && i < chgModelLandingCtrl.maxChgModels; i++)
					arr.push(chgModelService.changeModels.chg_model[arraySysIds[i]]);
			}
			return arr;
		};

		chgModelLandingCtrl.chgModelArray = [];

		function _getLimit() {
			if (!chgModelService.changeModels.__struct)
				return chgModelLandingCtrl.limit;
			chgModelLandingCtrl.limit = chgModelService.changeModels.__struct[chgModelLandingCtrl.filter].limit; // Reset limit, just in case it's below minimum
			if (chgModelService.changeModels.__struct[chgModelLandingCtrl.filter].maxLimit - chgModelService.changeModels.__struct[chgModelLandingCtrl.filter].offset > chgModelLandingCtrl.limit)
				return chgModelLandingCtrl.limit;

			return chgModelService.changeModels.__struct[chgModelLandingCtrl.filter].maxLimit - chgModelService.changeModels.__struct[chgModelLandingCtrl.filter].offset;
		}

		chgModelLandingCtrl.getChangeModels = function() {
			if ($scope.isLoading() || chgModelService.hasReachedLimit(chgModelLandingCtrl.filter) || !chgModelService.hasMoreChangeModels(chgModelLandingCtrl.filter)) {
				chgModelLandingCtrl.chgModelArray = chgModelLandingCtrl.getChgModelArray();
				return;
			}

			// Do not get more records if limit is zero or negative number
			if (chgModelService.changeModels.__struct[chgModelLandingCtrl.filter] && chgModelService.changeModels.__struct[chgModelLandingCtrl.filter].maxLimit < 1) {
				if (chgModelLandingCtrl.initialLoad)
					_splashScreenVisibility();

				return;
			}
			_getChangeModels();
		};

		function _getChangeModels() {
			var offset = chgModelService.changeModels.__struct[chgModelLandingCtrl.filter].offset;
			chgModelLandingCtrl.loading = true;
			chgModelService.getChangeModels(chgModelLandingCtrl.filter, chgModelLandingCtrl.getOrderBy(), offset, _getLimit(), chgModelLandingCtrl.textSearch.trim())
				.then(function(changeModels) {
					chgModelLandingCtrl.initialLoad = false;
					_splashScreenVisibility();
					chgModelLandingCtrl.chgModelArray = chgModelLandingCtrl.getChgModelArray();
					chgModelLandingCtrl.loading = false;
				})
				.catch(function(response) {
					if (response.status === 401)
						$window.location.reload();
				});
		}

		function _reset() {
			chgModelLandingCtrl.loading = true;
			chgModelService.initialHit(chgModelLandingCtrl.filter, chgModelLandingCtrl.getOrderBy(), _getLimit(), chgModelLandingCtrl.textSearch.trim())
				.then(function(changeModels) {
					chgModelLandingCtrl.initialLoad = false;
					_splashScreenVisibility();
					chgModelLandingCtrl.searching = false;
					chgModelLandingCtrl.chgModelArray = chgModelLandingCtrl.getChgModelArray();
					chgModelLandingCtrl.loading = false;
				})
				.catch(function(response) {
					if (response.status === 401)
						$window.location.reload();
				});
		}

		chgModelLandingCtrl.initialHit = function() {
			chgModelLandingCtrl.loading = true;
			chgModelService.initialHit(chgModelLandingCtrl.filter, chgModelLandingCtrl.getOrderBy(), _getLimit())
				.then(function(changeModels) {
					chgModelLandingCtrl.initialLoad = false;
					_splashScreenVisibility();
					chgModelLandingCtrl.chgModelArray = chgModelLandingCtrl.getChgModelArray();
					chgModelLandingCtrl.loading = false;
				})
				.catch(function(response) {
					if (response.status === 401)
						$window.location.reload();
				});
		};

		chgModelLandingCtrl.init = function() {
			chgModelLandingCtrl.initialLoad = true;
			chgModelLandingCtrl.initialHit();
		};

		chgModelLandingCtrl.getStateModel = function(chgModel) {
			if (!chgModel || !chgModel.state_choices)
				return i18n.getMessage("No state model defined");

			var msg = i18n.getMessage("State model: {states}");
			var states = chgModel.state_choices.map(function(state) { return state.label; }).join(", ");
			return i18n.format(msg, { states: states });
		};

		chgModelLandingCtrl.getAccentStyle = function(color) {
			if (!color)
				return {};

			return { "background-color": color };
		};

		chgModelLandingCtrl.form = {};
		chgModelLandingCtrl.messages = {
			"Loading...": i18n.getMessage("Loading..."),
			"Searching...": i18n.getMessage("Searching..."),
			"Limit Reached": i18n.getMessage("You have reached the limit of visible Change Request Models in this view."),
			"No results found": i18n.getMessage("No results found")
		};

		chgModelLandingCtrl.form.title = i18n.getMessage("Create a change request");
		chgModelLandingCtrl.form.clearFilters = i18n.getMessage("Clear Filters");
		chgModelLandingCtrl.form.pinChangeModel = i18n.getMessage("Pin Change Model");
		chgModelLandingCtrl.form.pinChangeModelA11y = i18n.getMessage("Pin Change Model - {name}");
		chgModelLandingCtrl.form.unpinChangeModel = i18n.getMessage("Unpin Change Model");
		chgModelLandingCtrl.form.unpinChangeModelA11y = i18n.getMessage("Unpin Change Model - {name}");
		chgModelLandingCtrl.form.pin = i18n.getMessage("Pin");
		chgModelLandingCtrl.form.icon = i18n.getMessage("icon");
		chgModelLandingCtrl.form.schedulePinnedMsg = "";
		chgModelLandingCtrl.form.addTo = i18n.getMessage("'{name}' added to '{yourPinned}'");
		chgModelLandingCtrl.form.removeFrom = i18n.getMessage("'{name}' removed from '{yourPinned}'");
		chgModelLandingCtrl.form.close = i18n.getMessage("Close");
		chgModelLandingCtrl.form.state_model = i18n.getMessage("State model");

		chgModelLandingCtrl.form.pinned = {};
		chgModelLandingCtrl.form.pinned.title = i18n.getMessage("Pinned");
		chgModelLandingCtrl.form.pinned.splash_title = i18n.getMessage("No pinned models");
		chgModelLandingCtrl.form.pinned.description = i18n.getMessage("You have no pinned models. When you pin a model, the model will be shown in this tab and you can access it directly.");

		chgModelLandingCtrl.form.change_models = {};
		chgModelLandingCtrl.form.change_models.title = i18n.getMessage("Models");
		chgModelLandingCtrl.form.change_models.splash_title = chgModelLandingCtrl.form.change_models.title;
		chgModelLandingCtrl.form.change_models.description = i18n.getMessage("A subset of Change Request types which have a change model.");

		chgModelLandingCtrl.form.preapproved = {};
		chgModelLandingCtrl.form.preapproved.title = i18n.getMessage("Preapproved");
		chgModelLandingCtrl.form.preapproved.splash_title = chgModelLandingCtrl.form.preapproved.title;
		chgModelLandingCtrl.form.preapproved.description = i18n.getMessage("A subset of Change Request types which are preapproved.");

		chgModelLandingCtrl.form.all = {};
		chgModelLandingCtrl.form.all.title = i18n.getMessage("All");
		chgModelLandingCtrl.form.all.splash_title = chgModelLandingCtrl.form.all.title;
		chgModelLandingCtrl.form.all.description = i18n.getMessage("All includes all change models you have access right to view.");

		$scope.form = chgModelLandingCtrl.form;
	}
]);

var ChannelClient = Class.create();

ChannelClient.prototype = {
    initialize: function(iframe, autoCreate) {
        this.iframe = iframe;
        this.fdocument = this._getFrameDocument();
        this.channelName = iframe.getAttribute("channel");
        this.autoCreate = autoCreate;
    },

    writeMsg: function(msg) {
        var gaj = new GlideAjax("ChannelAjax");
        gaj.addParam("sysparm_name", this.channelName);
        gaj.addParam("sysparm_type", "write");
        gaj.addParam("sysparm_chars", msg);
        gaj.addParam("sysparm_silent_request", "true");
        gaj.getXML(doNothing);
    },

    stream: function() {
        this._initUnloader();
        this.streamChannel(0);
    },

    streamChannel: function(lastSeq) {
        var gaj = new GlideAjax("ChannelAjax");
        gaj.addParam("sysparm_name", this.channelName);
        gaj.addParam("sysparm_type", "read");
        gaj.addParam("sysparm_value", lastSeq);
        gaj.addParam("sysparm_silent_request", "true");
        gaj.addParam("sysparm_express_transaction", "true");

        if (this.autoCreate)
            gaj.addParam("sysparm_auto_create", "true");

        gaj.getXML(this.gotChannelMessages.bind(this));
    },

    gotChannelMessages: function(response) {
        if (this.haveError(response))
            return;

        var lastSeq = response.responseXML.documentElement.getAttribute("channel_last_sequence");
        var items = response.responseXML.getElementsByTagName("item");
        jslog("response=" + response.responseText);

        for(var i = 0; i < items.length; i++) {
            var item = items[i];
            var msg = item.getAttribute('message');
            var date = item.getAttribute('date');
            var friendlyDate = new Date(parseInt(date));
            this.newMsg(getFormattedDateAndTime(friendlyDate) + " " + msg);
        }

        if (lastSeq != null)
           this.streamChannel(lastSeq);
    },

    newMsg: function(msg) {
        var el = this.fdocument.createElement('pre');
        el.innerHTML = htmlEscape(msg);
        this.fdocument.body.appendChild(el);

        this.scroll();
    },

    scroll: function() {
        if (this.iframe.getAttribute("autoscroll") == 'true')
            this.fdocument.documentElement.scrollTop = this.fdocument.documentElement.scrollHeight;
    },

    haveError: function(response) {
        if (!response) {
            alert("invalid response!");
            return true;
        }

        var interrupted = response.responseXML.documentElement.getAttribute("interrupted");
        if (interrupted)
            return true;

        var error = response.responseXML.documentElement.getAttribute("error");
        if (error) {
            alert("ERROR: " + error);
            return true;
        }

        return false;
    },

    _initUnloader: function() {
        Event.observe(window, 'beforeunload', this._forceUnload.bind(this), false);
    },

    _forceUnload: function() {
        this.iframe.src = "threads.do?sysparm_action=interrupt&sysparm_name=streaming_channel&sysparm_value=" + this.channelName;
    },

    _getFrameDocument: function() {
        if (this.iframe.contentDocument) {
            return this.iframe.contentDocument;  // For NS6
        } else if (this.iframe.contentWindow) {
            return this.iframe.contentWindow.document; // For IE5.5 and IE6
        } else if (this.iframe.document) {
            return this.iframe.document; // For IE5
        }
    },

    type: "ChannelClient"
}

var PwdWfManager = Class.create();

PwdWfManager.prototype = {
    wfResult: {
        bPass: false,
        msg: ""
    },
    requestId: "",
    processId: "",
    // Error Message to display in case of workflow errors
    errorMsg: "",
    startTime: "",
    wfCheckDoneFrequency: 500,
    wfTimeOut: 300000,
    wfContextSysId: "",
    redirectUrl: "",

    initialize: function(_wfCheckDoneFrequency, _wfTimeOut) {
        this.wfCheckDoneFrequency = _wfCheckDoneFrequency;
        this.wfTimeOut = _wfTimeOut;
    },

    startWfViaAjax: function(processId, requestId, password, redirectUrl) {
        this.requestId = requestId;
        this.processId = processId;
        this.redirectUrl = redirectUrl;
        this.startTime = new Date().getTime();

        var ga = new GlideAjax('PwdAjaxWFRequestProcessor');

        // These 4 params are required.
        ga.addParam('sysparm_name', 'startWorkflow');
        ga.addParam('sysparam_request_id', requestId);
        if (password != undefined) {
            ga.addParam('sysparam_new_password', password);
            ga.addParam("ni.nolog.sysparam_new_password", "true");
        }
        ga.addParam('sysparam_pwd_csrf_token', findCSRFElement().value);
        ga.addParam("ni.truncate.sysparam_pwd_csrf_token", "true");
        ga.getXML(this.callBackForStartWf.bind(this));
    },

    callBackForStartWf: function(response, args) {
        //Let's handle the security.
        handleSecurityFrom(response);

        var res = response.responseXML.getElementsByTagName("response");
        var status = res[0].getAttribute("status");
        var message = res[0].getAttribute("message");
        this.wfContextSysId = res[0].getAttribute("value");
        if (status.match(/failure/i)) {
            var is_flow = this.isFlow().toString();
            if (is_flow === 'true' && message === "non_fatal_failure")
                submitWithOKAndSysParam('$pwd_new.do', 'sysparm_page_text', message);
            else
                this._onError(message);
            return;
        }
        this.setTimeoutForRefresh();
    },

    setTimeoutForRefresh: function() {
        window.setTimeout(this.checkWFProgress.bind(this), this.wfCheckDoneFrequency);
    },

    //To check if the execution is through workflow or flow
    isFlow: function() {
        var ga = new GlideAjax('PwdAjaxWFRequestProcessor');
        ga.addParam('sysparm_name', 'isFlow');
        ga.addParam('sysparm_process_id', this.processId);
        ga.getXMLWait();
        return ga.getAnswer();
    },

    checkWFProgress: function() {
        var currTime = new Date().getTime();
        if (currTime - this.startTime > this.wfTimeOut) {
            this._onError("TIMEOUT");
            return;
        }

        var is_flow = this.isFlow().toString();

        if (is_flow === 'true') {
            var gaCheck = new GlideAjax('PwdAjaxWFRequestProcessor');
            gaCheck.addParam('sysparm_name', 'checkMaximumAttempts');
            gaCheck.addParam('sysparam_request_id', this.requestId);
            gaCheck.getXML(this.callBackForCheckResetAttempts.bind(this));
        } else {
            var ga = new GlideAjax('PwdAjaxWFRequestProcessor');
            ga.addParam('sysparm_name', 'getWorkflowStatus');
            ga.addParam('sysparam_wf_context_sys_id', this.wfContextSysId);
            // csrf token
            ga.addParam('sysparam_pwd_csrf_token', findCSRFElement().value);
            ga.getXMLWait();

            handleSecurityFrom(ga.requestObject);
            var state = ga.getAnswer();

            if (!state.match(/finished/i)) {
                this.setTimeoutForRefresh();
                return;
            } else if (state.match(/failure/i)) {
                this._onError("An error occured in PwdWfManager UI Script : checkWFProgress()");
                return;
            }

            var gaResult = new GlideAjax('PwdAjaxWFRequestProcessor');
            gaResult.addParam('sysparm_name', 'getWorkflowResult');
            gaResult.addParam('sysparam_wf_context_sys_id', this.wfContextSysId);
            gaResult.addParam('sysparam_request_id', this.requestId);
            // csrf token
            gaResult.addParam('sysparam_pwd_csrf_token', findCSRFElement().value);
            gaResult.getXML(this.callBackForGetWfContext.bind(this));
        }
    },

    callBackForCheckResetAttempts: function(response) {
        this._postProcessSuccess();
    },

    callBackForGetWfContext: function(response) {
        handleSecurityFrom(response);

        this._alert("client: callBackForGetWfContext triggered!");

        var history = response.responseXML.getElementsByTagName('history');
        var names = response.responseXML.getElementsByTagName('activity');
        var bAllActivitiesSucceeded = true;
        // An array of obj representing the state of an activity
        var allActivitiesObj = [];


        // Grab all the activities in the workflow and build an array of objects
        for (var ii = 0; ii < history.length; ii++) {
            var tmp = {};
            tmp.name = names[ii].getAttribute("name");
            tmp.index = history[ii].getAttribute("activity_index");

            tmp.result = history[ii].getAttribute("activity_result");
            if (tmp.result == undefined) {
                tmp.result = "";
            }

            tmp.faultDescription = history[ii].getAttribute("fault_description");
            if (tmp.faultDescription == undefined) {
                tmp.faultDescription = "";
            }

            allActivitiesObj.push(tmp);

            this._alert("Adding activity: " + "index=" + tmp.index + " : name=" + tmp.name + " : result=" + tmp.result + " : faultDescription=" + tmp.faultDescription);
        }

        // Iterate through activity history list to find the first error
        allActivitiesObj.sort(function(a, b) {
            return b.index - a.index;
        });
        var failingActivityObj = {};
        for (var jj in allActivitiesObj) {
            var eachObj = allActivitiesObj[jj];

            if (eachObj.result == undefined)
                continue;

            if (eachObj.result.match(/failure/i)) {
                bAllActivitiesSucceeded = false;
                this._alert("Found an error in activity history -> " + eachObj.name);

                // Check if faultDescription is an json object. If so, this was created via SNC.PwdWorkflowManager().creatError().
                try {
                    failingActivityObj = eval("(" + eachObj.faultDescription + ")");
                    this._alert("Fault description created via new SNC.PwdWorkflowManager().getErrorJSONString(...): \n* wfName=" + failingActivityObj.wfName + "\n* activityName=" + failingActivityObj.activityName + " \n* message=" + failingActivityObj.message + " \n* isFatal=" + failingActivityObj.isFatal);
                } catch (e) {
                    //The faultDescription was created manually, and not via new SNC.PwdWorkflowManager().getErrorJSONString(...)
                    this._alert("Fault description created manually");
                    failingActivityObj.message = eachObj.faultDescription;
                    failingActivityObj.isFatal = true;
                }
                break;
            }
        }

        try {
            var res = response.responseXML.getElementsByTagName("response");
        } catch (error) {
            alert("PwdWfManager caught exception! " + error.message);
        }
        var wfGeneralStatus = res[0].getAttribute("status");
        var message = res[0].getAttribute("message");
        var value = res[0].getAttribute("value");

        if (!wfGeneralStatus.match(/success/i) || (!bAllActivitiesSucceeded)) {
            //Check failure via by workflow result
            this.wfResult.bPass = false;
            this.wfResult.msg = failingActivityObj.message || "Password reset request failed.";
        } else { //pass
            this.wfResult.bPass = true;
        }

        this._alert("Final: WFContext results:\n bPass=" + this.wfResult.bPass + " \n msg=" + this.wfResult.msg);

        if (this.wfResult.bPass) {
            this._postProcessSuccess();
        } else {
            if (failingActivityObj) {
                if (!failingActivityObj.isFatal) {
                    var max_reset_attempts = response.responseXML.getElementsByTagName('max_reset_attempts');
                    if (max_reset_attempts.length) {
                        failingActivityObj.isFatal = true;
                        this._postProcessError(max_reset_attempts[0].getAttribute("message"));
                    } else {
                        var msg = failingActivityObj.message || "non fatal failure";
                        this._alert(msg);
                        submitWithOKAndSysParam('$pwd_new.do', 'sysparm_page_text', msg);
                    }
                } else {
                    this._postProcessError(this.wfResult.msg);
                }
            } else {
                this._postProcessError(this.wfResult.msg);
            }
        }
    },

    _postProcessSuccess: function() {
        var ga = this._createPostProcessAjax("true");
        ga.getXML(this._onSuccess.bind(this));
    },

    _postProcessError: function(msg) {
        this.errorMsg = msg;
        var ga = this._createPostProcessAjax("false");
        // Need to use the callback because of the parameter errorMsg
        ga.getXML(this.callBackForPostProcessOnError.bind(this));
    },

    callBackForPostProcessOnError: function(response) {
        this._onError(this.errorMsg);
    },

    _createPostProcessAjax: function(isSuccess) {
        var ga = new GlideAjax('PwdAjaxWFRequestProcessor');
        ga.addParam('sysparm_name', 'runPostProcessor');
        ga.addParam('sysparam_request_id', this.requestId);
        ga.addParam('sysparam_process_id', this.processId);
        ga.addParam('sysparam_workflow_status', isSuccess);
        ga.addParam('sysparam_pwd_csrf_token', findCSRFElement().value);
        return ga;
    },

    _alert: function(msg) {
        var debug = false;
        if (debug) {
            alert(msg);
        }
    },
    _onError: function(errorReason) {
        submitWithBlock('$pwd_error.do', errorReason);
    },
    _onSuccess: function(response) {
        handleSecurityFrom(response);
        if (this.redirectUrl) {
            // Check if a top frame exists, if so change the
            // top frame to the redirect url
            if (window.top)
                window.top.location.href = this.redirectUrl;
            else
                window.location.href = this.redirectUrl;
        } else {
            submitWithOKAndSysParam('$pwd_success.do', 'sysparm_success', 'SUCCESS');
        }
    },

    type: "PwdWfManager"
};

