.directive('snCabFocusInOut', function ($timeout) {
	return {
		restrict: 'A',
		scope: {
			snCabFocusInOut: '&'
		},
		link: function ($scope, el, attrs) {
			var $el = $(el);
			var fin = false, fout = false;
			$el.on('focusin.snCabFocusInOut', function () {
				checkForEvent();
				fin = true;
			});
			$el.on('focusout.snCabFocusInOut', function () {
				checkForEvent();
				fout = true;
			});
			var timer;
			function checkForEvent() {
				fout = false;
				fin = false;
				if (timer)
					$timeout.cancel(timer);
				timer = $timeout(function () {
					if (fout)
						$scope.snCabFocusInOut({$isFocusIn: false});
					if (fin)
						$scope.snCabFocusInOut({$isFocusIn: true});
					fin = false;
					fout = false;
					timer = undefined;
			  }, 100);
			}
			$scope.$on('$destroy', function () {
				$el.off('.snCabFocusInOut');
			});
		}
	};
})
.directive('snFocusContainer', function () {
	var TAB = 9;
	return {
		restrict: 'A',
		scope: false,
		link: function ($scope, el, attrs) {
			var $el = $(el);
			$el.on('keydown.snFocusContainer', function (evt) {
				var list = $(this).find('button, input').not("[tabindex='-1']");
				var focused = $(document.activeElement);
				// if calendar is not render, skips event handling
				if (list.length <= 1)
					return;
				if (evt.keyCode === TAB) {
					evt.preventDefault();
					var index;
					var next;
					if (typeof index == 'undefined')
						index = list.index(focused);
					if (!evt.shiftKey)
						index = (index === list.length - 1) ? 0 : index + 1;
					else if (evt.shiftKey)
						index = (index === 0) ? list.length - 1: index - 1;
					next = list.get(index);
					if (next)
						next.focus();
				}
			});

			$scope.$on('$destroy', function () {
				$el.off('.snFocusContainer');
			});
		}
	};
});
