angular.module("sn.chg_soc.snCreateNewInvite", ["sn.common"])
	.directive("snCreateNewInvite", function($timeout, getTemplateUrl, i18n) {
		"use strict";

		return {
			restrict: "E",
			templateUrl: getTemplateUrl("sn_chg_soc_sharing_invitation_search.xml"),
			replace: true,
			link: function(scope, elem, attrs, ctrl) {

				scope.focusOnInput = function() {
					$timeout(function() {
						angular.element("#recipient-input").focus().css("cursor", "");
					}, 100);
				};

				scope.scrollRecipientListToBottom = function() {
					$timeout(function() {
						var recipientListElem = angular.element("#recipient-list");
						recipientListElem.scrollTop = recipientListElem.scrollHeight;
						scope.focusOnInput();
					});
				};

				scope.$on("sn.aside.change_soc_side.open_share:opened", scope.focusOnInput);

			},
			controller: function($scope) {
				$scope.messages = {
					"To:": i18n.getMessage("To:"),
					"Remove {0}": i18n.getMessage("Remove {0}")
				};

				$scope.ignoreList = function() {
					return $scope.pendingRecipients.map(function(recipient) {
						return recipient.sys_id;
					}).join(",");
				};

				$scope.selectMember = function(selectedSuggestion) {
					var alreadyAdded = $scope.pendingRecipients.some(function(recipient) {
						return angular.equals(recipient, selectedSuggestion);
					});
					if (!alreadyAdded)
						$scope.pendingRecipients.push(selectedSuggestion);
					$scope.suggestions.query = "";
					$scope.scrollRecipientListToBottom();
				};

				$scope.keyUp = function(event, index, identityDetails, suggestions) {
					if (event && event.keyCode === 40 && suggestions && index < (suggestions.length + 2)) {
						var suggestion = suggestions[index + 1];
						if (suggestion && suggestion.sys_id)
							angular.element("#suggestion-" + suggestion.sys_id).focus();
					}
					else if (event && event.keyCode === 38) {
						if (suggestions && index > 0)
							angular.element("#suggestion-" + suggestions[index - 1].sys_id).focus();
						else
							$scope.focusOnInput();
					}
				};

				$scope.i18n = i18n;

				$scope.removeRecipient = function(event, index) {
					if (event && event.keyCode === 9)
						return;
					$scope.pendingRecipients.splice(index, 1);
					$scope.focusOnInput();
				};

			}
		};
	});

(function() {
	var isDigit = function(thchar) {
		return (thchar >= '0' && thchar <= '9');
	};

	var isAlpha = function(thchar) {
		return (thchar >= 'a' && thchar <= 'z\uffff')
			|| (thchar >= 'A' && thchar <= 'Z\uffff') || thchar == '_';
	};

	var isAlphaNum = function(thchar) {
		return isAlpha(thchar) || isDigit(thchar);
	};

	var containsOnlyChars = function(validChars, sText) {
		if (!sText) { return true; } // blank is fine

		for (var i = 0; i < sText.length; i++) {
			var c = sText.charAt(i);
			if (validChars.indexOf(c) === -1) { return false; } // not a match
		}

		return true;
	};

	var containsPeriodsSideBySide = function(sText) {
		var pattern = /\.\./g;
		return pattern.test(sText);
    };

	var isEmailValidWithReason = function(value) {
		var localPartChars = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!#$%*/?|^{}`~&'+-=_.";
		var domainChars =    "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789-_.";

		// break into local part and domain
		if (value.indexOf("@") === -1) { return "missing @ sign"; }

		var s = value.split("@");
		if (s.length != 2) { return "too many at signs"; }

		// check the local part of the address
		if (!containsOnlyChars(localPartChars, s[0])) { return "invalid character before the at sign"; }
		if (s[0].length < 1) { return "at least one character must be before the at sign"; }
		if (s[0].substr(0,1) === ".") { return "period cannot be the first character"; }
		if (s[0].substr(s[0].length-1,1) === ".") { return "period cannot be the last character before the at sign"; }

		// check the domain part of the address
		if (!containsOnlyChars(domainChars, s[1])) { return "invalid character after the at sign"; }

		var periodIndex = s[1].indexOf(".");
		if (periodIndex === -1) { return "missing period after the at sign"; }
		if (periodIndex === 0) { return "period cannot be the first character after the at sign"; }

		//check whether periods are used side by side
		if(containsPeriodsSideBySide(s[0])) {return "invalid usage of periods before @ sign"; }
		if(containsPeriodsSideBySide(s[1])) {return "invalid usage of periods after @ sign"; }

		var periods = s[1].split(".");
		var lastPeriod = periods[periods.length-1];
		if (lastPeriod.length < 1) { return "must be at least 1 character after the last period"; }
		if (!isAlphaNum(s[1].substr(0,1))) { return "the first character after the at sign must be alphanumeric"; }
		if (!isAlphaNum(s[1].substr(s[1].length-1,1))) { return "the last character must be alphanumeric"; }

		return ""; // address is OK
	};

	var isEmailValid = function(value) {
		return isEmailValidWithReason(value) === "";
	};

	return {
		isEmailValid: isEmailValid
	};
});

angular.module("sn.change_management.cab.agenda")
.filter("agendaItemFilter", ["CAB", "meetingAgenda", "$log", function(CAB, meetingAgenda, $log) {
	return function(items, filter, userId, pageNum) {
		if (!items || items.length === 0)
			return [];

		if (!filter || CAB.VALID_FILTERS.indexOf(filter) === -1) 
			filter = CAB.ALL;

		if (isNaN(pageNum) || pageNum < 0)
			pageNum = 0;

		var startAt = pageNum * CAB.AGENDA.PAGE_SIZE;			
		var result = [];
		var pageCount = 0;

		items.some(function(item) {	
			if (!meetingAgenda.agendaItemFilters[filter].call({"userId": userId}, item))
				return;

			if (pageCount < startAt) {
				pageCount++;
				return;
			}

			result.push(item);

			// If we have the complete page, no need to do any more
			if (result.length === CAB.AGENDA.PAGE_SIZE)
				return true;
		});

		return result;
	};
}]);

	//
    // THIS CODE IS USED WITHIN THE INDICATOR FORMS TO
    // SELECT TAGS aka INDICATOR GROUPS
    //

    var IndicatorTagControl = Class.create({
    initialize: function(container,indicatorId) {
        this.container = container;
        this.indicatorId = indicatorId;

        var fldCol = document.createElement("div");
        fldCol.className = "input-group-transparent";

        var searchIcon = document.createElement("span");
		Element.extend(searchIcon);
        searchIcon.addClassName("input-group-addon-transparent");
        searchIcon.addClassName("icon-search");

        var refComp = document.createElement("input");
        Element.extend(refComp);
		refComp.setAttribute("type", "search");
		refComp.setAttribute("data-ref-dynamic", "true");
		refComp.setAttribute("aria-label", getMessage("Add Indicator Groups"));
        refComp.addClassName("indicatortag_complete");
        refComp.addClassName("form-control");
        refComp.addClassName("form-control-search");
        refComp.id="indicatortag_complete";
        refComp.autocomplete="off";
        refComp.maxlength="40";
        refComp.placeholder = IndicatorTagControl.FLD_MSG;
        refComp.tabindex="-1";

        this.refComp = refComp;

        var refCompHid = document.createElement("input");
        Element.extend(refCompHid);
        refCompHid.type = "hidden";
        refCompHid.id = refComp.id+"_hidden";

        fldCol.appendChild(refComp);
        fldCol.appendChild(searchIcon);
        fldCol.appendChild(refCompHid);

        //Keep a reference to the container for the tags
        this.tagCol = document.createElement("div");
        this.tagCol.className = "indicatortag_tags";

        var tagClear = document.createElement("div");
        tagClear.className = "indicatortag_clear";

        // Build the top Indicator Group bar
        this.container.appendChild(fldCol);
        this.container.appendChild(this.tagCol);
        this.container.appendChild(tagClear);

        // Add the AJAX completer to the element.  Do this after it's in the DOM
        var completer = new AJAXReferenceCompleter(refComp, refComp.id+'_hidden', 'null', '', 'pa_tags');
        completer.enterSubmits = false;
        refComp.ac.enterSubmits = false;

        // Unregister old event handlers
        refComp.stopObserving("keypress");
        refComp.stopObserving("keydown");
		refComp.stopObserving("focus");
        refComp.stopObserving("blur");

        // Register new event handlers
        refComp.observe("keypress",this._keyPress.bind(this));
        refComp.observe("keydown",this._keyDown.bind(this));
		refComp.observe("focus", this._focus.bind(this));
        refComp.observe("blur", this._blur.bind(this));

        this._loadTags();
    },

    // Wrapper for keydown event handler
    _keyDown: function(event) {
        acReferenceKeyDown(this.refComp, event);
    },

    // Wrapper for keypress event handler
    _keyPress: function(event) {

        // Ref completer handler first
        acReferenceKeyPress(this.refComp, event);

        if (event.keyCode == Event.KEY_RETURN) {
            if (this._addTag()) {
                this.refComp.clear();
				this.refComp.placeholder = '';
                this.refComp.ac.clearKeyValue();
            }

            Event.stop(event);
        }
    },

    _focus: function(event) {
        if (this.refComp._cleared)
            return;

        this.refComp.clear();
		this.refComp.placeholder = '';
        this.refComp._cleared = true;
    },
    _blur: function(event) {
        this.refComp.placeholder = IndicatorTagControl.FLD_MSG;
        this.refComp._cleared = false;
        Event.stop(event);
    },

    _loadTags: function() {
        var tagAjax = new GlideAjax("IndicatorTagsAjax");
        tagAjax.addParam("sysparm_name", "getIndicatorTags");
        tagAjax.addParam("sysparm_indicatorid", this.indicatorId);

        tagAjax.getXML(this._loadTagsCallback.bind(this));
    },

    _loadTagsCallback: function(response) {
        var resp = response.responseXML.getElementsByTagName("result");

        if (resp[0].getAttribute("value") != "success")
            return;

        var jsonStr = response.responseXML.documentElement.getAttribute("answer");
        var tagData = jsonStr.evalJSON();

        for (var i = 0; i < tagData.length; i++) {
            this.tagCol.appendChild((new IndicatorTag(tagData[i].label,tagData[i].sys_id)).getTagElement());
        }
    },

    _addTag: function() {
        var label = this.refComp.value;

        //Check we have a reasonable value
        if (label.length < 2) {
            alert("Indicator Groups must be 2 or more characters");
            return false;
        }

        //Send of the AJAX to see if it can add the Indicator Group
        var tagsAjax = new GlideAjax("IndicatorTagsAjax");
        tagsAjax.addParam("sysparm_name", "addIndicatorTag");
        tagsAjax.addParam("sysparm_indicatorid", this.indicatorId);
        tagsAjax.addParam("sysparm_label", label);

        tagsAjax.getXML(this._addTagCallback.bind(this));
        return true;
    },

    _addTagCallback: function(response) {
        var resp = response.responseXML.getElementsByTagName("result");

        if (resp[0].getAttribute("value") != "success")
            return;

        var jsonStr = response.responseXML.documentElement.getAttribute("answer");
        var tagData = jsonStr.evalJSON();

        this.tagCol.appendChild((new IndicatorTag(tagData.label,tagData.sys_id)).getTagElement());
    },

    type: "IndicatorTagControl"
});

IndicatorTagControl.FLD_MSG = new GwtMessage().getMessage("Add Indicator Groups");

/**
 * IndicatorTag
 * Represents a Indicator Group that has been assigned to the record
 *
 * Handles it's own deletion but not creation as Indicator Group creation is handled via the above class.
 */
var IndicatorTag = Class.create({
    initialize: function(label, sys_id) {
        this.sys_id = sys_id;
        this.label = label;
    },

    /**
     * removeTag(event): AJAX handler for removing this Indicator Group
     */
    removeTag: function(event) {
       // Create AJAX request that removes the Indicator Group.
       var tagAjax = new GlideAjax("IndicatorTagsAjax");
       tagAjax.addParam("sysparm_name","removeIndicatorTag");
       tagAjax.addParam("sysparm_sysid",this.sys_id);
       tagAjax.getXML(this.removeTagCallback.bind(this));
    },

    /**
     *removeTagCallback(response): AJAX calback method for removing this Indicator Group
     */
    removeTagCallback: function(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
        var resp = response.responseXML.getElementsByTagName("result");
        if (resp[0].getAttribute("value") != "success") {
            alert("Failed to remove Indicator Group. Already Removed?");
            return;
        }

        this._tagEl.remove();
    },

    /**
     * getTagElememt(): Returns a configured dom element for the Indicator Group
     */
    getTagElement: function() {
        var tagEl = document.createElement("div");
        Element.extend(tagEl);
        tagEl.addClassName("indicatortag_control_tag");
        tagEl.id = "tag:" + this.sys_id;

        var tagLabel = document.createElement("span");
        Element.extend(tagLabel);
        tagLabel.addClassName("indicatortag_tag_name");
        tagLabel.appendChild(document.createTextNode(this.label));

        tagEl.appendChild(tagLabel);

        var tagRm = document.createElement("a");
        Element.extend(tagRm);
        tagRm.addClassName("indicatortag_delete_button");
        tagRm.setAttribute("role", "button");
        var removeTagMsg = getMessage('Remove tag: ')
        tagRm.setAttribute("aria-label", removeTagMsg + this.label);
        tagRm.setAttribute("tabindex", "0");
        var tagRmIcon = document.createElement("span");
        Element.extend(tagRmIcon);
        tagRmIcon.addClassName("text-icon icon-cross");
        tagRm.appendChild(tagRmIcon);

        // Bind this object to a click on the X remove Indicator Group
        tagRm.observe("click", this.removeTag.bind(this));
        tagEl.appendChild(tagRm);

        // Cleaner than assigning at the beginning.
        this._tagEl = tagEl;
        return this._tagEl;
    },

    type: "IndicatorTag"
});
