 * @typedef {import("..").BreadcrumbItemClicked} BreadcrumbItemClicked
 * @typedef {import("..").BrowserBackRouteInfo} BrowserBackRouteInfo
 * @typedef {import("..").ChangedScreenContentArgs} ChangedScreenContentArgs
 * @typedef {import("..").BreadcrumbDataPropertyChanged} BreadcrumbDataPropertyChanged
 * @typedef {import("..").SelectedContentWithUrlState} SelectedContentWithUrlState
 */
function include() {

	const resizeMinRatio = {
		minRatio: 25,
		minPixels: 16
	};
	const resizeDefaultDividerState = {dividerPosition: 0, isExpanded :true};
	const REAPPLY_SUBTOOLBAR_OPTIONS = 'BREADCRUMB_APPSHELL#REAPPLY_SUBTOOLBAR_OPTIONS';
	const defaultI18n = {toggle: "Toggle subtoolbar content", expand: "Expand", collapse: "Collapse"};

	class BreadcrumbUtils {
