		static getOrderedCacheKey(routeInfo, selectedToolbarItem) {
			const { fields = {}, params = {} } = routeInfo;
			/**
			 * @type {Record<string, any>}
			 */
			const orderedFields = {};
			Object.keys(fields).sort().forEach(key => {
				orderedFields[key] = fields[key];
			});
			/**
			 * @type {Record<string, any>}
			 */
			const ordereredParams = {};
			Object.keys(params).sort().forEach(key => {
				ordereredParams[key] = params[key];
			});
			return {
				toolbarRoute: selectedToolbarItem.routeInfo.route,
				route: routeInfo.route,
				fields: orderedFields,
				params: ordereredParams,
			};
		}
		/**
