function include({ imports }) {
    const {
        'sn_vtb.laneHandlers': laneHandlersInclude,
        'sn_vtb.utils': utilsInclude,
        'sn_vtb.moveCard': moveCard
    } = imports;

    const { getQuery } = laneHandlersInclude();
    const { cloneDeep } = utilsInclude();
	/**
	 * This method is used to transform the record array into an object.
	 * @param cardData
	 */
    const transformCardData = (cardData) => {
        let record = {};
        cardData.id = cardData.sys_id;
        cardData.record.forEach((field) => {
            let fieldValue;
            if (field.value.hasOwnProperty('string')) {
                fieldValue = field.value.string;
            } else if (field.value.hasOwnProperty('boolean')) {
                fieldValue = field.value.boolean;
            } else {
                fieldValue = field.value;
                if (fieldValue.value && fieldValue.value.length === 1) fieldValue.value = fieldValue.value[0];
            }

            record[field.name] = fieldValue;
        });
        cardData.record = record;

        if (cardData.sys_updated_on && cardData.sys_updated_on.hasOwnProperty('date_time'))
            cardData.sys_updated_on = cardData.sys_updated_on.date_time;

        return cardData;
    };

    // I'm not so sure whether this method is necessary. This method was used in classic VTB's DnD flow. Though in seismic
    // VTB, The GraphQL query anyway specifically asks for labels and due_date_field in the output so the data should always
    // contain these props. Opinions ? PS: I don't know when these properties might be missing for some reason.
    const addCardProperties = (cardData, board) => {
        const previousCardData = board.cards.find((c) => c.id === cardData.sys_id);

        if (previousCardData) {
            const dueDateField = board.due_date_field;

            if (
                dueDateField &&
                previousCardData.record.hasOwnProperty(dueDateField) &&
                !cardData.record.hasOwnProperty(dueDateField)
            )
                cardData.record[dueDateField] = previousCardData.record[dueDateField];

            if (previousCardData.hasOwnProperty('labels') && !cardData.hasOwnProperty('labels'))
                cardData.labels = previousCardData.labels;
        }
        return cardData;
    };

    function getUser(userId, users) {
        return users.find((user) => userId === user.sys_id);
    }

    const addUsersToCard = (card, boardUsers) => {
        // Card users
        let users = {};
        const {
            assigned_to
        } = card.record;
        users.primary_assignees = [];
        if (assigned_to && assigned_to.value) {
            let user = getUser(assigned_to.value, boardUsers);
            if(user){
                if(user.avatar) assigned_to.avatar = user.avatar;
                //Todo: Fetch for users not on board
                users.primary_assignees.push(user);
            }
        }

        // Additional Assignee list
        const {
            additional_assignee_list
        } = card.record;
        users.additional_assignees = [];
        if (additional_assignee_list) {
            if (typeof additional_assignee_list.value === 'string') {
                const additionalAssignee = additional_assignee_list.value;
                additional_assignee_list.value = [additionalAssignee];
            }
            additional_assignee_list.value.forEach((userId) => {
                let user = getUser(userId, boardUsers);
                if(user) users.additional_assignees.push(user);
            });
        }

        card.users = users;
    };
    const reOrderCards = (board, payload) => {
        const updatedCardIds = payload.cardIdList;
        const cards = board.cards;
        const reorderedCards = cards.filter((card) => updatedCardIds.indexOf(card.sys_id) > -1);
        const cardsCopy = [...cards];

        reorderedCards.forEach((card) => {
            const updatedCard = cloneDeep(card);
            updatedCard.order = updatedCardIds.indexOf(updatedCard.sys_id);
            const index = cards.findIndex((c) => c.sys_id === updatedCard.sys_id);
            cardsCopy.splice(index, 1, updatedCard);
        });

        let updatedBoard = {
            ...board,
            cards: cardsCopy
        }
        return sortCards(updatedBoard);
    }

    const sortCards = (board) => {

        let newCards = [];
        board.lanes.forEach((lane) => {
            newCards = newCards.concat(
                board.cards
                    .filter((card) => card.lane_id === lane.sys_id)
                    .sort((a, b) => a.order - b.order)
                    .map((card, cardIndex) => {
                        card.order = cardIndex;
                        return card;
                    })
            );
        });

        // As we are maintaining one card array and not deleting cards after lane deleted because
        // It card can be restored later. So there will be a case where we have deleted lane and card
        // belong to that lane will be present in cards array and should be shown in archive card
        // panel. Since we are dispatching this action on card move we need to handle this case
        // separately.
        const laneIds = board.lanes.map((lane) => {
            return lane.sys_id;
        });

        let cardsInDeletedLane = [];
        board.cards.forEach((card) => {
            if (!laneIds.includes(card.lane_id)) cardsInDeletedLane.push(card);
        });

        const updatedCards = newCards.concat(cardsInDeletedLane);

        return {
            ...board,
            cards: updatedCards
        };
    }

    const constructVtbCardFormData = (boardState,payload ,cardAdded) => {
        const newCardData = {
            ...payload.card,
            isVisible: true,
            recordId: cardAdded ?  "-1" : payload.card.task_id 
        };
         const lane = boardState.lanes.find((lane) => lane.sys_id === payload.card.lane_id);
         const swimlane = payload.card.swim_lane_id ? boardState.swimlanes.find((smlane) => smlane.sys_id === payload.card.swim_lane_id) : null;
        if (lane) {
            lane.options = {
                table: boardState.table,
                lane_field: boardState.field
            }
        }
        if (swimlane) {
            swimlane.options = {
                table: boardState.table,
                swimlane_field: boardState.swim_lane_field
            }
        }

        const swimlaneQuery = swimlane ? getQuery(swimlane) : "";
        const laneQuery = lane ? getQuery(lane) : "";

        let query = `${laneQuery}`;
        if (swimlaneQuery) {
            query = `${laneQuery}^${swimlaneQuery}`;
        }
        newCardData.query = query;
        return newCardData;
    }

    return {
       moveCard,
        transformCardData,
        addCardProperties,
        addUsersToCard,
        reOrderCards,
        constructVtbCardFormData
    }
}

function include() {
	return {
		getPayload (event) {
			const {eventOrigin, currentTabParFilters} = event.payload;
			let payload;

			if (eventOrigin === 'NOW_VIS_WRAPPER#CLICKED') {
				const {
					params: {
						table,
						query,
						indicatorSysid,
						kpiSysId,
						breakdowns,
						aggregate,
						domain,
						scoreDate,
						dataSourceType,
					},
					title,
				} = event.payload;

				if (dataSourceType === 'metricBase') {
					const {
						params: {
							dateRange: {
								startTime,
								endTime,
							},
							ciSysId,
							ciType,
						},
					} = event.payload;
					payload = {
						route: 'metric-explorer', 
						params: {
							ciSysId,
							ciType,
							startTime,
							endTime,
							chartIndex: -1,
						},
						title: 'Metric Explorer',
					};
				} else if (dataSourceType === 'hlaElasticSearchComponents') {
					payload = {
						route: 'log-viewer',
						params: {},
						title: 'Log viewer',
					};
				} else if (dataSourceType === 'table') {
					const coreUIParam = `${table}_list.do?sysparm_query=${query}`;
					payload = {
						context: {
							path: 'now/nav/ui'
						},
						params: {
							target: coreUIParam,
						},
						route: 'classic',
						title: title || table,
					};
				} else if (kpiSysId) {
					payload = {
						fields: {
							dataSourceId: kpiSysId,
							dataSource: 'analyticalmodel',
						},
						params: {
							breakdowns,
							aggregate,
							scoreDate,
							domain,
						},
						route: 'kpi-details',
						title: title || 'KPI Details',
					};
				} else if (dataSourceType === 'indicator') {
					payload = {
						fields: {
							dataSourceId: indicatorSysid,
							dataSource: 'kpid',
						},
						params: {
							breakdowns,
							aggregate,
							scoreDate,
							domain,
						},
						route: 'kpi-details',
						title: title || 'KPI Details',
					};
					if (currentTabParFilters) {
						const {parFilters} = currentTabParFilters;
						payload.params.startDate = parFilters.length ? parFilters[0].start : null;
						payload.params.endDate = parFilters.length ? parFilters[0].end : null;
					}
				}
			}

			if (eventOrigin === 'NOW_RECORD_LIST_CONNECTED#ROW_CLICKED') {
				const {table, row, title} = event.payload;
				const coreUIParam = `${table}.do?sys_id=${row?.sys_id?.value || '-1'}`;
				payload = {
					context: {
						path: 'now/nav/ui'
					},
					params: {
						target: coreUIParam,
					},
					route: 'classic',
					title: title || 'Form Record',
				};
			}

			if (eventOrigin === 'SN_PAR_CALENDAR_CONNECTED#EVENT_CLICKED') {
				const {table, title, sysId} = event.payload.event;

				const coreUIParam = `${table}.do?sys_id=${sysId || '-1'}`;
				payload = {
					context: {
						path: 'now/nav/ui'
					},
					params: {
						target: coreUIParam,
					},
					route: 'classic',
					title: title || 'Form Record',
				};
			}

			if (eventOrigin === 'NOW_RECORD_LIST_CONNECTED#VIEW_ALL_CLICKED') {
				const {table, query, listTitle, currentTabParFilters, listFilters} = event.payload;
				const filterQueries = currentTabParFilters && currentTabParFilters.encodedQueries && currentTabParFilters.encodedQueries[table] ? `${currentTabParFilters.encodedQueries[table]}^` : '';
				let queryWithFilterClauses = `${filterQueries}${query || ''}`;
				if (listFilters)
					queryWithFilterClauses = `${queryWithFilterClauses}^${listFilters}`;

				const coreUIParam = `${table}_list.do?sysparm_query=${queryWithFilterClauses}`;
				payload = {
					context: {
						path: 'now/nav/ui'
					},
					params: {
						target: coreUIParam,
					},
					route: 'classic',
					title: listTitle || 'List',
				};
			}

			return payload;
		},
	};
}

function include({ imports }, api, helpers) {

    // -------------------------------------------------------------------------------- //

    const NodeIcon = {
        User: "user-fill",
        Group: "user-group-fill",
        Role: "user-tag-fill"
    }

    // -------------------------------------------------------------------------------- //

    class Node {

        constructor(node) {
            Object.assign(this, node);
            this.children = [];
        }

        addChild(node) {
            if (!this.children.includes(node.id)) {
                this.children.push(node.id);
            }
        }

        getEdges() {
            let edges = this.children.map(childId => {
                return {
                    id: this.id + ':' + childId,
                    sourceId: this.id,
                    targetId: childId
                }
            });
            return edges;
        }

        getNodeData(conf) {
            conf = conf || {};
            var data = {
                id: this.id,
                nodeType: this.type,
                viewData: {
                    icon: NodeIcon[this.type],
                    primaryLabel: this.name,
                    secondaryLabel: this.type
                }
            };

            if (!isEmpty(this.status))
                data.viewData.status = this.status;

            if (!isEmpty(this.statusIcon))
                data.viewData.statusIcon = this.statusIcon;

            if (!isEmpty(this.tooltipContent))
                data.tooltipContent = this.tooltipContent;

            if (conf.leafNodeStatus) {
                data.viewData.status = this.children.length == 0 ? conf.leafNodeStatus : "none"
            }
            return data;
        }

    }

    // -------------------------------------------------------------------------------- //

    class RoleNode extends Node {
        constructor(node) {
            super(node);
            this.type = "Role";
        }
        getNodeData(conf) {
            let node = super.getNodeData(conf);
            if (this.children.length > 0) {
                node.isGroup = true;
                node.viewData.groupCount = this.children.length;
            }
            return node;
        }
    }

    // -------------------------------------------------------------------------------- //

    class GroupNode extends Node {

        constructor(node) {
            super(node);
            this.type = "Group";
        }

        getNodeData(conf) {
            let node = super.getNodeData(conf);
            node.isGroup = true;
            node.viewData.groupCount = this.children.length;
            return node;
        }
    }

    // -------------------------------------------------------------------------------- //

    class UserNode extends Node {
        constructor(node) {
            super(node);
            this.type = "User";
        }
    }

    // -------------------------------------------------------------------------------- //

    class NodeMap {

        _nodeMap;

        constructor() {
            this._nodeMap = new Map();
        }

        getRoleMap(user, nodes, conf) {
            this.conf = conf;
            this.createNode(user);
            this._createNodes(nodes);
            this._connectNodes(user.id, nodes);
            return this._generateRoleMap();
        }

        _createNodes(nodes) {
            nodes.forEach(node => {
                this.createNode(node);
                node.containingGroups.forEach(group => this.createNode(group));
            });
        }

        _connectNodes(userId, nodes) {
            nodes.forEach(node => {

                if (node.assignmentTypes.includes('DIRECT')) {
                    this.addChild(userId, node.id);
                }

                node.containedRoles.forEach(childNodeId => this.addChild(node.id, childNodeId));

                node.containingGroups.forEach(group => {
                    this.addChild(userId, group.id);
                    this.addChild(group.id, node.id);
                });
            });
        }

        _generateRoleMap() {
            var nodes = [];
            var edges = [];
            this._nodeMap.forEach(node => {
                nodes.push(node.getNodeData(this.conf));
                edges.push(...node.getEdges())
            });
            return {
                nodes: nodes,
                edges: edges
            };
        }

        addChild(parentId, childId) {
            this._nodeMap.get(parentId).addChild(this._nodeMap.get(childId));
        }

        getNode(id) {
            return this._nodeMap.get(id);
        }

        createNode(node) {
            if (this._nodeMap.has(node.id))
                return null;
            let n = null;
            let type = node.type || "";
            switch (type.toLowerCase()) {
                case "role":
                    n = new RoleNode(node);
                    break;
                case "group":
                    n = new GroupNode(node);
                    break;
                default:
                    n = new UserNode(node)
            }
            if (!n)
                return null;
            this._nodeMap.set(node.id, n);
            return node;
        }

    }

    // -------------------------------------------------------------------------------- //

    class ViewChangesNodeMap extends NodeMap {
        getRoleMap(user, nodes, conf) {
            let k = {
                ADDED: {
                    status: "positive",
                    icon: null
                },
                REMOVED: {
                    status: "critical",
                    icon: "close-fill"
                },
                UNCHANGED: {
                    status: "low",
                    icon: "exclamation-fill"
                }
            }
            let parsedNodes = nodes.map(obj => {
                let node = {
                    ...obj.accessEntity,
                    containingGroups: [],
                };
                let status = obj.simulatorChangeAction;
                if (status != "NONE") {
                    node.status = k[status].status;
                    node.statusIcon = k[status].icon;
                    let nodeTypeLabel = this._changeProperCase(node.type);
                    let tms = api.state.translatedMessage;
                    node.tooltipContent = node.name + "<br/> [" + tms[nodeTypeLabel] +  " " +  tms['being'] + " " + tms[status.toLowerCase()] + "]";
                }
                return node;
            });
            return super.getRoleMap(user, parsedNodes, conf);
        }

        _changeProperCase(type) {
            return type.charAt(0) + type.slice(1).toLowerCase();
        }

        getStats(nodes) {
        	let count = {
                NONE: 0,
                ADDED: 0,
                REMOVED: 0,
                UNCHANGED: 0
            };
            nodes.forEach(node => count[node.simulatorChangeAction]++);
            return count;
        }

        selectNode(nodeId, align = false, statVar = "mapData") {
             let obj = {
                 selectedNodeId: [nodeId],
             };
             if (align)
                 obj.alignNodeId = nodeId;
             api.setState(statVar, ctx => ({
                ...ctx.currentValue,
                ...obj
             }));
         }

    }


    // -------------------------------------------------------------------------------- //

    const generateRoleMapData = (user, nodes, conf) => {
        return new NodeMap().getRoleMap(user, nodes, conf);
    }

    // -------------------------------------------------------------------------------- //

    const updateMap = (userObj, data, props) => {

        const { EmptyState } = imports["sn_access_analyzer.UserComparisonUtil"](api, helpers);

        var conf = Object.assign({
            leafNodeStatus: "positive"
        }, props);

        var mapData;
        const emptyState = api.state[userObj.type + "EmptyState"];
        if (data == null || data.length == 0) {
            mapData = { nodes: [], edges: [] };

            emptyState.show = true;
            userObj.showLegend = false;
            var err = {
                heading: emptyState.messages.noRoles.heading,
                content: emptyState.messages.noRoles.content
            }
            EmptyState.show(err, userObj.type + "EmptyState");

        } else {
            userObj.showLegend = true;
            emptyState.show = false;
            mapData = generateRoleMapData({
                id: userObj.id,
                name: userObj.name
            }, data, conf);
        }
        userObj.loadingState = false;
        api.setState(userObj.type, userObj);
        var tms = api.state.translatedMessages;
        mapData.nodes.forEach(node => node.viewData.secondaryLabel = tms[node.viewData.secondaryLabel]);
        api.setState(userObj.type + "Map", mapData);
    }

    // -------------------------------------------------------------------------------- //
    function showServerError() {
        const { EmptyState } = imports["sn_access_analyzer.UserComparisonUtil"](api, helpers);
        var baseUser = api.state.baseUser;
        var modelUser = api.state.modelUser;

        baseUser.showLegend = false;
        baseUser.loadingState = false;

        modelUser.showLegend = false;
        modelUser.loadingState = false;

        api.setState("baseUser", baseUser);
        api.setState("modelUser", modelUser);
        EmptyState.showServerErrorMessage("baseUserEmptyState");
        EmptyState.showServerErrorMessage("modelUserEmptyState");
    }

    const isEmpty = (val) => val == undefined || val == null || val.trim().length == 0;

    return {
        generateRoleMapData,
        ViewChangesNodeMap,
        updateMap,
        showServerError
    }

}

