function include({
    imports
}) {

    class OnCallShiftCreationUtilsSNC {
        static async validateNumber(api, helpers, id, value, isCreateShift) {
            const numberRegex = new RegExp(/^[1-9]\d*$/);
            if (!numberRegex.test(value)) {
                if (isCreateShift) {
                    api.setState('alertMessage', await helpers.translate("Duration cannot be less than zero or equal to zero."))
                    api.setState('hideAlert', false);
                } else {
                    let errors = api.state.errors;
                    errors[id] = {
                        status: 'critical',
                        icon: 'info-circle-outline',
                        content: await helpers.translate('Duration cannot be less than zero or equal to zero.'),
                        action: {
                            type: 'dismiss'
                        }
                    };
                    api.setState('errors', {
                        ...errors
                    });
                }
                api.setState('repeats_number_invalid', true);
            } else {
                if (!isCreateShift) {
                    let errors = api.state.errors;
                    if (errors.hasOwnProperty(id))
                        delete errors[id];
                    api.setState('errors', {
                        ...errors
                    });
                } else
                    api.setState('hideAlert', true);
                api.setState('repeats_number_invalid', false);
            }
        };

        static async validateDaysSelected(api, helpers, id, daySelected, isCreateShift) {
            const repeat_type = api.state.repeat_type;
            if (repeat_type == 'week') {
                let selected_days = daySelected || api.state.day_selected;
                const day_keys = Object.keys(selected_days);
                let at_least_one = false;
                for (let day = 0; day < day_keys.length; day++) {
                    if (selected_days[day_keys[day]]) {
                        at_least_one = true;
                        break;
                    }
                }
                if (!at_least_one) {
                    if (isCreateShift) {
                        api.setState('alertMessage', await helpers.translate("Select at least one day of the week."));
                        api.setState('hideAlert', false);
                    } else {
                        let errors = api.state.errors;
                        errors[id] = {
                            status: 'critical',
                            icon: 'info-circle-outline',
                            content: await helpers.translate('Select at least one day of the week.'),
                            action: {
                                type: 'dismiss'
                            }
                        };
                        api.setState('errors', {
                            ...errors
                        });
                    }
                } else if (!isCreateShift) {
                    let errors = api.state.errors;
                    if (errors.hasOwnProperty(id))
                        delete errors[id];
                    api.setState('errors', {
                        ...errors
                    });
                } else
                    api.setState('hideAlert', true);
            }
        };

        static async repeatsUntilValidation(api, helpers, shift_start, shift_end, repeats_until, isCreateShift) {
            if (!isCreateShift) {
                var errors = api.state.errors;
                if (shift_end && repeats_until && (shift_end > repeats_until)) {
                    errors["repeats_until_shift_ends"] = {
                        status: 'critical',
                        icon: 'info-circle-outline',
                        content: await helpers.translate('Shift ends can not be later than repeat until.'),
                        action: {
                            type: 'dismiss'
                        }
                    };
                    api.setState('repeats_until_invalid', true);
                } else {
                    delete errors['repeats_until_shift_ends'];
                    api.setState('repeats_until_invalid', false);
                }
                if (shift_start && repeats_until && (shift_start > repeats_until)) {
                    errors["repeats_until_shift_starts"] = {
                        status: 'critical',
                        icon: 'info-circle-outline',
                        content: await helpers.translate('Shift starts can not be later than repeat until.'),
                        action: {
                            type: 'dismiss'
                        }
                    };
                    api.setState('repeats_until_invalid', true);
                } else {
                    delete errors['repeats_until_shift_starts'];
                    api.setState('repeats_until_invalid', false);
                }
                api.setState('errors', {
                    ...errors
                });
            } else {
                if (repeats_until && shift_end && (shift_end.split("T")[0] > repeats_until)) {
                    api.setState('alertMessage', await helpers.translate('Shift ends can not be later than repeat until.'));
                    api.setState('hideAlert', false);
                } else if (repeats_until && shift_start && (shift_start.split("T")[0] > repeats_until)) {
                    api.setState('alertMessage', await helpers.translate('Shift starts can not be later than repeat until.'));
                    api.setState('hideAlert', false);
                } else
                    api.setState('hideAlert', true);
            }
        }

        static async dateValidation(api, helpers, shift_start, shift_end, repeat_until_iso, validateRepeatsUntil, isCreateShift) {
            api.setState('shift_ends_invalid', false);
            api.setState('shift_starts_invalid', false);
            if (!isCreateShift) {
                let errors = api.state.errors;
                if (!shift_end) {
                    errors["shift_ends_empty"] = {
                        status: 'critical',
                        icon: 'info-circle-outline',
                        content: await helpers.translate('Shift ends can not be empty.'),
                        action: {
                            type: 'dismiss'
                        }
                    };
                } else
                    delete errors["shift_ends_empty"];
                if (!shift_start) {
                    errors["shift_starts_empty"] = {
                        status: 'critical',
                        icon: 'info-circle-outline',
                        content: await helpers.translate('Shift starts can not be empty.'),
                        action: {
                            type: 'dismiss'
                        }
                    };
                } else
                    delete errors["shift_starts_empty"];
            }

            if (shift_start && shift_end && ((shift_end < shift_start) || (shift_end == shift_start))) {
                if (isCreateShift) {
                    if (shift_end < shift_start)
                        api.setState('alertMessage', await helpers.translate("The end date and time must be after the start date and time"));
                    else if (shift_end == shift_start)
                        api.setState('alertMessage', await helpers.translate("End date should be more than start date"));
                    api.setState('hideAlert', false);
                } else {
                    let errors = api.state.errors;
                    errors["shift_ends"] = {
                        status: 'critical',
                        icon: 'info-circle-outline',
                        content: await helpers.translate('Shift start time cannot be later than or equal to shift end time.'),
                        action: {
                            type: 'dismiss'
                        }
                    };
                    api.setState('errors', {
                        ...errors
                    });
                }
                api.setState('shift_ends_invalid', true);
                api.setState('shift_starts_invalid', true);
                return;
            } else {
                if (!isCreateShift) {
                    let errors = api.state.errors;
                    if (errors.hasOwnProperty("shift_ends"))
                        delete errors["shift_ends"];
                    api.setState('errors', {
                        ...errors
                    });
                } else
                    api.setState('hideAlert', true);
                api.setState('shift_ends_invalid', false);
                api.setState('shift_starts_invalid', false);
            }

            if (validateRepeatsUntil)
                await this.repeatsUntilValidation(api, helpers, shift_start, shift_end, repeat_until_iso, isCreateShift);
        };

        static getFormattedDateOrTime(dateTimeStr, dateFormat, timeFormat, fromTZ, toTZ, getDate) {
            const {
                getNowDateTime
            } = imports['sn_uib_on_call.OnCallDateTimeUtils']();
            return getNowDateTime(dateTimeStr, dateFormat.toUpperCase() + ' ' + timeFormat, fromTZ, toTZ).format((getDate) ? dateFormat.toUpperCase() : timeFormat);
        };

        static async handleValidations(api, event, helpers, isCreateShift) {
            const {
                NowDateTime
            } = imports['sn_uxf.core_libraries']();

            switch (event.elementId) {
                case 'repeats_number':
                    await this.validateNumber(api, helpers, event.elementId, event.payload.fieldValue, isCreateShift);
                    break;
                case 'shift_ends':
                    await this.dateValidation(api, helpers, api.state.shift_starts_iso, event.payload.isoDate, api.state.repeats_until_iso, true, isCreateShift);
                    break;
                case 'shift_starts':
                    await this.dateValidation(api, helpers, event.payload.isoDate, api.state.shift_ends_iso, api.state.repeats_until_iso, true, isCreateShift);
                    break;
                case 'repeats_until':
                    await this.repeatsUntilValidation(api, helpers, api.state.shift_starts_iso, api.state.shift_ends_iso, event.payload.isoDate, isCreateShift);
                    break;
                case 'day':
                    let daySelected = api.state.day_selected;
                    daySelected[event.context.item.value] = !daySelected[event.context.item.value];
                    api.setState('day_selected', {
                        ...daySelected
                    });
                    await this.validateDaysSelected(api, helpers, event.elementId, daySelected, isCreateShift);
                    break;
                case 'all_day_shift':
                    if (event.payload.value) {
                        if (isCreateShift) {
                            let startDate = (api.state.shift_starts) || "";
                            startDate = startDate ? this.getFormattedDateOrTime(startDate, api.context.session.user.dateFormat, api.context.session.user.timeFormat, api.state.selected_timezone, api.state.selected_timezone, true) : api.state.start_date;
                            let startDatIso = (api.state.shift_starts_iso) || "";
                            startDatIso = startDatIso ? startDatIso.slice(0, 10) : api.state.start_date_iso;
                            api.setState('start_date', startDate);
                            api.setState('start_date_iso', startDatIso);
                        } else {
                            var errors = api.state.errors;
                            api.setState('start_date', this.getFormattedDateOrTime(api.state.shift_starts, api.context.session.user.dateFormat, api.context.session.user.timeFormat, api.state.selected_timezone, api.state.selected_timezone, true));
                            api.setState('start_date_iso', api.state.shift_starts_iso.slice(0, 10));
                            if (errors.hasOwnProperty("shift_ends"))
                                delete errors["shift_ends"];
                            delete errors['repeats_until_shift_ends'];
                            api.setState('errors', {
                                ...errors
                            });
                        }
                    } else {
                        if (isCreateShift) {
                            const startDate = (api.state.start_date && api.state.shift_starts) ? (api.state.start_date + ' ' + this.getFormattedDateOrTime(api.state.shift_starts, api.context.session.user.dateFormat, api.context.session.user.timeFormat, api.state.selected_timezone, api.state.selected_timezone, false)) : "";
                            const startDateIso = (api.state.start_date_iso && api.state.shift_starts_iso) ? (api.state.start_date_iso + api.state.shift_starts_iso.slice(10)) : "";
                            api.setState('shift_starts', startDate);
                            api.setState('shift_starts_iso', startDateIso);
                            await this.dateValidation(api, helpers, startDateIso, api.state.shift_ends_iso, "", false, isCreateShift);
                        } else {
                            api.setState('shift_starts', api.state.start_date + ' ' + this.getFormattedDateOrTime(api.state.shift_starts, api.context.session.user.dateFormat, api.context.session.user.timeFormat, api.state.selected_timezone, api.state.selected_timezone, false));
                            api.setState('shift_starts_iso', api.state.start_date_iso + api.state.shift_starts_iso.slice(10));
                            api.setState('shift_ends', api.state.start_date + ' ' + this.getFormattedDateOrTime(api.state.shift_ends, api.context.session.user.dateFormat, api.context.session.user.timeFormat, api.state.selected_timezone, api.state.selected_timezone, false));
                            api.setState('shift_ends_iso', api.state.start_date_iso + api.state.shift_ends_iso.slice(10));
                            await this.dateValidation(api, helpers, api.state.start_date_iso + api.state.shift_starts_iso.slice(10), api.state.start_date_iso + api.state.shift_ends_iso.slice(10), "", false, isCreateShift);
                            await this.repeatsUntilValidation(api, helpers, api.state.shift_starts_iso, api.state.shift_ends_iso, api.state.repeats_until_iso, isCreateShift);
                        }
                    }
                    break;
                case 'repeats_type':
                    if (event.payload.value === 'never') {
                        if (!isCreateShift) {
                            const errors = api.state.errors;
                            if (errors.hasOwnProperty('repeats_number'))
                                delete errors['repeats_number'];
                            delete errors['repeats_until_shift_ends'];
                            delete errors['repeats_until_shift_starts'];
                            api.setState('repeats_until_invalid', false);
                            api.setState('repeats_until', "");
                            api.setState('repeats_until_iso', "");
                            api.setState('errors', {
                                ...errors
                            });
                            if (errors.hasOwnProperty('day'))
                                delete errors['day'];
                            api.setState('errors', {
                                ...errors
                            });
                        } else
                            api.setState("hideAlert", true);
                        api.setState('repeats_until', "");
                        api.setState('repeats_until_iso', "");
                        await this.dateValidation(api, helpers, api.state.shift_starts_iso, api.state.shift_ends_iso, api.state.repeats_until_iso, false, isCreateShift);
                    } else if (event.payload.value === 'day') {
                        if (!isCreateShift) {
                            const errors = api.state.errors;
                            if (errors.hasOwnProperty('day'))
                                delete errors['day'];
                            api.setState('errors', {
                                ...errors
                            });
                        }
                        await this.validateNumber(api, helpers, 'repeats_number', api.state.repeat_number, isCreateShift);
                        await this.dateValidation(api, helpers, api.state.shift_starts_iso, api.state.shift_ends_iso, api.state.repeats_until_iso, true, isCreateShift);
                    } else {
                        await this.validateNumber(api, helpers, 'repeats_number', api.state.repeat_number, isCreateShift);
                        await this.validateDaysSelected(api, helpers, 'day', null, isCreateShift);
                        await this.dateValidation(api, helpers, api.state.shift_starts_iso, api.state.shift_ends_iso, api.state.repeats_until_iso, true, isCreateShift);
                    }
                    break;
                case 'start_date':
                    if (event.payload.value && api.state.shift_starts && api.state.shift_ends) {
                        var shift_starts = event.payload.value + api.state.shift_starts.substring(api.state.start_date.length);
                        var shift_ends = event.payload.value + api.state.shift_ends.substring(api.state.start_date.length);
                        api.setState('shift_starts', shift_starts);
                        api.setState('shift_ends', shift_ends);
                    }
                    if (event.payload.isoDate && api.state.shift_starts_iso && api.state.shift_ends_iso) {
                        var shift_starts_iso = event.payload.isoDate + api.state.shift_starts_iso.slice(10);
                        var shift_ends_iso = event.payload.isoDate + api.state.shift_ends_iso.slice(10);
                        api.setState('shift_starts_iso', shift_starts_iso);
                        api.setState('shift_ends_iso', shift_ends_iso);
                    }
                    await this.repeatsUntilValidation(api, helpers, shift_starts_iso, '', api.state.repeats_until_iso, isCreateShift);
                    break;
                case 'extra_start_date_time':
                    await this.dateValidation(api, helpers, event.payload.isoDate, api.state.shift_ends_iso, "", false, isCreateShift);
                    break;
                case 'extra_end_date_time':
                    await this.dateValidation(api, helpers, api.state.shift_starts_iso, event.payload.isoDate, "", false, isCreateShift);
                    break;
            }
        };
    }
    return OnCallShiftCreationUtilsSNC;
}

