        }

        /**
         * Returns configuration for work persona dropdown
         * @param {object} helpers
         * @return {object}
         */
        static async getWorkPersonaConfig(helpers) {
            return {
                "items": [{
                        "id": "your_work",
                        "label": await helpers.translate("Your work")
                    },
                    {
                        "id": "your_team_work",
                        "label": await helpers.translate("Your team's work")
                    }
                ],
                "defaultSelected": ["your_work"]
            }
        }

        static containsRole(userRoles, widgetRoles) {
            if(userRoles.includes("admin"))
                return true;
            return userRoles.some(item => widgetRoles.includes(item));
        }

        static getRangeObject(query, key) {
            var ans = {}
            var queryTokens = query.split('^');
            for (var index in queryTokens) {
                var token = queryTokens[index];
                if (token.indexOf(key) == -1)
                    continue;
                token = token.replace('=', '');
                if (token.indexOf('<') != -1)
                    ans.end_value = token.substring(token.indexOf('<') + 1)
                else
                    ans.start_value = token.substring(token.indexOf('>') + 1)
            }
            return ans;
        }

        static getRangeObjectFromQuerysegment(querySegment, key) {
            var ans = {}
            for(var i=0; i<querySegment.length; i++) {
                var segment = querySegment[i];
                if(segment.field === key) {
                    if(segment.operator.includes(">")) ans.start_value = segment.values[0];
                    else if(segment.operator.includes("<")) ans.end_value = segment.values[0];
                }
            }
            return ans;
        }

        static async getSLAHeader(range, report_ranges, helpers) {
            for (var index in report_ranges) {
                if (range.end_value == report_ranges[index].upper_value_duration)
                    return (await helpers.translate("Incident SLA time remaining - {0}", report_ranges[index].label));
            }
            return (await helpers.translate('Incident SLAs'));
        }

        static getFieldMap() {
            const fieldMap = {
                'State': 'state',
                'Priority': 'priority',
                'Actual time left': 'time_left',
                'Task type': 'sys_class_name'
            };
            return fieldMap;
        }

        static async fetchTitleForSLA(query, reportRanges, helpers, querySegment) {
           var range = {};
            if(querySegment)
                range = this.getRangeObjectFromQuerysegment(querySegment, 'time_left'); 
            else
                range = this.getRangeObject(query, 'time_left');

            return (await this.getSLAHeader(range, reportRanges, helpers));
        }

        static async fetchTitle(table, selectedField, groupByField, groupMode = false) {
            if(selectedField == undefined){
                 selectedField = 'all';
                }
            const labelMap = {
                'incident': {
                    'state': {
                        'all':'Incidents',
                        '1': 'New incidents',
                        '2': 'In Progress incidents',
                        '3': 'On Hold incidents',
                        '6': 'Resolved incidents'
                    },
                    'priority': {
                        'all':'Unassigned incidents',
                        '1': 'P1 unassigned incidents',
                        '2': 'P2 unassigned incidents',
                        '3': 'P3 unassigned incidents',
                        '4': 'P4 unassigned incidents',
                        '5': 'P5 unassigned incidents'
                    }
                },
                'sc_task': {
                    'state': {
                        'all':'Catalog tasks',
                        '-5': 'Pending catalog tasks',
                        '1': 'Open catalog tasks',
                        '2': 'Work in progress catalog tasks'
                    }
                },
                'task': {
                    'sys_class_name': {
                        'sc_task': 'Catalog tasks delegated to you',
                        'incident': 'Incidents delegated to you'
                    }
                }
            };

            if((table == 'incident' && groupByField == 'priority') || table == 'task')
                return labelMap[table][groupByField][selectedField];

            if (groupMode)
                return labelMap[table][groupByField][selectedField] + ' assigned to your team';
            return labelMap[table][groupByField][selectedField] + ' assigned to you';
        }

        static async getVisualisationCardConfig(id, helpers, mode, conditionalRecordCount) {
            var visualizationConfig = await this.getVisualizationConfig(helpers, mode, conditionalRecordCount);
            var currentVizSelection = {};
            for (var i = 0; i < visualizationConfig.length; i++) {
                if (visualizationConfig[i].id === id) {
                    currentVizSelection.table = visualizationConfig[i].tableName;
                    currentVizSelection.query = mode === "your_team_work" ? visualizationConfig[i].myTeamQuery + visualizationConfig[i]["updated_on"] : visualizationConfig[i].myWorkQuery + visualizationConfig[i]["updated_on"];
                    currentVizSelection.evamId = visualizationConfig[i].evamId;
                    currentVizSelection.elementId = visualizationConfig[i].id;
                    currentVizSelection.title = visualizationConfig[i].header;
                    currentVizSelection.groupByField = visualizationConfig[i].groupByField;
                    currentVizSelection.listView = visualizationConfig[i].listView;
                    break;
                }
            }
            return currentVizSelection;
        }

        static async getVisualisationCardQuery(vizSelection, nameValueMap, helpers, mode, conditionalRecordCount) {
            var field = this.getFieldMap()[vizSelection.groupBy] !== undefined ? this.getFieldMap()[vizSelection.groupBy] : vizSelection.groupByField;
            var value = nameValueMap[field];
            var query = '';
            var visualizationConfig = await this.getVisualizationConfig(helpers, mode, conditionalRecordCount);
            for (var i = 0; i < visualizationConfig.length; i++) {
                if (visualizationConfig[i].id === vizSelection.elementId) {
                    query = mode === "your_team_work" ? visualizationConfig[i].myTeamQuery : visualizationConfig[i].myWorkQuery;
                    break;
                }
            }
            if (Array.isArray(value)) {
                for (let index in value)
                    query += '^' + field + value[index];
            } else {
                if (value.includes('='))
                    query += '^' + field + value;
                else
                    query += '^' + field + '=' + value;
            }
            return query + visualizationConfig[0]["updated_on"];
        }
    }

    return ServiceDeskLandingPageUtilsSNC;
}

function include( {imports} ) {

    // Prepare navigation payload with route, required params (fields) and optional params (params)
    const generateNavigationPayload = (endpoint, currentRouteRequiredParams, currentRouteOptionalParams, canReloadPage = true) => {
        try {
            if (!endpoint)
                return null;

            let payload = {
                reload: canReloadPage
            };
            const [route, params] = endpoint.split('?');
            if (!route)
                return null;

            payload.route = route;
            if (params) {
                payload.fields = {};
                const allParams = params.split('&');
                allParams.forEach(param => {
                    if (param.match(/optional=(.+)/)) {
                        payload.params = {};
                        let optionalParams = param.match(/optional=(.+)/)[1].split('^');
                        optionalParams.forEach(optionalParam => {
                            const [optionalParamName, optionalParamValue] = optionalParam.split('=');
                            payload.params[optionalParamName] = getParamValue(optionalParamValue, currentRouteOptionalParams);
                        });
                    } else {
                        const [paramName, paramValue] = param.split('=');
                        payload.fields[paramName] = getParamValue(paramValue, currentRouteRequiredParams);
                    }
                });
            }
            return payload;
        } catch (error) {
            console.error('[ACE] ERROR generating navigation payload');

            return null;
        }
    }

    const areEqual = (o1, o2) => {
        let obj1 = o1 || {};
        let obj2 = o2 || {};

        const keys1 = Object.keys(obj1);
        const keys2 = Object.keys(obj2);
        if (keys1.length !== keys2.length)
            return false;

        for (let key1 of keys1) {
            if (obj1[key1] !== obj2[key1])
                return false;
        }
        return true;
    }

    const isEndpointUrlLoaded = (endpoint, {
        route,
        fields,
        params
    }) => {
        const payloadFromEndpoint = generateNavigationPayload(endpoint, fields, params);

        if (!payloadFromEndpoint || payloadFromEndpoint.route !== route || !areEqual(payloadFromEndpoint.fields, fields) || !areEqual(payloadFromEndpoint.params, params))
            return false;

        return true;
    }

    // Replace placeholder template text with current route parameters 
    const getParamValue = (template, currentRouteParams) => {
        const templateMatches = template.match(/{(.+)}/);
        if (!templateMatches)
            return template;

        const matchedName = templateMatches[1];
        return currentRouteParams[matchedName] || '';
    }

    return {
        generateNavigationPayload,
        isEndpointUrlLoaded
    };
}

/**
  * Copy of sn_sow_on_call.SOWOnCallRelativeDateTime
  */
function include({imports}) { 
	const {NowDateTime} = imports['sn_uxf.core_libraries']();

	const DEFAULT_DATE_TIME_FORMAT = 'YYYY-MM-DD HH:mm:ss';
	const DEFAULT_RELATIVE_DATE_FORMAT = 'DD-MMM-YYYY';
	const DEFAULT_RELATIVE_TIME_FORMAT = 'HH:mm';

	const getCurrentDateTime = function(tgtTZ) {
		let ndt = new NowDateTime();
		if (!tgtTZ) return ndt;
		return ndt.tz(tgtTZ);
	};

	const getTimeTillDateChangeInMillis = function(tgtTZ) {
		if (!tgtTZ) return;		
		let now = getCurrentDateTime(tgtTZ);
		return now.startOf('day').add(1, 'days').diff(now);
	};

	const getFormattedDateText = function(date, timeZone, format) {
		if(!date) return;
		if(!format) format = DEFAULT_DATE_TIME_FORMAT;
		if(!timeZone) return date.format(format);
		return date.tz(timeZone).format(format);
	};


	const getRelativeDay = function(date, tgtTZ) {
		if (!date) return;

		let now = getCurrentDateTime(tgtTZ);
		now = date.add(now.$d.valueOf() - date.valueOf(), 'millisecond');
		const dayDiff = Math.abs(date.startOf('day').diff(now.startOf('day'), 'days'));
		const isPast = date.isBefore(now);

		if (dayDiff === 0)
			return ('today');
		else if (dayDiff === 1)
			return isPast ?  'yesterday' :  'tomorrow';
		else return getFormattedDateText(date, null, DEFAULT_RELATIVE_DATE_FORMAT);
	};

	const getRelativeDayAndTime = function(date, timeFormat, tgtTZ) {
		if(!date) return;
		if (!timeFormat) timeFormat = DEFAULT_RELATIVE_TIME_FORMAT;

		let dayText = getRelativeDay(date, tgtTZ);
		let timeText = date.format(timeFormat);

		return [dayText , timeText];
	};

	const getRelativeDateTime = function(dateTimeStr, timeOnly, tgtTZ, time_fmt) {
		if (!dateTimeStr) return;

		var dateTime = new NowDateTime(dateTimeStr);

		if (timeOnly) 
			return getFormattedDateText(dateTime, null, time_fmt || DEFAULT_RELATIVE_TIME_FORMAT);

		return getRelativeDayAndTime(dateTime, time_fmt || DEFAULT_RELATIVE_TIME_FORMAT, tgtTZ);
	};

	return {
		getRelativeDateTime,
		getTimeTillDateChangeInMillis
	};
}

