function include({
    imports
}) {

    const {
        NowDateTime
    } = imports['sn_uxf.core_libraries']();

    const convertDateFormat = function(dateStr) {
        if (!dateStr || dateStr === '') return '';
        return NowDateTime(dateStr).format('YYYY-MM-DD') + '';
    };

    const getInfiniteDateAsString = function() {
        return '9999-12-31';
    };

    const doesOverlap = async function(newBucket, existingBuckets, helpers, api) {
        var message = '';
        for (let i = 0; i < existingBuckets.length; i++) {
            let existingStart = convertDateFormat(existingBuckets[i].start),
                existingEnd = convertDateFormat(existingBuckets[i].end);
            newBucket.start = convertDateFormat(newBucket.start), newBucket.end = convertDateFormat(newBucket.end);
            // If existing range has no end date, consider it as infinity
            if (!existingEnd) {
                existingEnd = getInfiniteDateAsString();
            }
            // If new range has no end date, consider it as infinity
            if (!newBucket.end) {
                newBucket.end = getInfiniteDateAsString();
            }
            // Otherwise, compare both start and end dates
            if (existingStart >= newBucket.start && existingEnd <= newBucket.end) {
                message = await helpers.translate("There are overlapping dates between this rotation and existing rotations. If you continue, any existing rotations with matching dates will be replaced.");
                break;
            }
            if (newBucket.start <= existingEnd && newBucket.end >= existingStart) {
                message = await helpers.translate("Selected dates overlap with existing rotations. Continuing will replace agents assigned to the overlapping rotations with agents assigned to this rotation on overlapping dates.");
                break;
            }
        }
        if (message) {
            //Just for loading effect
            if (api.state.bucketOverlappingMessage) {
                api.setState('bucketOverlappingMessage', '')
                helpers.timing.setTimeout(() => {
                    api.setState('bucketOverlappingMessage', message);
                }, 1000);
            } else {
                api.setState('bucketOverlappingMessage', message);
            }
        } else {
            api.setState('bucketOverlappingMessage', '');
        }
    };

    const compareObjects = function(obj1, obj2) {


        /* Checking if the types and values of the two arguments are the same. */
        if (obj1 === obj2) return true;


        /* Checking if any arguments are null */
        if (obj1 === null || obj2 === null) return false;

        /* Checking if any argument is none object */
        if (typeof obj1 !== 'object' || typeof obj2 !== 'object') return false;



        /* Using Object.getOwnPropertyNames() method to return the list of the objects properties */
        let obj1_keys = Object.getOwnPropertyNames(obj1);

        let obj2_keys = Object.getOwnPropertyNames(obj2);



        /* Checking if the objects' length are same*/
        if (obj1_keys.length !== obj2_keys.length) return false;




        /* Iterating through all the properties of the obj1 object with the for of method*/
        for (let key of obj1_keys) {


            /* Making sure that every property in the obj1 object also exists in obj2 object. */
            if (!Object.hasOwn(obj2, key)) return false;


            /* Using the compareObjects function recursively (calling itself) and passing the values of each property into it to check if they are equal. */
            if (compareObjects(obj1[key], obj2[key]) === false) return false;

        }



        /* if no case matches, returning true */
        return true;


    };

    const shiftDetailChanges = function(state) {
        var shift_details_state_var = ['all_day_shift', 'shift_starts_iso', 'shift_ends_iso', 'repeat_type', 'repeat_number', 'day_selected', 'selected_timezone', 'repeats_until_iso', 'selected_holiday_schedule', 'start_date_iso'];
        var initState = state.init_state || {};
        var finalShiftState = {};
        shift_details_state_var.forEach(detail => {
            var initValue = initState[detail];
            var currentValue = state[detail];
            //avoid inital load
            if ((typeof initValue !== 'undefined') && (typeof currentValue !== 'undefined') && (!compareObjects(initValue, currentValue))) {
                if (detail == "repeats_until_iso") {
                    finalShiftState[detail] = state["repeats_until"];
                } else {
                    finalShiftState[detail] = state[detail];
                }
            } else {
                if (finalShiftState.hasOwnProperty(detail))
                    delete finalShiftState[detail];
            }
        });
        return finalShiftState;
    };

    const fieldsComparator = function(obj, newObj, compare) {
        var fieldsToCompare = ['sys_id', 'dow_for_rotate', 'reminder_lead_time', 'order', 'rotation_start_time', 'rotation_interval_type', 'rotation_interval_count'];

        for (let fdx = 0; fdx < fieldsToCompare.length; fdx++) {
            let field = fieldsToCompare[fdx];
            if (!(field in obj)) return false;
            if (!compare) newObj[field] = obj[field].value;
            else {
                if (!(field in newObj) || obj[field] != newObj[field].value) return false;
            }
        }

        return true;
    };

    const compareShifts = function(a, b) {
        if(compareObjects(a, b)) return true;

        if (!a.hasOwnProperty('shifts') || !b.hasOwnProperty('shifts') ||
            !a.shifts.hasOwnProperty('rosters') || !b.shifts.hasOwnProperty('rosters')) {
            return false;
        }

        const rostersA = a.shifts.rosters;
        const rostersB = b.shifts.rosters;

        if (rostersA.length !== rostersB.length) return false;

        if ((!rostersA.length || !rostersB.length) && (rostersA != rostersB)) return false;

        const createMap = arr => arr.reduce((map, roster) => (map[roster.sys_id.value] = {}, map), {});

        const jsonMap1 = createMap(rostersA);

        if (!rostersA.every((roster, idx) => fieldsComparator(roster, jsonMap1[roster.sys_id.value], false))) return false;

        return rostersB.every(roster => {
            const rosterId = roster.sys_id.value;
            return rosterId in jsonMap1 && fieldsComparator(jsonMap1[rosterId], roster, true);
        });
    };

    return {
        shiftDetailChanges: shiftDetailChanges,
        compareShifts: compareShifts,
        doesOverlap: doesOverlap,
        compareObjects: compareObjects
    };
}

function include({ imports }) {
    const buildDefaultRoutePayload = (route, defaultTargetRoute, event, domainId = undefined) => {
        const { table, sysId, params, multiInstField, title, passiveNavigation, redirect, targetRoute, external } = _getPayloadParams(event, domainId);

        return {
            "route": route,
            "targetRoute": (targetRoute || targetRoute === '') ? targetRoute : defaultTargetRoute, 
            "fields": { "table": table, "sysId": sysId },
            "params": params,
            "redirect": redirect,
            "passiveNavigation": passiveNavigation,
            "title": title,
            "multiInstField": multiInstField,
            "external": external
        };
    };

    const routeMapping = (routeMap, defaultRoutePayload, event, domainId = undefined) => {
        const { elementId, sourceActionId, table, sysId, params, multiInstField, title } = _getPayloadParams(event, domainId);
        const { payload } = event;
        var tableRoute;
        if (routeMap)
            tableRoute = routeMap[table];

        var param_payload = defaultRoutePayload;
        if (tableRoute && elementId) {
            Object.keys(tableRoute).map((key) => {
                if (elementId.includes(key) || (sourceActionId && sourceActionId.includes(key))) {
                    param_payload = tableRoute[key].payload;
                    var pattern = /\{\{([^}]+)\}\}/ig;
                    var matches = param_payload.match(pattern);
                    matches.forEach(
                        function(token) {
                            var paramKey = token.substr(2, token.length - 4);
                            var newToken = "";
                            if (payload.hasOwnProperty(paramKey)) { //Handle case: e.x. payload.sysId
                                newToken = payload[paramKey];
                            } else if (payload.fields && payload.fields.hasOwnProperty(paramKey)) { //Handle case: e.x. payload.fields.sysId
                                newToken = payload.fields[paramKey];
                            } else if (payload.params && payload.params.hasOwnProperty(paramKey)) { //Handle case: e.x. payload.params.sysId
                                newToken = payload.params[paramKey];
                            } else if (paramKey == 'sysId') { //Handle case when param_payload come with "sysId" while event payload come with "sys_id"
                                newToken = payload.hasOwnProperty('sys_id') ? payload['sys_id'] :
                                        payload.fields && payload.fields.hasOwnProperty('sys_id') ? payload.fields['sys_id'] : 
                                        (payload.params && payload.params.hasOwnProperty('sys_id') ? payload.params['sys_id'] : '');
                            } else if (paramKey == 'sys_id') { //Handle case when param_payload come with "sys_id" while event payload come with "sysId"
                                newToken = payload.hasOwnProperty('sysId') ? payload['sysId'] :
                                        payload.fields && payload.fields.hasOwnProperty('sysId') ? payload.fields['sysId'] : 
                                        (payload.params && payload.params.hasOwnProperty('sysId') ? payload.params['sysId'] : '');
                            }
                            param_payload = param_payload.replace(token, newToken);
                        }
                    );
                    param_payload = JSON.parse(param_payload);
                    if (param_payload.fields) 
                        param_payload.fields.sysId = sysId;
                    param_payload.params = params;
                    param_payload.multiInstField = multiInstField;
                    param_payload.title = title;
                    return param_payload;
                }
            });
        }
        return param_payload;
    };

    const _getPayloadParams = (event, domainId) => {
        const { elementId, payload } = event;
        const { fields, params, query, row, nativeEvent, redirect = false, targetRoute, external } = payload;
        const sourceAction = payload.__sourceAction || {};
        var table = payload.table || fields.table;
        var sysId = payload.sysId || payload.sys_id || fields.sysId || fields.sys_id;
        var title = payload.title;

        const metaKeyPressed = nativeEvent && (nativeEvent.metaKey || nativeEvent.ctrlKey);

        if (row) {
            sysId = sysId || row.sys_id.value;
            title = row.displayValue.value;
        }

        var multiInstField = sysId === '-1' ? "sysId" : "";

        var outputPayloadParams = {};
        if (params) {
            outputPayloadParams = JSON.parse(JSON.stringify(params));
            outputPayloadParams.extraParams = Object.keys(params).map((x) => { return `${x}/${params[x]}` }).join("/");
        }

        if (query) {
            outputPayloadParams.query = query;
        }

        // create or update `query` with domainId when state has domainId 
        if (domainId) {
            if (!outputPayloadParams.query)
                outputPayloadParams.query = '';

            outputPayloadParams.query += (outputPayloadParams.query ? '^' : '') + 'sys_domain=' + domainId;            
        }

        var res = {};
        res.elementId = elementId || payload.elementId;
        res.sourceActionId = sourceAction.id;
        res.table = table;
        res.sysId = sysId;
        res.params = outputPayloadParams;
        res.title = title;
        res.multiInstField = multiInstField;
        res.external = external || "";

        if (targetRoute || targetRoute === '') {
            res.targetRoute = targetRoute;   
        }

        res.passiveNavigation = metaKeyPressed;
        res.redirect = redirect;

        return res;
    };

    return ({ routeMapping, buildDefaultRoutePayload });
}

